css:
@-moz-document domain("thecodinglove.com") {
html {
	color: #FFF;
	background: #222222 none;
}
body {
    background: #222222 none;
}
#main {
    background: #000000 none;
	border-bottom-left-radius: 5px;
	border-bottom-right-radius: 5px;
}
h1 a:link, h1 a:visited, h3 a:link, h3 a:visited ,
a.titleClass:visited ,
a.titleClass:link {
	color : rgb(255,255,255);
}
h1 {
	text-shadow : 1px 1px 1px #000;
}
.randomgif span {
	background-color : transparent !important;
}
.submitgif img ,
.randomgif img {
  	filter : grayscale(1);
	-webkit-filter: grayscale(1);
}
.submitgif:hover img ,
.randomgif:hover img {
  	filter : grayscale(0);
	-webkit-filter: grayscale(0);
}
a.randomgif:visited ,
a.randomgif:link ,
a.previouslink:visited ,
a.previouslink:link {
	color : #7886B0;
}
a.randomgif:hover ,
a.previouslink:hover {
	color       : #B7C3CF;
	text-shadow : 1px 1px 1px #000000;
}
a.submitgif:link ,
a.submitgif:visited ,
a.nextlink:link ,
a.nextlink:visited {
	color : #9C4F4F;
}
a.submitgif:hover ,
a.nextlink:hover {
	color       : #D0ADAD;
	text-shadow : 1px 1px 1px #000000;
}

font[color="#00b800"] {
	color : #81C081;
}
a:hover font[color="#00b800"] {
	color : #AECAAE;
}
}
