css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("osp-ua.info") {
  body,
  p,
  div p {
    color: #93A1A1;
  }
  
  body, 
  #main {
    background-color: #002B36;
  }
  
  #main > div:nth-child(1) > div:nth-child(1) > div {
    background: none !important;
  }
  
  img[src="/banner-osp/gif/cityukraine2.gif"] {
    display: none;
  }
  
  .menu a span,
  .lenta a,
  .blockcontent a,
  td.blocktitle div.tdrt a {
    color: #B58900 !important;
  }

  a:visited,
  .menu a:visited span,
  .lenta a:visited,
  .blockcontent a:visited,
  td.blocktitle div.tdrt a:visited {
    color: #6C71C4 !important;
  }
  
  td.blocktitle,
  td.blocktitle div.tdlt,
  td.blocktitle div.tdrt {
    background-image: none !important;
  }
  
  td.blocktitle {
    border: 1px solid white;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
  }
  
  
  .blockcontent td {
    color: #93A1A1 !important;
  }
  
  .rticle {
    color: #93A1A1 !important;
  }
  
  h2,
  #content strong {
    color: #B7C8C8 !important;
  }
  
  #content > table > tbody > tr > td:nth-child(1),
  #content > table > tbody > tr > td:nth-child(3) {
    display: none;
  }
}
