}
.leftblok_contener {
visibility: hidden;
}
.horizontal, .footer {
display: none !important;
}
.horizontal2 {
display: none !important;
}
.menu {
display: none !important;
}
div.main_menu a img {display: none !important;}
.header44 .social {
display: none !important;
}
.header44 .main_menu {
    padding-top: 0px !important;
    float: left !important;
    background: url('http://storage7.static.itmages.ru/i/15/1016/h_1445036521_7496468_e43b927d99.png') no-repeat !important;
    background-size: 200px 50px !important;
    width: 180px !important;
    height: 50px !important;
}
.header44 {
position: absolute !important;
top: -91px !important;
margin-top: 0px !important;
width: 980px !important;
}
.contener {
margin-bottom: 32px;
opacity: 1;
}
iframe {
display: none !important;
width: 0px !important;
height: 0px !important;
}
}

