css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coursera.org") {
  body,
  #origami,
  #course-page-content,
  .c-phoenix-template-page,
  .coursera-body,
  .coursera-full-canvas,
  .c-coursePage-body-container,
  .c-courseList-body-container{
    background-color: #002B36;
    color: #93A1A1;
  }
  .coursera-full-canvas {
   box-shadow: 0px 1px 2px 1px #195E8C;
  }
  
  .rc-NewPageHeader {
    margin-bottom: 0;
    border-bottom: 1px solid #195E8C;
  }
  
  a {
    color: #268BD2;
  }
  
  a:hover {
    color: #2AA198;
  }
  
  .btn, .bt3-btn {
    background-image: none;
    background-color: #B58900;
  }
  .btn:hover, .bt3-btn:hover {
    background-color: #C89600;
  }
  
  textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], 
  input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], 
  input[type="color"], .uneditable-input,
  select {
    background-color: #002B36;
    border-color: #195E8C;
    color: #93A1A1;
  }
    
  .course-topbanner {
    background-color: #657B83;
  }
  
  .course-navbar-list .course-navbar-item a {
    border-radius: 5px;
    border-color: #268BD2;
    color: #268BD2;
  }
  
  .course-navbar-list .course-navbar-item.active a {
    background-color: #084250;
    border-right: 2px solid #2AA198;
  }

  pre {
    background-color: black;
    color: #B8C8C8;
  }
  
  #course-page-content th,
  #course-page-content td {
    padding: 5px;
    border-color: #586E75;
  }
  
  
  .rc-NewPageHeader,
  .c-item-page-header-container {
    background-color: #002B36;
    color: #93A1A1;
  }
  
  .bt3-dropdown-menu {
    background-color: #002B36;
  }
  
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-right-nav ul.c-navbar-list .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu > .c-nph-right-nav-button a {
    color: #268BD2;
  }
  
  .bt3-dropdown-menu > li > a:hover, .bt3-dropdown-menu > li > a:focus {
    background-color: #084250;
  }
  
  .c-membership-tabs li a {
    color: #268BD2;
  }
  .c-membership-tabs li.active a, 
  .c-membership-tabs li:hover a {
    color: #2AA198;
  }
  
  .c-dashboard-card, .c-dashboard-membership,
  .c-recently-viewed-container .c-recently-viewed .c-courses {
    background-color: #04232B;
    border-color: #195E8C;
  }
  .c-dashboard-card, .c-dashboard-promo-card, .c-dashboard-membership {
    border-color: #195E8C;
    box-shadow: 0px 1px 2px 0px #195E8C;
  }
  
  .rc-NewDashboard .c-nd-pagelist-container .c-nd-page-list .c-page-list-item {
    background-color: #002B36;
    border-color: #195E8C;
    color: #268BD2;
  }
  .rc-NewDashboard .c-nd-pagelist-container .c-nd-page-list .c-page-list-item:hover:not(.c-selected) {
    background-color: #073642;
  }
  .rc-NewDashboard .c-nd-pagelist-container .c-nd-page-list .c-page-list-item.c-selected {
    background-color: #0A4B5A;
    border-color: #268BD2;
    color: #B58900;
  }
  
  .c-accomplishments-table {
    background-color: #04232B;
  }
  .c-accomplishments-table,
  .c-accomplishments-header .c-accomplishments-header-left .c-accomplishments-header-text .c-accomplishments-header-label,
  .c-accomplishments-table .c-accomplishments-table-header, 
  .c-accomplishments-table .c-accomplishments-table-subheader,
  .c-accomplishments-list .c-accomplishments-list-other .c-accomplishments-other-courses-container > div {

    border-color: #195E8C;
  }
  .c-accomplishments-header .c-accomplishments-header-left .c-accomplishments-header-text .c-accomplishments-header-label,
  .c-accomplishments-table .c-accomplishments-table-header {
        color: #ADBEBE;
  }
  .c-accomplishments-list-other-record .c-accomplishments-list-other-record-course-link {
    color: #B58900;
  }
  
  .c-vc-courses-button {
    background-color: #B58900;
  }
  .c-accomplishments-list-past-courses-container > div {
    border-top: 1px solid #195E8C;
  }
  .c-accomplishments-list-past-courses-container > div:first-child {
    border-top: none;
  }
  
  .coursera-course-listing-box,
  .coursera-profile-courses-box .coursera-profile-course-listing-box {
    background-color: #002B36;
    box-shadow: 0px 1px 2px 1px #195E8C;
    border-color: #268BD2;
  }
  .coursera-course-listing-box:hover,
  .coursera-profile-courses-box .coursera-profile-course-listing-box:hover {
    box-shadow: 0px 1px 2px 1px #268BD2;
    border-color: #268BD2;
  }
  
  .rc-FeaturedContentList .c-fcl .c-fcl-list {
    background-color: #04232B;
  }
  .rc-FeaturedContentList .c-fcl .c-fcl-list .c-fci-container .c-fci-wrapper-link .c-fci:hover {
    background-color: #052F37;
  }
  .rc-FeaturedContentList .c-fcl .c-fcl-list .c-fci-container .c-fci-wrapper-link .c-fci {
    border-color: #268BD2;
  }
  
  /* Top search box */
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-search-bar .c-nph-search-wrapper {
    border-color: #268BD2;
  }
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-search-bar .c-nph-search-wrapper .c-catalog-button {
    background-color: #002B36;
    border-color: #268BD2;
    color: #B58900;
  }
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-search-bar .c-nph-search-wrapper .c-catalog-button:hover {
    background-color: #052F37;
    color: #B58900;
  }
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-search-bar .c-nph-search-wrapper .c-search-input-container .c-search-catalog-input {
    background-color: #04232B;
  }
  .rc-NewPageHeader .c-nph-nav .c-container .c-nph-search-bar .c-nph-search-wrapper .c-search-input-container .c-search-catalog-input:focus {
    background-color: #052F37;
  }
  
  .coursera-profile-section,
  .rc-PageFooter {
    border-color: #195E8C;
  }
  
  .pac-container {
    background-color: #002B36;
  }
  .pac-item {
    color: #93A1A1;
  }
  .pac-item:hover {
    background-color: #003A46;
  }
  .pac-item-query {
    color: white;
  }
  
  .input-append .add-on, .input-prepend .add-on {
    background-color: #002B36;
    border-color: #195E8C;
    text-shadow: none;
  }
  
  .course-item-list .course-item-list-header {
    background-color: #073642;
    color: white;
  }
  .course-item-list .course-item-list-header:hover {
    background-color: #073642;
  }
}
