css:
@-moz-document domain("volafile.org") {
html {
  --main-bg-color: #303030;
  --snd-bg-color: #101010;
  --input-color: #131313;
  --input-disabled-color: #8e8e8e;
  --snd-input-color: #333;
  --box-color: #282828;
  --slider-color: #707070;
  --hi-color: #282828;
  --hi-hover-color: #505050;
  --file-list-color: #242424;
  --file-color: #3a3a3a;
  --file-hover-color: #505050;
  --header-hi-color: #404040;
  --header-disabled-color: #e0e0e0;
  --tag-color: rgba(164, 159, 159, 0.3);
  --tag-color-nick: rgba(140, 170, 121, 0.5);
  --button-color: #dcdcdc;
  --button-hi-color: #ececec;
  --button-light-color: #666;
  --button-light-hi-color: #777;
  --channel-color: #bd7fd7;
  --context-bg-color: #444;
  --action-color: #424242;
  --file-select-color: #464646;
  --control-disabled-color: #aaa;
  --radio-current-color: #555;
  --radio-progress-color: #5e5e5e;
  --radio-bar-color: #535353;
  --radio-slider-color: #777;
  --icon-image-color: lightskyblue;
  --icon-film-color: yellowgreen;
  --icon-book-color: lightsalmon;
  --icon-archive-color: bisque;
  --icon-music-color: orchid;
  --uploading-bg: #696969;
  --uploading-bg2: #6d6d6d;
  --uploading-done-bg: #567556;
  --uploading-done-bg2: #598059;
  --username-color: rgb(170, 170, 180);
  --username-green-color: #23d16f;
  --username-donor-color: #42ff96;
  --username-staff-color: #fc807e;
  --username-admin-color: #d880fc;
  --username-sys-color: #ff6c00;

  background-color: var(--snd-bg-color);
}

*,
*::after {
  border-color: var(--main-bg-color) !important;
}

select {
  background-color: var(--action-color);
}

table th {
  background-color: var(--main-bg-color);
}

#chat_frame,
#files_frame {
  border-top-color: var(--header-hi-color) !important;
  top: 2em;
}

#admin_button,
#chat_header,
#files_header,
#name_container,
#room_search {
  height: 2em;
  line-height: 2em;
}

#admin_button {
  background-color: var(--main-bg-color);
  width: 2em;
}

#admin_button_icon {
  font-size: 1em;
  vertical-align: middle;
}

#name_container {
  padding-left: 0.5ex;
  background-color: var(--main-bg-color);
}

#clearsearch,
.header_row_element {
  line-height: 2em;
}

#home_button {
  height: 2em;
  width: 2em;
  padding: 0 0.8em;
  background-color: var(--main-bg-color);
}

.volafile_icon_svg.button .v_stroke {
  stroke-width: 80px;
}

#room_name {
  font-size: 1.3em;
}

#rename_container {
  line-height: 1.7em;
}

#rename_input {
  font-size: 1em;
  width: 20em;
}

.filelist_file {
  align-items: center;
  border-bottom: 1px solid var(--main-bg-color);
}

#file_list.even .filelist_file:nth-child(2n),
#file_list.uneven .filelist_file:nth-child(2n+1) {
  background-color: var(--file-list-color);
}

.file_selected {
  background-color: var(--file-select-color) !important;
}

.filelist_file:hover::before {
  border-color: var(--file-hover-color);
}

.file_status {
  font-size: small;
}

.file_uploading::before {
  background-color: var(--uploading-bg);
  background-image: repeating-linear-gradient(-45deg, var(--uploading-bg), var(--uploading-bg) 25%, var(--uploading-bg2) 25%, var(--uploading-bg2) 50%);
}

.file_uploading::after {
  background-color: var(--uploading-done-bg);
  background-image: repeating-linear-gradient(-45deg, var(--uploading-done-bg), var(--uploading-done-bg) 25%, var(--uploading-done-bg2) 25%, var(--uploading-done-bg2) 50%);
}

.file_queued {
  background-color: #696969 !important;
}

.scroller-slider,
.nano > .pane > .slider {
  background-color: var(--slider-color);
}

#chat_hbar_container {
  box-shadow: 0 -5px 5px 0 var(--snd-bg-color) !important;
}

#file_notifier,
#filter_reminder,
#chat_hbar {
  background-color: var(--main-bg-color);
}

#file_notifier:hover,
#filter_reminder:hover {
  background-color: var(--hi-hover-color);
}

#chat_hbar {
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  border: 0;
}

#chat_name {
  width: 16ex;
  margin: 0;
  height: auto;
  border: 0;
  margin-top: 1px;
  margin-bottom: 1px;
  padding-left: 1ex;
  padding-right: 1ex;
}

#user_count {
  margin-left: 1ex;
  margin-right: 1ex;
  min-width: 5ex;
}

#chat_input {
  padding: 0.5ex 1ex;
  font-size: inherit;
  border-bottom: 24px solid var(--input-color) !important;
  height: calc(5em + 24px) !important;
}

#toggles span.toggle_icon {
  color: var(--input-disabled-color) !important;
}

#toggles > .toggle.enabled span.toggle_icon {
  color: #f6f1f1 !important;
}

#search_input:hover,
#toggles > .toggle:hover {
  background-color: rgba(120, 120, 120, 0.1);
  border-radius: 3px;
}

body:not(.mobile_filelist) #toggles span.toggle_container {
  margin-right: 1ex;
}

#toggles > .toggle:active {
  background: transparent;
}

#room_search {
  background-color: var(--input-color);
}

body:not(.mobile_filelist) #room_search {
  margin-left: 1em;
}

#search_input:active,
#search_input:focus,
#search_input:valid {
  background-color: transparent;
  width: 25em;
}

.ui_frame_contextmenu {
  border-radius: 0;
  border: 0;
}

.ui_frame_contextmenu,
.ui_frame_contextmenu .button.light,
.ui_frame_contextmenu .button.light:active,
.ui_frame_contextmenu .button.light:visited {
  background-color: var(--context-bg-color);
}

input[type=text],
input[type=password],
textarea {
  background-color: var(--input-color);
}

input[type=text]::-webkit-input-placeholder,
input[type=password]::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
  color: var(--input-disabled-color);
  opacity: 1;
}

input[type=text]::placeholder,
input[type=password]::placeholder,
textarea::placeholder {
  color: var(--input-disabled-color);
  opacity: 1;
}

input[type=text][disabled],
input[type=password][disabled],
textarea[disabled] {
  color: var(--input-disabled-color);
}

input[type=radio]:disabled + span._icon::before,
input[type=checkbox]:disabled + span._icon::before,
.radio_toggle {
  color: var(--input-disabled-color);
}

.main > form input[type=text],
.main > form input[type=password] {
  background-color: var(--snd-input-color);
}

input.defaultValue[type="text"],
input[type="text"][disabled],
input.defaultValue[type="password"],
input[type="password"][disabled],
textarea.defaultValue,
textarea[disabled] {
  color: var(--control-disabled-color);
}

.chat_message > i {
  color: var(--channel-color);
}

a.chat_room {
  text-decoration: underline;
  display: inline-block;
}

a.chat_room::after {
  content: none;
  display: none;
}

a.chat_room::before {
  display: inline-block;
  content: '#';
  font-weight: bold;
  color: rgba(255, 255, 255, 0.4);
  padding-right: 0.3ex;
  text-decoration: none !important;
}

a.chat_room:hover::before,
a.chat_room:active::before {
  color: rgba(255, 255, 255, 0.8);
}

.chat_file {
  background-color: var(--file-color);
}

.chat_file:hover,
.chat_file:active {
  background-color: var(--file-hover-color);
}

#chat_messages_frame::before {
  border-left-color: var(--snd-bg-color);
}

.chat_message.highlight {
  border-left-color: var(--hi-color) !important;
  background-color: var(--hi-color);
}

.ui_frame,
#overlay {
  background-color: var(--box-color);
}

.dropdown:hover,
a.header_row_element:hover {
  background-color: var(--header-hi-color);
}

a.header_row_element:active {
  background-color: var(--header-hi-color);
}

.header_row_element.clickable:hover,
a.header_row_element_disabled,
a.header_row_element_disabled:active,
a.header_row_element_disabled:hover,
a.header_row_element_disabled:visited {
  background-color: var(--header-hi-color);
  color: var(--header-disabled-color);
}

.header_row_element_disabled.clickable,
.header_row_element_disabled.clickable:hover {
  color: var(--input-disabled-color);
}

.dropdown_item:hover {
  background-color: var(--hi-hover-color);
}

.dropdown_item:active {
  background-color: var(--header-hi-color);
}

.dropdown_list {
  background-color: var(--header-hi-color);
}

.dropdown::after {
  border-color: var(--header-hi-color);
}

.file_status::before,
hr {
  border: 1px solid var(--file-select-color);
}

.file_left_part {
  align-items: center;
}

.file_tags {
  flex-grow: 1;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: flex-end;
}

.file_queued .file_tags,
.file_uploading .file_tags {
  display: none;
}

.file_right_part {
  text-align: right;
  float: none;
}

.file_tag {
  padding-left: 0.5ex;
  padding-right: 0.5ex;
  background-color: var(--tag-color) !important;
}

.file_tag.tag_key_user,
.file_tag.tag_key_nick {
  order: 1000;
}

.file_tag.tag_key_user {
  background-color: var(--tag-color-nick) !important;
}

.file_tag.tag_key_hellban {
  text-decoration: line-through;
}

.file_control {
  padding-right: 0.2em;
}

.file_clock {
  font-size: 12px;
}

.button:not(.gallery_button),
input[type=submit] {
  background-color: var(--button-color);
}

.button:visited:not(.gallery_button),
input[type=submit]:visited {
  background-color: var(--button-color);
}

.button:hover:not(.gallery_button),
input[type=submit]:hover {
  background-color: var(--button-hi-color);
}

.button:active:not(.gallery_button),
input[type=submit]:active {
  background-color: var(--button-hi-color);
}

.gallery_button {
  font-size: x-small;
}

.gallery_area {
  border: 0;
}

.button.light,
.button.light:visited,
input[type=submit].light,
input[type=submit].light:visited {
  background-color: var(--button-light-color);
}

.button.light:hover,
input[type=submit].light:hover {
  background-color: var(--button-light-hi-color);
}

.ui_frame_content .button.light:hover {
  background-color: var(--button-light-hi-color);
}

#chat_header,
#files_header {
  background-color: var(--main-bg-color);
}

#chat_name_container {
  background-color: var(--main-bg-color);
  order: 1;
}

#loading_frame {
  background-color: var(--main-bg-color);
}

#loading_text {
  color: white;
}

#room_name_angle_down,
#chat_user_angle_up,
#chat_user_icon,
#clearsearch {
  opacity: 0.3;
}

#chat_user_angle_up {
  padding-left: 0.7ex;
  display: none;
}

#chat_user_icon {
  padding-right: 0.7ex;
}

#toggles span.toggle_text {
  display: none;
}

#chat_notifier {
  order: 2;
}

#chat_status {
  order: 3;
}

#gallery_title {
  font-size: small;
}

#main_small_links {
  background-color: #434343;
}

.top_nav_item,
.top_nav_item:hover,
.top_nav_item:visited,
body {
  color: rgb(231, 231, 231);
}

.top_nav_item:hover {
  background: var(--header-hi-color);
}

.chat_message.staff:not(.profile) {
  word-wrap: break-word;
  opacity: 0.7;
  font-size: small;
  max-height: 4.5em;
  overflow-y: hidden;
  text-overflow: ellipsis;
}

.chat_message.staff:not(.profile):hover {
  max-height: none;
}

.chat_message .username .clickable:not(:hover) {
  color: inherit;
}

.chat_message .username {
  color: var(--username-color);
  float: none;
}

.chat_message.user .username {
  color: var(--username-green-color);
}

.chat_message.donator .username {
  color: var(--username-donor-color);
}

.chat_message.staff .username {
  color: var(--username-staff-color);
}

.chat_message.admin .username {
  color: var(--username-admin-color);
}

.chat_message.staff:not(.user) .username {
  color: var(--username-sys-color) !important;
}

.icon-image {
  color: var(--icon-image-color);
}

.icon-film {
  color: var(--icon-film-color);
}

.icon-book {
  color: var(--icon-book-color);
}

.icon-archive {
  color: var(--icon-archive-color);
}

.icon-music {
  color: var(--icon-music-color);
}

.chat_room_url {
  background-color: var(--hi-hover-color);
}

#radio_container {
  background-color: var(--main-bg-color);
}

#radio_current {
  background-color: var(--radio-current-color);
}

#radio_progress {
  background-color: var(--radio-progress-color);
}

#radio_volume_bar,
#radio_load_progress {
  background-color: var(--radio-bar-color);
}

#radio_volume_slider,
#radio_play_progress {
  background-color: var(--radio-slider-color);
}

body:not(.mobile_filelist) .file_right_part {
  min-width: 164px;
}

body:not(.mobile_filelist) .file_status {
  min-width: 90px;
}

.file_buttons::before,
.file_status::before {
  border-color: var(--main-bg-color) !important;
}

#uploadButton {
  font-size: 18px;
  padding: 0 0.5em 1px 0.5em;
}

#clearsearch {
  padding-left: 0.6ex;
}

#call_to_action,
#call_to_action_container.active #call_to_action {
  color: var(--action-color);
}

.drag_here_svg .drag_here_rect,
#call_to_action_container.active .drag_here_rect {
  color: var(--action-color);
  stroke: var(--action-color);
}

#call_to_action_container::before {
  background-color: var(--snd-bg-color);
}

.page_admin_reports table,
.page_admin_modlog table {
  table-layout: fixed;
  width: 100%;
}

.page_admin_modlog table th:nth-child(1) {
  max-width: 15%;
}

.page_admin_modlog table th:nth-child(3) {
  width: 56%;
}

.page_admin_modlog table th:nth-child(4) {
  width: 7ex;
}

.page_admin_reports table th:nth-child(1) {
  max-width: 9%;
}

.page_admin_reports table th:nth-child(4) {
  width: 10%;
}

.page_admin_reports table th:nth-child(3) {
  width: 53%;
}

#bottom_navigation_content {
  background-color: var(--main-bg-color);
}

#admin_button:not(:hover) {
  color: var(--input-disabled-color) !important;
}

#upload_button .icon-upload-button {
  margin: 0;
}

#upload_button > .on_small_header {
  display: none;
}

textarea[name="motd"] {
  width: 24em;
  height: 8em;
}

textarea[name="files"] {
  width: 30em;
  height: 12em;
}

.page_admin_adiscover tr:nth-child(odd) {
  background-color: rgba(255,255,255,0.04);
}

.page_admin_adiscover tr:hover {
  background-color: rgba(255,255,255,0.1);
}
}
