css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("leekwars.com") {
#wrapper {max-width: 80% !important;}
#big-leeks {display: none !important;}
body{font-size: 13px !important;background: radial-gradient( hsl(200,40%,20%), hsl(200,30%,6%) ) !important;}
image{opacity: 0.2 !important;}
#leek-image {opacity: 0.2 !important;}
#wrapper {opacity: 0.8;}
#logo {display: none;}
#favicon{display: none !important;}
body.connected #center, body.connected #footer {margin-left: 0px;}
body.connected:not(.social-collapsed) #center, body.connected:not(.social-collapsed) #footer {margin-right: 200px;}
#forum_topic-page .profile {opacity: 0.2;}
#leek-chips {opacity: 0.2;}
#leek-page #stats .stat {opacity: 0.2;}
#loader {opacity: 0;}
#weapons {opacity: 0.2;}
#chips {opacity: 0.2;}
#potions {opacity: 0.2;}
#hats {opacity: 0.2;}
.image {opacity: 0.2;}
#game-canvas {opacity: 0.35 !important;}
#left-part {opacity: 0.4;}
#right-part {opacity: 0.4;}
#header {opacity: 0.4;}
.avatar{opacity: 0.2;}
#squares {width: 100%;height: 100%;position: fixed;top: 0px;left: -99.9%;background-color: rgba(255,255,255,1);z-index: 1;opacity: 1;background-image:url(http://img11.hostingpics.net/pics/114345wiki.png);background-position:50% 50%;background-repeat:no-repeat;}
#squares:hover{  left: 0%;}
}
