css:
@-moz-document domain("lobste.rs") {
html {
    background-color: #292929 !important;
}

body {
    color: #eee !important;
    font-weight: lighter !important;
    font-family: "Lucidia", "Helvetica" !important;
}

body a {
    color: #aaa !important;
}

#header a.cur_url {
    color: #ffb946 !important;
}
.headerlinks a {
    color: #eee !important;
    font-weight: lighter !important;
    font-family: "Lucidia", "Helvetica" !important;
}

.select2-drop {
    color: #000 !important;
}

#headertitle a {
    color: #aaa !important;
}

#leader {
    color: #eee;
}
#leader a {
    color: #ccc;
}

.legend {
	background: transparent !important;
	color: #ccc !important;
}


.story, .story_liner {
    margin-bottom: 12px !important;
}
.story .link a, .story_liner .link a {
    color: #eee !important;
    font-weight: lighter !important;
}
.story .link a:visited, .story_liner .link a:visited {
    color: #aaa !important;
}

.comments .comment_text {
    color: #ddd !important;
    font-weight: lighter !important;
    font-family: "Lucidia", "Helvetica" !important;
    font-size: 15px;
    line-height: 20px;
}
.comments .comment_text a {
    color: #aaa;
}
.comments blockquote {
    color: #aaa !important;
}
.comments .comment {
    border-left-style: solid !important;
    border-left-color: #666;
}
.comments .comment_form_container {
    border-left-style: none !important;
}
.comment.highlighted {
    background-color: #333 !important;
}

.morelink a {
    color: #aaa !important;
}

#story_guidelines {
	color: #eee;
}
#story_guidelines > div > a {
	background: transparent !important;
	color: #ccc !important;
	border: none !important;
}

table.data, table.data * {
    background-color: #333 !important;
    border-color: #000 !important;
}
table.data {
    border: 2px solid #000;
}

.tag {
    color: #000 !important;
    font-size: 0.8em !important;
}
}
