css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhihu.com") {

.zu-main-sidebar{
	position: fixed;
	left: 0px;
	top:50px;
	margin:0 0 0 0px;
	width:80px;
	height: 90%;
	transition: width 1s,height 1s, opacity 1s;
	overflow: hidden;
	opacity:0;
	padding: 5px 5px 5px 5px;
	border-color: black;
	border-style: solid;
	border-width: 0;
	
	
}
	.zu-main{
  padding-left: 0px;
		padding-right: 0px;
		padding-bottom: 0px;
		padding-top: 0px;
  }
.zu-main-sidebar:hover{
	background-color: white;
	width: 270px;
	opacity: 1;
}
.zu-main-content{
	width:130%
}
html{
	overflow-x: hidden;
}
.arrow{
	position: fixed;
	
}
.zh-backtotop{
	left: 80%;
	bottom: 5px;
	
}
#zh-topstory2-newblock{
	left: 60%;
	color: black;
	background-color: white;
	border-style: solid;
	border-color: black;
}
	#zh-topstory2-newblock.hover{
		background-color: white;
	}
	.zu-top{
		background: rgba(128,128,128,0);
		box-shadow:unset;
		height: 50px;
		opacity: 1;
		border-bottom: 0px solid black;
		padding-left: 5px;
	}
	.zu-top:hover #zh-top-inner{
		opacity: 1;
	}
	.zu-top:hover{
		background: rgba(256,256,256,128);
	}
	#zh-top-inner{
		opacity: 0;
		transition: opacity .3s;
		width: 100%;
	}
	.name{
		color: black;
	}
		.zu-top-nav-li.current{
		background-color: white;
			border-style: solid;
			border-bottom-width: 5px;
			border-top-width: 0px;
		background-image:none;
	}
	.zu-top-nav-link{
		color:black;
		line-height: 50px;
	}
	.zu-top-nav-link:visited{
		color: black;
		line-height: 50px;
	}
	.zu-top-nav-link:active{
		color: black;
		line-height: 50px;
	}
	.top-nav-profile .zu-top-nav-userinfo{
		line-height: 50px;
	}
	.zu-top-nav-userinfo .avatar{
		top:12px;
	}
	.top-nav-profile{
		background-color: white;
	}
	.top-nav-profile a{
		background-color: white;
		color: black !important;
	}
	.zu-top-link-logo{
		width: 20%;
		filter: brightness(50%);
		background-position: 0px 10px;
	}
	.zu-top-search-input{
		background: none;
		border:1px solid gray;
		border-radius: 0px;
	}
	.zu-top-search-input:focus{
		border:1px solid black;
	}
	.zu-top-add-question{
		background: white;
		color: black;
		box-shadow: none;
		border: 1px solid black;
		height: 38px;
	}
	.zu-top-search-form{
		top: 3px;
	}
	a{
		color: gray;
		font-size: 15px;
	}
	a:visited {
    color:black;
}
	.zm-comment-box{
		max-width: 100%;
	}
}
