@-moz-document domain("eldarya.fr") {

    
  /*Gazar en familier*/  
 
  /*Images - pictures*/
 #pet-headerPortrait-base {
   background: url("http://i.imgur.com/bk5V8aF.png") -15px top no-repeat !important;
  background-size : auto 125px !important ;
  }

  #pet-portrait {
    background : url("http://i.imgur.com/gypCK6L.png?1") center no-repeat !important ;
    background-size : auto 450px !important ;
    height : 450px !important ;
  }

  #pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}

 /*icon*/

#pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}
#player-display-pet {
    background : url("http://i.imgur.com/h4uHcYM.png") center no-repeat !important ;
    background-size: auto 100px !important ;
    height: 189px !important ;
    width: 250px !important ;
  }
 #player-display-pet > img[src*="/static/img/pet/mood/profile/"] {opacity : 0 !important ;
  }

	#player-display-pet {
    background : url("http://i.imgur.com/h4uHcYM.png") center no-repeat !important ;
}


    /*Infos*/

  #pet-info-name:before {
    content : "Gazar" !important ;
    font-size : 20px !important ;
  }
  
  #pet-info-name {
    font-size : 0 !important ;
  }  
  
  #pet-info-evolve > .on {font-size : 0 !important ;}
  
  .pet-info-evolve-icon {font-size : 13px !important ;}
  
  .pet-info-evolve-icon:after {
    content : " Gazar croissante ." ;
  }
  
  
  #pet-info-racegender:before {
    content : "Gazar" !important ;
    font-size : 16px !important ;
    line-height : 20px !important ;
  } 
  
  #pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
  }  

 /*Nourriture - food*/
  
 #pet-info-food > img {
  background : url("http://i.imgur.com/DqOutQA.png") !important ;
  background-size : 60px 60px !important ;
  margin-top : -5px !important ;
  padding-left : 60px !important ;
  padding-top : 60px !important ;
  width : 0 !important ;
  height : 0 !important ;
} 
  
  #pet-info-food {font-size : 0 !important ;}
  }
