@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gamepod.hu") {

/*
for PH!mod: "red alert" ver. 0.8.5 by sz.balazs.95
created: 2016.02.26
published: 2016.03.01

red: #a61f22;
light gray: #f3f3f3;
gray: #939598;
dark gray: #58595b;
*/

#head .logo {width: 260px; left: 47px;}
#head .logo a {outline: 0px none; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/bannera-gamepod-1.1.png) no-repeat scroll 50% 50%;}
#head .logo a span {color: transparent;}
body.background_banner #network div.bg {left: 9px;}
#network li.gp {background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/redcube-2.0.png) 0 0 no-repeat;}
#network li.ph, #network li.ma, #network li.it, #network li.lo, #network li.sg {background: url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/rcm-1.0.png) 4px 8px no-repeat;}
img[src="/design/headicon-rss_gp.gif"] {width: 0px !important; height: 0px !important; background: #a61f22 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/rss-1.2.png) no-repeat scroll 0% 0%; padding: 0px 0px 19px 19px;}
.forumok .for_15 .title a, .forumok .for_33 .title a, .forumok .for_34 .title a, .forumok .for_47 .title a {color: #a61f22;}
#main .boxl_coloured h4 {background: #a61f22 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/menu-m-1.0.png) left center no-repeat; color:#fff; text-align: center; font-size: 13px; font-weight: bold;}
#center .finfo a {color: #000;}
#center .sechead .ddm {background: #a61f22 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/opt-119p-1.2.png) no-repeat scroll 50% 50%;}
#center .sechead .ddm ul {border-top: 3px solid #a61f22;}
#center .sechead .ddm a {color: #000;}
.relcnt h4 {font-size: 18px; color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/blackline-1.1.png) repeat-x scroll 0px 16px;}
.relcmp h4 {font-size: 18px; color: #000; background: transparent url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/blackline-1.1.png) repeat-x scroll 0px 15px;}
}

@-moz-document domain("m.gamepod.hu") {

#head {border-bottom: 0.4em solid #a61f22; background: #000 url(http://users.atw.hu/b95/css/PH!mod/redalert/0.8.5/background-gp-1.2.png) no-repeat scroll 0px 0px; height: 3.9em;}
#head div.logo a img {display: none;}
.xabretext a {color: #a61f22;}
}
