css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opennet.ru") {
  body,
  #r_memo,
  tr, td {
    background-image: none !important;
    background-color: #002B36 !important;
    color: #93A1A1;
  }
  
  a,
  a:link{
    color: #268BD2;
  }
  
  a:hover {
    color: #2AA198;
  }
  a:visited {
    color: #6C71C4;
  }
  
  h2 {
    color: #EEE8D5;
  }
  
  font {
    color: inherit !important;
  }
  
  table.thdr,
  table.ttxt,
  table.tlist {
    border-color: #155378;
    background-color: #155378;
  }
  
  select {
    background-color: #002B36 !important;
    color: #93A1A1 !important;
  }
  
  div.tdelim {
    display: none;
  }
  
  td.ctxt,
  td.ctxt blockquote, td.ctxt ul {
    color: #93A1A1;
  }
  td.ctxt,
  td.chdr,
  td.chdr2,
  td.line,
  td.i,
  .vt_d,
  .vt_d2 {
    border-color: #155378;
  }
  textarea, input[type=text], input[type=submit], select {
    border: 1px solid #155378 !important;
    color: #93A1A1 !important;
     background-color: #002B36 !important;
  }
  
  #tabs_top {
    border-color: #155378;
    background-color: #002B36 !important;
  }
  div.tab, div.tab-selected {
    background-color: inherit;
    border-color: #155378;
  }
  div.tab {
    color: #268BD2;
  }
  div.tab:hover,
  div.tab-selected {
    background-color: #073642;
    color: #2AA198;
  }
  div.tab-null {
    border-color: #155378;
    background-color: inherit;
  }
  
  table.fadv {
    border-color: #155378 !important;
    background-color: #155378 !important;
  }
}
