 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lighthouseapp.com") {
/* Issue list */
table.data th,
table.data td {
  padding: 2px 5px !important;
  font-size: 10px;
  line-height: 1.2em;
}
table.data th {
  font-size: 11px;
  white-space: nowrap;
}
table.data td.issue {
  font-size: 11px;
  width: 50% !important;
}
.data-list a {
  color: #999 !important;
}
.data-list .issue a {
  color: #3388BB !important;
}
.data-list .issue a:hover {
  text-decoration: underline !important;
}
.data-list tbody tr:hover {
  background-color: #ffc;
}

/* Ticket page */
ul.ticket-changes {
  background-color: #eee;
  font-size: 11px;
  line-height: 1;
  border-radius: 3px;
}
ul.ticket-changes li {
  padding: 4px !important;
  margin: 0 !important;
  font-weight: normal !important;
}
ul.ticket-changes li em.change {
  font-weight: bold !important;
}
    
.tickets-show #main .desc p,
.tickets-show #main .greet-cnt p {
  padding: 0 5px 1.2em;
  margin: 0;
}
div.desc ul, .greet-cnt ul {
  margin: 0 0 1.2em 2em;
}

.tickets-show .gbar {
  float: right;
}

textarea#ticket-body {
  padding: .5em;
  font-size: 12px;
  line-height: 1.3em;
  height: 100px;
}

/* move ticket num to left side */
.ticket-meta {
  background: #ddd;
  left: 0;
  margin: 0;
  position: fixed;
  text-align: center;
  top: 0px;
  width: 100%;
  padding: 2px 10px;
  border-radius: 0 0 5px 0;
  box-shadow: 1px 1px 2px #555;
  z-index: 100;
}
.ticketnum, .tickets-show .tstate {
  padding: 0 !important;
  line-height: 1;
  float: left;
  margin: 0 !important;;
  font-size: 14px;
}
.ticketnum {
  
}
.tickets-show .tstate {
  margin-left: 10px !important;
  font-size: 12px !important;
  margin-top: 1px !important;
}
.greet .date {
  position: fixed;
  top: 0;
  left: 125px;
  z-index: 101;
  /*font-weight: bold;*/
  color: #666;
  text-transform: uppercase;
  font-size: 10px;
}
.greet .date a {
  text-decoration: none;
}

}/* end -moz-document */
