css:
@-moz-document domain("pikabu.ru") {
body, div[id^='wrap'] {
  font-family: "PT Sans", Verdana, Arial, Helvetica, sans-serif!important; 
  background-color: #222!important; 
  color: #bbb!important;
}

div[id^='facebook_page_block_lenta'], div[id^='facebook_page_block'], table[style$="5px;text-align: center"] {display:none!important;}

.b-story__header-info, .story_head, .b-story__header, .slide_up_container, .footer_links, .footer_links2, .footer {
  font-family: "PT Sans", Verdana, Arial, Helvetica, sans-serif!important; 
  background-color: #222!important; 
  color: #bbb!important;
}

.count{
  background-color:#171616 !important;
  border-color: gray !important;
  border-radius: 3px!important;
  border-style: solid!important;
  border-width: 1px!important;
} 

.personals {
  background-color:#222!important;
  border: 1px solid #171616!important;
  border-radius: 3px!important;
}  

.text_story_div {
  background: #222!important;
  opacity: .5!important;
  border: 0px!important;
  color: gray!important;
}
    
.text_story_div:hover {
  background: #222!important;
  opacity: 1!important;
  border: 0px!important;
  color: gray!important;
  transition: all 300ms !important;
}

  
.add_story_cn_textarea, .add_story_cn_text{background: #222 !important; color: #989898 !important; border-color: #565656 !important;}
.add_story_cn_textarea:focus, .add_story_cn_text:focus{border-color: #6F6F6F !important; box-shadow: 0px 1px 6px #4B4B4B !important;}
.submit_button, .submit_button_freshitems, .comment_desc_edit_submit_button, .moderator_add_tag_init, .b-button_type_default {color: #989898 !important; text-shadow: none !important; background-image: -moz-linear-gradient(center top, #181818, #181818) !important; border-color: #4D4D4D !important;}
  .b-tabs_type_sign .b-tabs__list{background: transparent -moz-linear-gradient(center top , #3E3E3E 0px, #141414 100%) repeat scroll 0px 0px !important; border-left: 1px solid #333 !important; border-top: 1px solid #333 !important; border-right: 1px solid #333 !important;}
  .b-tabs_type_sign .b-tabs__container, .b-tabs_type_sign .b-tabs_tab{background: transparent -moz-linear-gradient(center top , #3E3E3E 0px, #141414 100%) repeat scroll 0px 0px !important;}
  .b-tabs_type_sign .b-tabs__container{border-left: 1px solid #333 !important; border-top: 1px solid #333 !important; border-right: 1px solid #333 !important;}
  .b-tabs_type_sign .b-tabs__list li.b-tabs_li_active_yes{background: rgba(255, 255, 255, 0) none repeat scroll 0% 0% !important;}
.b-user-menu-list__count{background-color: rgb(21, 21, 21) !important;}
.b-comments{background: #222 !important; border-left-color: #474747 !important;}
.b-comment{color: #989898 !important; background: #222 !important;}
.b-comment-toggle__icon{color: #6E6E6E !important; background: #222 !important;}
.b-comment blockquote{color: #808080 !important; background: #333 !important;}
.b-comment__body_highlight_newest::before{background-color: #1D1D1D !important;}
.b-comments__next {background-color: #181818 !important;}
.b-comments-reply__editable {background-color: #222 !important; color: #989898 !important; border-color: #565656 !important;}
.b-comments-reply__editable:focus {border-color: #6F6F6F !important; box-shadow: 0px 1px 6px #4B4B4B !important;}
.b-comment-toggle_type_expand {background-color: #181818 !important;}
.b-social{background: #222 !important;}
.b-branding__tnt-play {background: repeat scroll 0% 0% #262626;}
.b-branding__tnt {background: repeat scroll 0% 0% #262626;}
.b-branding__wrapper {background: none repeat scroll 0% 0% rgb(38, 38, 38);}
.comment_msg {color:gray!important;}
.story-search div.story-adv-search > a {color: #5C5C5C!important;}
.story-search div.story-adv-search {color: #808080!important;}
.story-search {background-color: #292929!important;}
.b-navigator{background: #222 !important; box-shadow: 0px 0px 2px #8C8C8C !important;}
.b-comments-navigation__button{background-color: #2C2C2C !important;}
.b-comments-navigation__button-refresh {background-color: #2C2C2C !important; color: #787878 !important;}
.b-comments-navigation__button-count {background-color: #329CE1 !important;}
.b-feed-panel{background-color: #0F0F0F !important;}
  .b-story__content_type_text {background-color: #171616 !important; border: 1px gray !important; color: #909090 !important;}
  .b-story-blocks {color: #909090 !important;}
  .b-story-blocks_with-border {background-color: #171616 !important; border-color: #171616 !important;}
  .b-story__show-all {background-color: #0B0B0B !important; color: #909090 !important;}
  .b-story__show-all span {color: #909090 !important; border-bottom-color: #909090 !important;}
.b-textarea {background-color:#171616!important;}
.header_links a {color: gray!important;}
.noactive a {color:#D7D8D8!important;background-color:#555!important;}
.active2 a, .active a {color:#D7D8D8!important;background-color:#171616!important;border:none!important;background:none!important;}
  div.img-viewer > div.iv-frame > div.iv-bg {background-color: #060606 !important;}

table[lang*="/go/"] {display: none;}
table[class="inner_wrap inner_wrap_visible"] {display: none;}
div[style="height: 30px"] {display: none;}
div[style^="width: 100%; background: url('http://s.pikabu.ru/images/header_bg13.gif')"] {
  background: url("http://i.imgur.com/teUgsDm.png")  #f0f0f0 repeat-x bottom!important;
}
div.menu-block-branding td.menu-best-comm {background-color: #1D1D1D !important;}

.b-story__link, .story_link, .b-story__link_visited_yes {
  color: #738290!important; 
}

td[style^="background: url(http://s.pikabu.ru/images/menu_elements_top_bottom.png)"] {display: none!important;}

.rating_bl, .name {
  text-shadow: 0 0 0!important;
  border-bottom: 0px!important;
  background-color: #171616!important;
}

.tag {
  background-color: #171616!important;
  color: #666666!important;
  text-shadow: 0 0 0!important;
  border-color: gray!important;
}

img[src$="keysall.png"] {
  content: url("http://i.imgur.com/tSiAC99.png");
}

img[src$="logout.png"] {
  content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAAQCAYAAAAbBi9cAAAAf0lEQVR42mNgwAHmzZuXExcb+59YjMscBmI0Ozs7hzs6OupbW1vzkmwQSDMQC+PVjM8ggjYTaxADuWDgDfr8+XMOsgayDfry5YsuTFNoaCgbumEkewFdI8UGwTQ7ODgIgNhAWgGvocQYAjVIgCjXwQIbV4DTP9ZoliDRixFC6gHsMeeruMs8GgAAAABJRU5ErkJggg==);
}

input[src$="search_btn4n.png"] {
  content: url("http://i.imgur.com/JoGzRYY.png");
}
  
  /* Затемнение подсказки с клавишами навигации по сайту */
img[src $= "keysall_r.png"] {
  opacity: 0.7;
}
  
img[src $= "question.png"] {
  opacity: 0.7;
}

.name h4 {background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAMkAAAABCAYAAAB0fYrcAAAAg0lEQVR42q1SWwrAMAjbIXzc/6QbSjKClFG2fQRtqzZJe2TEGe4N5hXdrBGV87zWjOybEXkKOHOF1d3JvTkfaF7Cgb2udei/5yF27UJbUBtqXbS81baDN/r17E8uTxzLrxx+B96hvDL6KX/E5c+4alL+9HXlw4ZPzQvvabgrBof4yOECWY/7Wpb7E74AAAAASUVORK5CYII=) no-repeat left bottom!important;}
.name h4 a {color: #738290!important;}
.avatar {border: 4px solid #171616!important;}
.personals {background: none;}
.pc-line-sec {background-color:#171616!important;}
.login_f {border: 1px solid #171616!important;}
.short {color:#777!important;}
.pc-panel-bl {background-color:#222!important;}
.name {background: none;}
a.author {color:#c53!important;}
#search_menu {background:#222!important;}

td[style^="border-left: dotted"] {
  border-left: dotted 1px #171616!important;
}

table[style="width: 90%; background-color: rgb(255, 255, 255);"] {
  background-color:#222!important;
  border: 1px solid #1c1c1c!important;
  border-radius: 15px!important;
}

a[href$="id=awards"] {text-shadow: 0 0 0!important;}

img[src*="/post_img/"]:hover {
  -webkit-filter: grayscale(0); filter: grayscale(0);
  opacity: .99;
}
  
.profile_wrap {
  background: #171616!important;
  text-shadow: 0 0 0!important;
  border: #222!important;
}
  
