@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cictr.com") {

body.bgcolor5 {background-color: #eee !important; color: black !important;}

body table {margin: 0 auto !important;}

td[width="20"] {font-weight: bold; color: #444 !important;}

td.roominfo {background: #eee none !important; color: #000 !important;}
td img[src="/images/timelinebox_leftend.gif"] {display:none !important;}
a.roominfo {color: #000 !important; font-size: 1.2em !important;}
img[src="/images/timeline_empty.gif"] { display: block !important; visibility: hidden; }

img[src="/images/timeline_seperator.gif"] { background: red; visibility: hidden; }

}
