css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  
  /**************
  * General CSS *
  **************/
  
  body {
    background: url("http://i.imgur.com/roQErS3.png") !important;
    background-size: cover !important;
  }
  
  .ps-room.ps-room-light {
    background: transparent;
    border-color: #000;
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.3);
  }
  
  button {
    border: 1px solid #000;
  }
  
  .header {
    background: rgba(255, 255, 255, 0.5);
    background-size: cover;
  }
  
  .maintabbarbottom {
    border-color: #000;
    background: #F8F8F8;
  }
  
  .tabbar a.button.subtle-notifying, .tabbar a.button.subtle-notifying:hover {
    color: #F58438;
  }
  
  .tabbar a.button.notifying {
    border: 1px solid #F26330;
    background: transparent -moz-linear-gradient(center top , #EECC49, #F6AB52);
    text-shadow: 0px -1px 0px #F26330;
    color: #FFF;
  }

  .tabbar a.button.notifying:hover {
    background: #F6AB52;
    border-color: #F26330;
    color: #FFF;
    text-shadow: 0px -1px 0px #F26330;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-color: #000;
    color: #000;
    background: #F8F8F8;
    text-shadow: none;
  }
  
  .tabbar a.button {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3) repeat scroll 0% 0%;
  }
  
  .tabbar a.button:hover {
    background: transparent -moz-linear-gradient(center top , #E3E3E3, #CCC) repeat scroll 0% 0%;
  }
  
  .tabbar a.button, .tabbar a.button:hover {
    border-color: #000;
    text-shadow: none;
    color: #252525;
  }
  
  .ps-popup {
    background: #EEE url("http://i.imgur.com/1Np7mNE.png?2") no-repeat bottom right;
    box-shadow: 1px 1px 0px #FFF inset, -1px -1px 0px #DDD inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
    border-color: #000;
  }

  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #000;
  }
  
  .ps-popup a {
    color: #5D7298;
  }
  
  .popupmenu h3, .credits h2, .credits ul {
    color: #4C8B7A;
  }
  
  a.subtle {
    color: #000;
  }
  
  a.subtle:hover {
    color: #151515;
  }
  
  .avatarlist button {
    border-color: #000;
  }
  
  .avatarlist button:hover {
    border-color: #F4AB38;
  }
  
  
  /*******************
  * General CSS ~end *
  *******************/
  
  /******************
  * Mainmenuwrapper *
  ******************/
  
  .menugroup {
    background: rgba(255, 255, 255, 0.5);
    border: 1px solid #000;
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
  }
  
  .mainmenu .menugroup:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:focus, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:focus, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:focus, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:focus {
    border: 1px solid #4C5081;
    background: transparent -moz-linear-gradient(center top , #7CAAC3, #526180);
    background: linear-gradient(to bottom,hsl(201, 37%, 63%),hsl(220, 22%, 41%));
    text-shadow: 0px -1px 0px #4C5081;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover {
    border: 1px solid #F26330;
    background: transparent -moz-linear-gradient(center top , #EECC49, #F6AB52);
    background: linear-gradient(to bottom,hsl(48, 83%, 61%),hsl(33, 90%, 64%));
    text-shadow: 0px -1px 0px #F26330;
  }
  
  .menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active {
    border: 1px solid #4C5081;
    background: #526180;
    text-shadow: 0px -1px 0px #4C5081;
    color: #FFF;
  }
  
  .select {
    border-color: #000;
    color: #252525;
    background: rgba(255, 255, 255, 0.9);
    text-shadow: none;
  }
  
  .select:hover {
    border-color: #000;
    color: #252525;
    background: rgba(255, 255, 255, 1);
  }
  
  .select:disabled, .select:disabled:hover {
    border-color: #000;
    background: rgba(255, 255, 255, 0.5);
  }
  
  .mainmenufooter {
    padding: 10px;
    border: 1px solid #000;
    background: rgba(255, 255, 255, 0.5);
    border-radius: 20px;
    color: #F6AB52;
  }
  
  .mainmenufooter a {
    color: #F6AB52;
  }
  
  .mainmenufooter a:hover {
    color: #EECC49;
  }
  
  .pm-window h3 {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
    border-color: #000;
    color: #000;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    background: #E3E3E3;
    border-color: #000;
    color: #000;
  }
  
  .pm-window h3.pm-notifying {
    border: 1px solid #F26330;
    background: transparent -moz-linear-gradient(center top , #EECC49, #F6AB52);
    background: linear-gradient(to bottom,hsl(48, 83%, 61%),hsl(33, 90%, 64%));
    text-shadow: 0px -1px 0px #F26330;
    color: #FFF;
  }

  .pm-window h3.pm-notifying:hover {
    background: #F6AB52;
    border-color: #F26330;
    color: #FFF;
    text-shadow: 0px -1px 0px #F26330;
  }
  
  .pm-log, .pm-window.focused .pm-log {
    border-color: #000;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  }
  
  .pm-log-add .textbox {
    border-color: #000;
  }
  
  .pm-log-add .textbox:focus {
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .message-pm {
    color: #000;
    font-style: italic;
  }
  
  .pm-window.news-embed {
    opacity: 0.6;
  }
  
  .pm-window.news-embed:hover {
    opacity: 0.95;
  }
  
  .challenge {
    border-color: #000;
    background: #F3BC54;
    color: #000;
  }
  
  /***********************
  * Mainmenuwrapper ~end *
  ***********************/
  
  /***********
  * Room CSS *
  ***********/
  
  .userlist {
    background: rgba(255, 255, 255, 0.5);
    border-color: #000;
  }
  
  .userlist li {
    background: rgba(255, 255, 255, 0.5);
    border-color: #000;
  }
  
  .userlist li em.group {
    color: #222;
  }
  
  .userlist li button:hover {
    background: #F9F9F9;
  }
  
  .chat-log-add {
    background: rgba(255, 255, 255, 0.5);
    border-color: #000;
  }
  
  .chat-log-add .textbox {
    border-color: #000;
  }
  
  .chat-log {
    background: rgba(255, 255, 255, 0.7);
  }
  
  .highlighted {
    background: #FFB2B2 !important;
  }
  
  .chat-log .chat em {
    padding: 5px;    
  }
  
  .chat-log .chat {
    margin: 3px;
    background: rgba(255, 255, 255, 0.1);
  }
  
  .message small {
    color: #222 !important;
  }
  
  .infobox {
    border-color: #000;
    background: rgba(255, 255, 255, 0.7);
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(255, 255, 255, 0.5);
    background-size: cover;
    color: #000;
    text-shadow: 0px 1px 0px #FFF;
  }
  
  .roomlist a.ilink {
    border-color: #000;
    color: #252525;
    box-shadow: 0px 0px 5px #D5D5D5;
    background: rgba(255, 255, 255, 0.9);
    text-shadow: none;
  }

  .roomlist a.ilink:hover {
    border-color: #000;
    color: #252525;
    background: rgba(255, 255, 255, 1);
  }
  
  .roomlist a.ilink small {
    color: #252525;
  }
  
  .tournament-title {
    background: rgba(255, 255, 255, 0.5);
  }
  
  .tournament-status, .tournament-toggle {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
    border-right: 1px solid #000;
    color: #000;
  }
  
  .tournament-toggle {
    border-left: 1px solid #000;
  }
  
  .tournament-box {
    border-bottom: 1px solid #000;
    border-right: 1px solid #000;
    background-color: rgba(242, 247, 250, 0.85);
  } 
  
  .tournament-bracket {
    border-top: 1px solid #000;
  }
  
  .ladder table, .ladder td, .ladder th {
    border-color: #000;
    text-shadow: none;
  }
  
  .ladder th {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  }
  
  .ladder td {
    background: rgba(255, 255, 255, 0.4);
  }
  
  /*****************
  * Rooms CSS ~end *
  *****************/
  
  /*****************
  * Teambuider CSS *
  *****************/
  
  ul li button {
    margin-bottom: 1px;
  }
  
  .teamchartbox {
    border-top: 1px solid #000;
    background: rgba(255, 255, 255, 0.5);
  }
  
  .ps-room.ps-room-light .textbox {
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .teamchart li.format-select select {
    border: 1px solid #000;
  }
  
  .setmenu {
    top: 3px;
  }
  
  .setmenu button:hover {
    background: #E3E3E3;
    border-color: #000;
  }
  
  .setchart, .setchart-nickname {
    border: 1px solid #000;
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .setdetails, .setstats, .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setdetails:hover, .setstats:hover, .setchart input:focus, .setchart-nickname input:focus, .statform input.inputform:focus, .setdetails:focus, .setstats:focus {
    border: 1px solid #000;
    box-shadow: none;
  }
  
  .setchart label, .setchart-nickname label {
    color: #000;
    text-shadow: none;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    border-color: #000;
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
    color: #000;
    text-shadow: none;
  }
  
  .utilichart {
    text-shadow: none;
  }
  
  .teambuilder-results .firstresult a, .teambuilder-results .firstresult a:hover, .teambuilder-results .result a:hover {
    background: #EEE;
    border-color: #000;
  }
  
  .utilichart b {
    color: #5F769D;
  }
  
  .teambar button {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
    border-color: #000;
  }
  
  .teambar button:hover {
    background: #E3E3E3;
    border-color: #000;
  }
  
  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: #E3E3E3;
    border-color: #000;
  }
  
  /* #999 */
  
  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #666;
  }
  
  /* #777 */
  
  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
    color: #444;
  }
  
  /* #444 */
  
  .utilichart .col {
    color: #222;
  }
  
  .utilichart .illegalcol em {
    border: 1px solid #CD2B2B;
    color: #CD2B2B;
  }
  
  .teambuilder-clipboard-data, .teambuilder-clipboard-data:hover {
    background: transparent -moz-linear-gradient(center top , #F6F6F6, #E3E3E3);
    background: linear-gradient(to bottom,hsl(0, 0%, 96%),hsl(0, 0%, 89%));
    border-color: #000;
  }
  
  .teambuilder-clipboard-data .section {
    border-color: #000;
  }
  
  .teambuilder-clipboard-title {
    text-shadow: none;
    text-decoration: underline;
  }
  
  .teambuilder-clipboard-container {
    border-color: #F26130;
  }
  
  /***********************
  * Teambuilder CSS ~end *
  ***********************/ 
  
  /*************
  * Battle CSS *
  *************/

  .controls {
    background: rgba(255, 255, 255, 0.5);
    color: #000;
    text-shadow: 0px 0px 1px #FFF;
  }
  
  .battle-log {
    background: transparent;
    border-left: none;
    color: #000;
    text-shadow: 0px 0px 1px #FFF;
  }
  
  .battle-log .chat strong {
    color: #000;
  }
  
  .battle-log .chat em {
    color: #000;
    text-shadow: none;
    border: 1px solid #000;
    background: #EEE;
  }
  
  .battle-log .chat small {
    color: #444;
    text-shadow: none;
  }
  
  em[style="color:#445566;display:block;"] {
    color: #000 !important;
    text-shadow: 0px 0px 3px #000;
    font-style: italic !important;
  }
  
  .battle-log h2 {
    border-color: #000;
    border-left: 1px solid #000;
    background: rgba(255, 255, 255, 0.3);
  }
  
  .battle-log-add {
    background: transparent;
  }
  
  .battle-log-add .textbox {
    border-color: #000;
  }
  
  .battle-log-add {
    border-top-left-radius: 5px;
    border: none;
    text-shadow: none;
  }
  
  .battle-log .rated strong {
    background: #000;
    text-shadow: none;
  }
  
  .battle-controls .whatdo {
    color: #212121;
  }
  
  .message-error {
    color: #922;
    border: 1px solid #000;
    background: #EEE;
    margin-top: 1px;
    padding-left: 5px !important;    
  }
  
  .messagebar {
    background: #EEE;
    color: #000;
    border: 1px solid #000;
  }
  
  .moveselect button, .switchselect button {
    color: #212121;
  }
  
  .chat small {
    color: #454545;
  }
  
  .ps-room.ps-room-opaque {
    background: rgba(255, 255, 255, 0.5);
    color: #000;
    text-shadow: none;
    border-right: none;
  }
  
  /* Battle Background */

  .backdrop {
    width: 640px;
    height: 360px;
    position: absolute;
    top: 0px;
    left: 0px;
    overflow: hidden;
    background: -50px -90px;
    opacity: 1 !important;
  }
  
  .backdrop:before{
    display: block;
    content: "";
    background: url("http://www.spacewallpapershd.com/wp-content/uploads/2015/01/space_view_of_the_earth_hd_wallpaper_for_android_.jpg");
    background-size: cover;
    width: 640px;
    height: 360px;
  }
  
  /* Battle Background ~end */
  
  /*******************
  * Leftbar/Rigthbar *
  *******************/

  .rightbar {
    border-color: rgba(215,215,215,0.5);
  }

  .leftbar {
    border-color: rgba(215,215,215,0.5);
  }

  /************************
  * Leftbar/Rigthbar ~end *
  ************************/
  
  /*****************
  * Battle Weather *
  *****************/

  .weather {
    text-shadow:none;
    color: rgba(0,0,0,0);
  }

  .weather em small {
    font-size: 7pt;
    color: white;
    text-shadow: 1px 1px black;
    position: absolute;
    left:0;
    top: 85px;
    width: 95px;
    text-align:center;
  }

  .sunweather, .sunnydayweather, .desolatelandweather {
    background: url(http://i.imgur.com/DNMtOWR.gif) scroll left top;
  }

  .rainweather, .raindanceweather, .primordialseaweather {
    background: url(http://i.imgur.com/NQqkU6j.gif) scroll left top;
  }
  
  .deltastreamweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
  }

  .hailweather {
    background: url(http://i.imgur.com/PweinWm.gif) scroll left top;
  }

  .sandstormweather {
    background: url(http://i.imgur.com/OEB3hum.gif) scroll left top;
  }

  .pseudoweather {
    background: url(http://play.pokemonshowdown.com/fx/weather-trickroom.png) scroll left top;
  }
  
  /**********************
  * Battle Weather ~end *
  **********************/
  
  .messagebar {
    z-index: 10;
  }

  .foehint div {
    z-index: 11;
  }

  .battle-controls {
    background: rgba(51,51,51,0.7);
  }

  .whatdo .healthy {
    position: absolute;
    right: 8px;
  }

  .movecontrols {
    float: left;
  }

  .movemenu {
    width: 302px;
  }

  .movemenu button {
    width: 150px;
    height: 50px;
    border-radius: 25px;
    border-width: 0;
    box-shadow: none;
    color: black;
    font-size: 10pt;
    font-family: Arial, sans-serif;
    padding-top: 0px;
    margin: 0px;
    margin-bottom: 2px;
  }

  .movemenu button:hover {
    top: -2px;
  }

  .movemenu button small {
    text-align: center;
    width: 62px;
    margin-top: 0px;
  }

  .movemenu button small.type {
    display: none;
  }

  .movemenu button small.pp {
    padding: 6px 0px 2px;
    color: white;
    font-size: 8pt;
    text-shadow: none;
  }

  .movemenu button small.pp:before {
    content: 'PP ';
  }

  .movemenu button.type-Normal, .movemenu button.type-Normal:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -50px !important;
  }

  .movemenu button.type-Fire, .movemenu button.type-Fire:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -50px !important;
  }

  .movemenu button.type-Water, .movemenu button.type-Water:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -150px !important;
  }

  .movemenu button.type-Electric, .movemenu button.type-Electric:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px 0px !important;
  }

  .movemenu button.type-Grass, .movemenu button.type-Grass:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -100px !important;
  }

  .movemenu button.type-Ice, .movemenu button.type-Ice:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px 0px !important;
  }

  .movemenu button.type-Fighting, .movemenu button.type-Fighting:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -150px !important;
  }

  .movemenu button.type-Poison, .movemenu button.type-Poison:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px 0px !important;
  }

  .movemenu button.type-Ground, .movemenu button.type-Ground:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -150px !important;
  }

  .movemenu button.type-Flying, .movemenu button.type-Flying:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px -50px !important;
  }

  .movemenu button.type-Psychic, .movemenu button.type-Psychic:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -300px 0px !important;
  }

  .movemenu button.type-Bug, .movemenu button.type-Bug:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll 0px -100px !important;
  }

  .movemenu button.type-Rock, .movemenu button.type-Rock:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -150px -200px !important;
  }

  .movemenu button.type-Ghost, .movemenu button.type-Ghost:hover {
    background: url(http://i.imgur.com/VmJQj2Y.png) no-repeat scroll -450px -50px !important;
  }

  .movemenu button.type-Dragon, .movemenu button.type-Dragon:hover {
