css:
@-moz-document url-prefix("https://gitter.im/") {
.popover, .popover-title, .popover-footer {
	background-color: rgb(29, 29, 29);
	color: white;
}

* {border: none !important;}

.github-push, h1, h2, h3, h4, h4, .js-chat-name, .trpMoreBadge, .trpAddBadge, #top-header {color: white;}

.trpContentPanel {
  background-color: rgb(29, 29, 29) !important;
}

#right-toolbar-header-region {  height: 74px;
	background: #659593;}

#chat-container {
  color:#aaa;
}

.menu-actions {  background-color: rgb(49, 49, 49);}

#profile-region {  background: #659593;
  padding-bottom: 6px;
  padding-top: 7px;}

.chat-header, .chat-input {
   background-color:rgb(48, 48, 48); 
}

.room-list-item--current-room {
  background-color: #1D1D1D !important;
  color: white;
}

a {
  color:white
}

.scroller::-webkit-scrollbar-track {
  background-color: rgb(48, 48, 48);
}

#right-toolbar-layout {
   background-color:black; 
   color: white !important;
}
  
.issue.closed {
    color: white !important
}
      
.chat-input__text-area {
	background-color:#666666;
}
        
.chat-input__box {
	background-color:#666666;
}

.menu {
    background-color:#162E34 !important;
}

.room-list-item--current-room {
    background-color:#6A797D;
}
  
.trpBtnWhite {
    background-color:#6A797D;
    border-bottom: none;
}

#header-wrapper {
	background-color: #659593;
  	color: white !important;
}

.chat-header__avatar img {
   border-radius: 0; 
}
  
.chat-header__content h1 a, .js-chat-topic {color: white !important;}

.chat-input__box {
  width: calc(100% - 80px);
  padding: 2px;
  margin-left: 41px;
  }
  
#chat-input-textarea, .js-chat-name {
   color: white !important; 
  }

.menu-content__wrapper.nano-content {
	padding-top: 10px;
	  background: rgb(48, 48, 48);
}

.trpContentPanel, .chat-item:not(.unread) {
  background-color: rgb(29, 29, 29) !important;
}

.gtrActivityItemText {
  color: rgb(240, 240, 240);
}

#search-results {
	color: rgb(200, 200, 200);
}

.result-empty {
	color: rgb(200, 200, 200);
}
	
#toolbar-top-content::-webkit-scrollbar-track {
  background-color: rgb(48, 48, 48);
}

.result.selected, .result:hover {
  background-color: rgba(240, 240, 240, 0.17);
  border-top-left-radius: 10px;
  border-bottom-left-radius: 10px;
}

em.highlight {
	background-color: rgba(192,201,200,.2);
	border-radius: 2px;
	padding: 0 4px;
	overflow-x: auto;
	color: rgb(231, 215, 54);
	word-wrap: break-word;
}

::-webkit-scrollbar {
  width: 9px;
}

::-webkit-scrollbar-thumb {
  border-radius: 0;
  background: rgba(100, 100, 100, 1);
  transition: opacity 300ms ease-in-out;
}

::-webkit-scrollbar-track {
  background-color: rgb(48, 48, 48);
}

#header-wrapper {
	height: 74px;
  overflow: hidden;
}
  
  #right-toolbar-header-region, #profile-region, #header-wrapper {
    background-color: /*[[font]]*/;
  }
}
