css:
@-moz-document url("http://www.kogama.com/"){
/* i really want this to be global */
/* Thanks HerpyTDerp for the review */ 
.avatar.offline { 
-webkit-border-radius:7px;
-moz-border-radius:7px;
border-radius:7px;
-webkit-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
-moz-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
background-color:rgba(255, 255, 255, 0.0666667);
border:3px outset rgba(255, 255, 255, 0.0666667);
}
.avatar.away{
-webkit-border-radius:7px;
-moz-border-radius:7px;
border-radius:7px;
-webkit-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
-moz-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
background-color:rgba(255,129,61,0.33);
border:3px outset rgba(255,129,61,0.33);
}
.avatar.online{
border:3px outset rgba(34,255,0,0.12);
-webkit-border-radius:7px;
-moz-border-radius:7px;
border-radius:7px;
-webkit-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
-moz-box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
box-shadow:rgb(38, 38, 38) 0px 0px 7px 0px;
background-color:rgba(34,255,0,0.12);
}
/* The bars on the top */
div#user-info-bar{
-webkit-box-shadow:rgba(0, 0, 0, 0.901961) 0px 5px 5px 0px;
-moz-box-shadow:rgba(0, 0, 0, 0.901961) 0px 5px 5px 0px;
box-shadow:rgba(0, 0, 0, 0.901961) 0px 5px 5px 0px;
background-image:-webkit-linear-gradient(0,#000 0%,rgb(0,145,255) 50%,rgb(0,0,0) 100%);
background-image:-moz-linear-gradient(0,#000 0%,rgb(0,145,255) 50%,rgb(0,0,0) 100%);
background-image:-o-linear-gradient(0,#000 0%,rgb(0,145,255) 50%,rgb(0,0,0) 100%);
background-image:-ms-linear-gradient(0,#000 0%,rgb(0,145,255) 50%,rgb(0,0,0) 100%);
}
#navigation .navigation-sub{
box-shadow: rgba(0, 0, 0, 0.901961) 0px 5px 8px 1px; 
background-color:#0a4d7e;
border:5px outset rgba(0, 15, 41, 0.760784);

}
li.games, li.build, li.marketplace, li.news, li.help, li.competition{

}
#navigation .navigation-sub>ul li.active a{
border-bottom-width:4px;
border-style:outset;
border-bottom-color:rgb(0, 55, 107);
-webkit-border-radius:12px;
-moz-border-radius:12px;
border-radius:12px;
border-top-color:rgb(255, 255, 255);
border-top-width:1px;
}
/* Background */ 
div#page-block-middle{
background-image:-webkit-linear-gradient(90deg,rgb(31,40,56) 0%,rgb(0,0,0) 20%,rgb(24,22,41) 50%,rgb(0,0,0) 80%,rgb(31,40,56) 100%);
background-image:-moz-linear-gradient(90deg,rgb(31,40,56) 0%,rgb(0,0,0) 20%,rgb(24,22,41) 50%,rgb(0,0,0) 80%,rgb(31,40,56) 100%);
background-image:-o-linear-gradient(90deg,rgb(31,40,56) 0%,rgb(0,0,0) 20%,rgb(24,22,41) 50%,rgb(0,0,0) 80%,rgb(31,40,56) 100%);
background-image:-ms-linear-gradient(90deg,rgb(31,40,56) 0%,rgb(0,0,0) 20%,rgb(24,22,41) 50%,rgb(0,0,0) 80%,rgb(31,40,56) 100%);
background-color:rgba(24, 41, 33, 0);
}
/* News Feed */
#news-feed ul.news-feed-thumbs>li{
background-color:rgb(94, 94, 94);
color:rgb(0, 0, 0);
font-family:Verdana,sans-serif;
}
#news-feed ul.news-feed-thumbs>li:hover{
background-color:rgb(171,171,171);
color:rgb(0, 0, 0);
font-family:Verdana,sans-serif;
}
textarea#status_message{
-webkit-border-radius:8px;
-moz-border-radius:8px;
border-radius:8px;
-webkit-box-shadow:rgb(23, 23, 23) 0px 2px 10px 0px inset;
-moz-box-shadow:rgb(23, 23, 23) 0px 2px 10px 0px inset;
box-shadow:rgb(23, 23, 23) 0px 2px 10px 0px inset;
}
button.pure-button.pure-button-primary.button-fill{
-webkit-border-radius:100px;
-moz-border-radius:100px;
border-radius:100px;
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
background-color:rgb(245, 67, 54);
}
#news-feed ul.news-feed-thumbs>li>.feed-avatar{
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 8px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 8px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 8px 0px;
-webkit-border-radius:10px;
-moz-border-radius:10px;
border-radius:10px;
}
textarea#comment{
-webkit-box-shadow:rgba(79, 79, 79, 0.843137) 0px 1px 14px 0px inset;
-moz-box-shadow:rgba(79, 79, 79, 0.843137) 0px 1px 14px 0px inset;
box-shadow:rgba(79, 79, 79, 0.843137) 0px 1px 14px 0px inset;
height: auto;
background-color:#cecece;
}
#news-feed .feed-comments .comments li .body{
-webkit-box-shadow:rgba(11, 12, 15, 0.843137) 0px 0px 11px 0px;
-moz-box-shadow:rgba(11, 12, 15, 0.843137) 0px 0px 11px 0px;
box-shadow:rgba(11, 12, 15, 0.843137) 0px 0px 11px 0px;
height: auto;
-webkit-border-radius:8px;
-moz-border-radius:8px;
border-radius:8px;


}
#profile-page .profile-avatar .image .avatar{
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 100px 0px inset;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 100px 0px inset;
box-shadow:rgb(0, 0, 0) 0px 0px 100px 0px inset;
-webkit-border-radius:0px 22px 0px 0px;
-moz-border-radius:0px 22px 0px 0px;
border-radius:0px 22px 0px 0px;

}
/* Games Page */
.game-thumbs-large .box-hover{
-webkit-border-radius:25px;
-moz-border-radius:25px;
border-radius:25px;
width:125px;
height:73px;
padding:0px;
-webkit-transform-origin:50%;
-moz-transform-origin:50%;
-o-transform-origin:50%;
-ms-transform-origin:50%;
transform-origin:50%;
-webkit-transform:matrix(1, 0.1, 0, 1, 0, 0);
-moz-transform:matrix(1, 0.1, 0, 1, 0, 0);
-o-transform:matrix(1, 0.1, 0, 1, 0, 0);
-ms-transform:matrix(1, 0.1, 0, 1, 0, 0);
transform:matrix(1, 0.1, 0, 1, 0, 0);
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 10px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 10px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 10px 0px;
background-color:rgb(0, 0, 0);
}
.display-image{
-webkit-border-radius:15px;
-moz-border-radius:15px;
border-radius:15px;
-webkit-box-shadow:rgb(0, 0, 0) 0px 2px 13px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 2px 13px 0px;
box-shadow:rgb(0, 0, 0) 0px 2px 13px 0px;
}
div#page-block-footer{
background-image:-webkit-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-moz-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-o-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-ms-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
}
/* Game Page */ 
.pure-u-1-3.right-part{
height:235px;
-webkit-box-shadow:rgb(152, 199, 235) 0px 0px 16px 0px;
-moz-box-shadow:rgb(152, 199, 235) 0px 0px 16px 0px;
box-shadow:rgb(152, 199, 235) 0px 0px 16px 0px;
background-image:-webkit-linear-gradient(90deg,rgba(0,0,0,0.4) 0%,rgba(171,171,171,0.137255) 50%,rgba(0,0,0,0.4) 100%);
background-image:-moz-linear-gradient(90deg,rgba(0,0,0,0.4) 0%,rgba(171,171,171,0.137255) 50%,rgba(0,0,0,0.4) 100%);
background-image:-o-linear-gradient(90deg,rgba(0,0,0,0.4) 0%,rgba(171,171,171,0.137255) 50%,rgba(0,0,0,0.4) 100%);
background-image:-ms-linear-gradient(90deg,rgba(0,0,0,0.4) 0%,rgba(171,171,171,0.137255) 50%,rgba(0,0,0,0.4) 100%);
background-color:rgba(209, 229, 255, 0.317647);
}
a.pure-button.pure-button-large.pure-button-success.pure-button-xlarge.play-now-fill.play-now-button{
-webkit-box-shadow:rgb(0, 0, 0) 7px 0px 22px 0px;
-moz-box-shadow:rgb(0, 0, 0) 7px 0px 22px 0px;
box-shadow:rgb(0, 0, 0) 7px 0px 22px 0px;
}
ul.regions.member{
-webkit-box-shadow:rgb(0, 0, 0) 0px 2px 15px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 2px 15px 0px;
box-shadow:rgb(0, 0, 0) 0px 2px 15px 0px;
background-image:-webkit-linear-gradient(0,rgba(46,58,74,0.57) 36%,rgba(46,58,74,0.57) 82%);
background-image:-moz-linear-gradient(0,rgba(46,58,74,0.57) 36%,rgba(46,58,74,0.57) 82%);
background-image:-o-linear-gradient(0,rgba(46,58,74,0.57) 36%,rgba(46,58,74,0.57) 82%);
background-image:-ms-linear-gradient(0,rgba(46,58,74,0.57) 36%,rgba(46,58,74,0.57) 82%);
background-color:rgba(46, 58, 74, 0.65098);

}
/* Marketplace */
.marketplace-thumbs .display-price{ 
-webkit-border-radius:16px;
-moz-border-radius:16px;
border-radius:16px;
-webkit-transform:matrix(0.9, -0.1, 0.1, 1, 3, 3);
-moz-transform:matrix(0.9, -0.1, 0.1, 1, 3, 3);
-o-transform:matrix(0.9, -0.1, 0.1, 1, 3, 3);
-ms-transform:matrix(0.9, -0.1, 0.1, 1, 3, 3);
transform:matrix(0.9, -0.1, 0.1, 1, 3, 3);
color:rgb(247, 247, 247);
border:4px outset rgb(79, 79, 79);
background-color:rgb(34, 36, 46);
}
.marketplace-thumbs .display-name {
border-width:4px;
border-style:groove;
-webkit-border-radius:13px;
-moz-border-radius:13px;
border-radius:13px;
}
#marketplace-detail .product-price{
-webkit-border-radius:24px;
-moz-border-radius:24px;
border-radius:24px;
height:53px;
width:315px;
background-color:rgba(75, 88, 107, 0.458824);
line-height:59px;
}
button.pure-button.pure-button-primary.pure-button-xlarge.purchase-button{
-webkit-border-radius:32px;
-moz-border-radius:32px;
border-radius:32px;
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 18px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 18px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 18px 0px;
}
.comments li .body{
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 15px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 15px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 15px 0px;
height:40px;
-webkit-border-radius:0px;
-moz-border-radius:0px;
border-radius:0px;

}
.marketplace-thumbs .box{
border-bottom-width:4px;
border-style:outset;
border-bottom-color:rgb(0, 55, 107);
-webkit-border-radius:12px;
-moz-border-radius:12px;
border-radius:12px;
border-top-color:rgb(255, 255, 255);
border-top-width:1px;
}
.marketplace-thumbs .box-hover{
border:2px groove rgb(30, 34, 46);
-webkit-border-radius:15px;
-moz-border-radius:15px;
border-radius:15px;
background-color:rgba(36, 42, 48, 0.74902);
}
.marketplace-thumbs-small .display-image{
border-width:3px;
border-style:solid;
border-bottom-color:rgb(0, 55, 107);
-webkit-border-radius:12px;
-moz-border-radius:12px;
border-radius:12px;
border-top-color:rgb(255, 255, 255);
}
/* Leaderboard */
#profile-leaderboard .table-wrapper{
background-image:-webkit-linear-gradient(90deg,rgba(53,72,84,0.0901961) 2%,rgba(53,72,84,0.407843) 100%);
background-image:-moz-linear-gradient(90deg,rgba(53,72,84,0.0901961) 2%,rgba(53,72,84,0.407843) 100%);
background-image:-o-linear-gradient(90deg,rgba(53,72,84,0.0901961) 2%,rgba(53,72,84,0.407843) 100%);
background-image:-ms-linear-gradient(90deg,rgba(53,72,84,0.0901961) 2%,rgba(53,72,84,0.407843) 100%);
background-color:rgba(53, 72, 84, 0);
}
/* Help */ 
#main-secs section.section h3 a, #main-secs section.section h3 a i{
-webkit-border-radius:41px;
-moz-border-radius:41px;
border-radius:41px;
border:5px outset rgb(46, 128, 179);

}
div#footer{
background-image:-webkit-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-moz-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-o-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
background-image:-ms-linear-gradient(90deg,rgb(45,48,61) 0%,rgb(0,0,0) 2%,rgb(45,48,61) 5%,rgb(14,17,18) 50%,rgb(45,48,61) 95%,rgb(0,0,0) 98%,rgb(45,48,61) 100%);
}
.search input[type=search], a.button-blue:hover, .button-blue:hover{
-webkit-border-radius:15px;
-moz-border-radius:15px;
border-radius:15px;
-webkit-box-shadow:rgba(31, 31, 31, 0.85098) 0px 6px 9px 0px inset;
-moz-box-shadow:rgba(31, 31, 31, 0.85098) 0px 6px 9px 0px inset;
box-shadow:rgba(31, 31, 31, 0.85098) 0px 6px 9px 0px inset;
}
a.button-blue, .button-blue{
transition: 1s border-radius;
}

/* Transitions :( */ 
button.pure-button.pure-button-primary.button-fill{
transition: .1s background;

}
.pure-button-hover, .pure-button:hover, .pure-button:focus{
background-color: rgb(42, 165, 160);
}
li.games, li.build, li.marketplace, li.news, li.help, li.competition{
transition: .4s background;
}
li.games:hover, li.build:hover, li.marketplace:hover, li.news:hover, li.help:hover, li.competition:hover{
background-color: rgba(0, 0, 0, 0.76); 
}
li.friends, li.profile, li.levels, li.xp_boosters, li.leaderboard{
transition: .4s background;
}
li.friends:hover, li.profile:hover, li.levels:hover, li.xp_boosters:hover, li.leaderboard:hover{
background-color: rgba(0, 0, 0, 0.76); 
}
.avatar.offline, .avatar.online, .avatar.away{
transition: 1s background-color;
}
.avatar.offline:hover{
background-color: rgba(253, 253, 253, 0.21);
}
/* Improvements */ 
/*Transition*/ 
#news-feed ul.news-feed-thumbs>li .feed-item a{
transition: 0.5 all;
}

#news-feed ul.news-feed-thumbs>li .feed-item a:hover{
color: #0146ad;
}
/*end transition*/
#news-feed ul.news-feed-thumbs>li .feed-time{
color: #111022;
}
#news-feed .feed-comments .comments form button{
transition: .1s background;
-webkit-border-radius:100px;
-moz-border-radius:100px;
border-radius:100px;
-webkit-box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
-moz-box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
box-shadow:rgb(0, 0, 0) 0px 0px 13px 0px;
background-color:rgb(245, 67, 54);
}
textarea#comment:focus{
background-color:rgba(255, 255, 255, 1);

}

#news-feed .feed-comments .comments li .avatar a{
-webkit-box-shadow:rgba(0, 0, 0, 0.870588) 0px 0px 9px 0px;
-moz-box-shadow:rgba(0, 0, 0, 0.870588) 0px 0px 9px 0px;
box-shadow:rgba(0, 0, 0, 0.870588) 0px 0px 9px 0px;
}

.comments{
background-color:rgb(94, 94, 94);
color:rgb(0, 0, 0);
font-family:Verdana,sans-serif;
border-left-width:1px;
border-bottom-width:1px;
border-right-width:1px;
border-style:solid;
border-color:rgb(255, 255, 255);

}
.comments:hover{
background-color:rgb(171,171,171);
color:rgb(0, 0, 0);
font-family:Verdana,sans-serif;
}
#news-feed ul.news-feed-thumbs>li:hover .footer, #news-feed ul.news-feed-thumbs>li:hover .feed-footer{
background-color:rgba(68, 68, 68, 0);
height:1px;
}
#news-feed ul.news-feed-thumbs>li .footer, #news-feed ul.news-feed-thumbs>li .feed-footer{
background-color:rgba(68, 68, 68, 0);
height:1px;

}
}
