css:
@-moz-document url-prefix("http://snapzu.com"), domain("snapzu.com") 

{

body,html,sd,ul,.date {
    color: rgb(204, 204, 204) !important;
    font-family:Calibri,Arial !important;
    font-size: 18px !important;
    background-color: rgb(34, 34, 34) !important;
}

li {
    
 background-color: rgb(51, 51, 51) !important;;   
    
}

/*Pre*/
pre {
    
    color: white !important;
 background-color: rgb(45, 67, 81) !important;   
    
}

/*No Way Else*/
a {
    
	background-color: rgba(0, 0, 0, 0.01) !important;
    
}

/*Colors*/
h1,h2,h3 {
    color: white !important;
}

/*Message Colors*/
.messageText, .messageContainer,#system-messages-info,.messageList, .desc {

    color: white !important;
    
}

/*Messages Title Bar*/
.title {
    
    background-color: transparent !important;
    
}

/*Submitting a Discussion*/
.editFooter,.editHeader {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Submitting Buttons Issue*/
li.markItUpButton.markItUpButton1,span.type.system {
    
    background-color: transparent !important;
	color: white !important;   
    
}

/*Submitting Post Title*/
label.title,#chartCounter_for_title.chartCounter {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Gerneral Comment Text*/
p {
    color: rgb(204, 204, 204) !important;
    font-family:Calibri,Arial !important;
    font-size: 18px !important;
}

/*Banner Text fix*/
.tribeHeader {
 
    padding-bottom: 10px !important;
    font-size: 29px !important;
    
}

/*Comment Filters*/
div#comments-filters,.commentsFilter {
    
    padding-top: 10px !important;
    background-color: rgb(34, 34, 34) !important;
    
}

/*Snap Related Photos*/
.photo,p {
    background-color: transparent !important;
    transition: background-color black !important;
    transition-property: background-color;
    transition-duration: 0.1s;
    transition-timing-function: linear;
    transition-delay: initial;
    font-size: 16px !important;
}

/*Snap Main Content*/
.brickContent,.brickBox,.intro,div.cell,header,.snapDesc,.photo,h2 {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Snap Extra*/
.table {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Snap Extra Title*/
strong {
    
 color: white !important;   
    
}

/*Snap Summary Box*/
.summaryBox {
 
    background-color: rgba(0, 0, 0, 0.8) !important;
    
}

/*Snap Vote Box*/
.votingBox {
    
	background-color: rgba(0, 0, 0, 0.8) !important;
    
}

/*Banner Fixed*/
.banerContainer,nav.menuBar {
	/*
    background-color: rgb(34, 34, 34) !important;
    */
}

/*Link URL*/
a.mainlink-url,img.favico {
    
    font-size: 12px !important;
    background-color: rgb(45, 67, 81) !important;
    
}

/*Vote Bar*/
.snapVoteBar,.postVoteBar {
    background-color: rgb(51, 51, 51) !important; 
}

/*Vote Bar Extra*/
.voteBar.highlight,.voteBar {
    
 	background-color: rgb(51, 51, 51) !important; 
    
}

/*Vote Bar Details*/
.details {
    /*
    background-color: #4f4f4f !important;
    */
    background-color: transparent !important;
}

/*Post Background*/
header.snapTitle.link {
    
    background-color: rgb(51, 51, 51) !important;
}

/*Post URL Background*/
.mainlink-url {
    color: White !important;
    background-color: #4f4f4f !important;
}

/*Post Extras*/
.snapExtras,#snap-extras {
    background-color: transparent !important;
}

/*UserName*/
.user {
 	
    font-size: 17px !important;
    background-color: transparent;
    
}

/*Pagination*/
.paginationList {
    
 border-right: 0px !important;
    
}

/*Filter Bar*/
.filter {
    
    color: white !important;
    background-color: rgb(45, 67, 81) !important;
    
}

/*Posted In...*/
h4.title.light {
    
 	color: #2393cd !important;
    
}

/*Moderator Color Change*/
.darkRed {
    
    color: #FF0000 !important;
    font-weight: bold !important;
    
}

/*OP Color Change*/
a.user.green {
    
	color: #668E39 !important;
    font-style: italic !important;
    
}

/*Button background fix*/
ul.tribes,.tribes{
    
 background: transparent !important;
    
}

/*More Buttons*/
.btn.btn-sm.btn-link {
 
	color: white !important;
    background-color: #333333 !important;
    
}

/*Other buttons*/
.niches, .nichesBtn, .select, .selected{
    
    color: white !important;
    background-color: rgb(45, 67, 81) !important;
    
}

/*Vote Progress*/
.progress {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Selected Button*/
.selected {
 
    color: white !important;
    background-color: rgb(45, 67, 81) !important;
    
}

/*Main Background*/
.bgLightGrey {
    
    background-color: rgb(34, 34, 34) !important;
    
}

/*Conversation Box*/
.comment.L.conversation {
    
    background-color: rgb(51, 51, 51) !important;
}

/*Label Color*/
div.label {
    
 color: white !important; 
    
}

