.tribeProfile {
    
    margin: 0 0 0px !important;
    background-color:  rgb(51, 51, 51) !important;
}

/*More Header Colors, Tribe Bar Related*/
.userMenu,#notificationHistory.notifications_history {
    /*
    background-color: rgb(34, 34, 34) !important; 
	*/
    /* Changing font size here could mess a lot up -- check for anything*/
    font-size: 1px !important;
    background-color: transparent !important;
}

/*Notify*/
span.note {
    
	color: black !important;   
    background-color: black !important;
    
}

/*Bookmarklet Note*/
.bookmarkletBox {
    
	background-color:  rgb(51, 51, 51) !important;
    
}

/*User Height*/
span.user {
    
    height: 58px !important;
    
}

/*Title*/
listItems.md,a {
  color: white !important;
}

/*Count*/
.count {
    
    background-color: rgb(45, 67, 81) !important;
    
}

/*Name*/
h3.name {
 
    color: white !important;
    
}

