.siteContainer{
    
    background-color: rgb(34, 34, 34) !important;
    
}

.content {
 
    background-color: rgb(51, 51, 51) !important;
}

/*Transparency*/
ul#container,header.site,ul#related_links_items.addedLinks {
 background-color: transparent !important;   
}

