}

/*Bar Color Blue*/
li.blue {
    background-color: #2393cd !important;
}

/*Bar Color Grey*/
li.grey {
    background-color: #5a5a5a !important;
}

/*Bar Color Green*/
li.green {
    background-color: #7eb224 !important;
}

