css:
@-moz-document domain("play.spotify.com") {
#bar-inner {
  background: /*[[Color]]*/;
}
.main-menu.narrow-menu a.active:after, .main-menu.narrow-menu a.selected:after {
  background-color: /*[[Color]]*/;
}
.nav > li.active > a:after {
  background-color: /*[[Color]]*/;
}
.btn:hover:not(.just-changed):not(.stay-added) {
  color: /*[[Color]]*/;
  border-color: /*[[Color]]*/;
}
.btn-primary {
  color: #FFFFFF;
  background: /*[[Color]]*/;
  border-color: /*[[Color]]*/;
}
.list-group-item.list-group-item-media.active:before {
  background: /*[[Color]]*/;
}
.btn-primary:not(.just-changed):not(.stay-added):hover {
  color: /*[[Color]]*/;
  background: #121314;
  border-color: /*[[Color]]*/;
}
.btn:hover:not(.just-changed):not(.stay-added):after {
  display: block;
  border: 1px solid /*[[Color]]*/;
}
}
