css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leboncoin.fr") {
#page_align > div:first-child {
    display: none;
}
.oas-top, .comment, .navlist.action, .list-gallery, .image-and-nb .nb .top, #headermain .header_title, #urgentsearch, #lost_passwd .create_acc .label_new {
    display: none;
}
body, .page_width, #page_align {
    background: #e9edf2  none repeat scroll 0 0 !important;
}
    /* HEADER */
#account_login_f {
    background: #ddd none no-repeat scroll 0 0;
}

#screen_form input[type="submit"], #pass_lost_f input[type="submit"] {
    background: #22baa0  none no-repeat scroll 0 0;
}
.navmain li.selected a {
    color: #22baa0;
}

    /* RECHERCHE */
.search_box {
    background: #fff none repeat scroll 0 0;
    border-radius: 0;
    margin-bottom: 30px;
    box-shadow: 0 5px 5px -5px rgba(0, 0, 0, 0.1);
}
.between .select {
    background: #eee none repeat scroll 0 0;
    border-radius: 0 3px 3px 0;
}
.between .type {
    background: #34425a  none repeat scroll 0 0;
    border-radius: 3px 0 0 3px;
    color: #fff;
}
.submitsearch {
    background: #eee none repeat scroll 0 0;
    display: inline-block;
    float: none !important;
    margin-top: 40px !important;
    min-height: 80px;
    width: 250px;
}
#searchbutton {
    background: #22baa0 none repeat scroll 0 0;
    border: 0 none;
    border-radius: 3px;
    width: 250px;
    height: 80px;
    float: none !important;
    margin: auto !important;
    animation: all ease 1.3s;
}
#searchbutton:hover {
    background: #34425a;
}
    
    
    /* ONGLETS */
.navlist.type li.selected {
    background: #22baa0 none repeat scroll 0 0;
    color: #fff;
    font-size: 14px !important;
}
.navlist.type li {
    background: #34425a;
    border-radius: 0;
    height: 25px !important;
}
.navlist.type li a {
    color: #899dc1;
    font-size: 14px !important;
}
.navlist li {
    padding-top: 10px !important;
}
.navlist.type li span.value {
    display: none;
}
.content-border {
    background: #2b384e none repeat scroll 0 0;
    border-radius: 0;
    padding: 3px 0;
}
    
    /* CONTENU */
    
    
.list-lbc {
    float: left;
    margin: 0;
    width: 100%;
}
.content-color {
    padding: 10px 0px !important;
    background: #fff;

}

.date::before {
    background: rgba(0, 0, 0, 0) url("https://cdn3.iconfinder.com/data/icons/linecons-free-vector-icons-pack/32/calendar-20.png") repeat scroll 0 0;
    content: "";
    display: inline-block;
    height: 20px;
    left: -30px;
    position: relative;
    top: 25px;
    width: 20px;
}
.list-lbc .lbc .date {
    color: #000;
    float: right;
    font-weight: normal;
    padding: 20px 20px 0 0;
    text-align: left;
    width: 90px;
}
    
.list-lbc .lbc .image {
    margin-left: 30px;
}
.list-lbc .lbc .image img{
    border: 8px solid #fff;
    box-shadow: 0 0 3px #aaa;
}
.list-lbc .lbc .image {
    padding: 20px 0 0;
}
.image-and-nb .nb .value {
    border-radius: 50%;
    width : 20px;
    height: 20px;
    line-height: 20px;
    box-shadow: 0 0 0 3px #fff;
}
.image-and-nb .nb {
    width: 26px;
    left: -10px;
}
.image-and-nb .nb div {
    background: #22baa0;
    text-align: center;
}
.lbc .detail div{
    color: #666 !important;
}
.lbc .detail .price{
    color: #22baa0 !important;
    font-size: 18px;
}
.lbc .detail h2.title {
    font-size: 16px;
    margin: 0;
}
.list-lbc .lbc .detail {
    float: left;
    overflow: hidden;
    padding: 25px 0 0 20px;
    width: auto;
}
.list-lbc a:hover {
    animation: ease 0.8s all;
}
.list-lbc a .lbc{
    animation: ease 0.8s all;
    padding-bottom:20px;
}
.list-lbc a:hover .lbc {
    background: #fafafa  none repeat scroll 0 0;
    animation: ease 0.8s all;
}
    
    
/* ARTICLES */

    
.fine_print {
    background: #34425a none repeat scroll 0 0;
    color: #eee;
    display: block;
    font-size: 12px;
    height: 30px;
    line-height: 30px;
    margin-top: -10px;
    padding-bottom: 5px;
    padding-left: 20px;
}
.fine_print a {
    color: #22baa0 ;
}
.header_adview h1 {
    font-size: 24px;
    padding-left: 20px;
    color: #34425a;
}
.colRight {
    padding-left: 20px;
}
.lbcOptions{
    margin: 0;
}
.lbc_option_box_title {
    background: #34425a none repeat scroll 0 0;
    color: #CFE0FF;
    padding-top: 1px;
    width: auto;
    height: 45px;
    line-height: 45px; 
}
.lbc_option_box {
    margin-bottom: 40px;
}
.lbc_option_box.lbc_actions ~ div{
    display: none;
}
.lbc_title_triangle {
    display: none;
}
.images_cadre {
    border: none;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAAGElEQVQYlWNgIALcvXv3/6gieipiYGAAALhsI92CpMAeAAAAAElFTkSuQmCC) repeat;
}
a.thumbs_cadre, a.thumbs_cadre.active {
    border: none;
    background: #eee;
}
a.thumbs_cadre{
    opacity : 0.8;
    animation: ease all 0.3s;
}
a.thumbs_cadre:hover{
    opacity : 1;
}
a.thumbs_cadre.active{
    opacity : 1;
}
div.lbcParams .price td {
    color: #22baa0;
}
.lbcParams .floatLeft + .floatRight {
    display: none;
}
.AdviewContent .content {
    font-size: 14px;
    line-height: 24px;
    width: 100%;
}
.bottomLinks {
    margin: 0 20px;
    padding: 25px 0 0;
    text-align: center;
}
  
.container_for_adsense {
    padding-left: 20px;
}
.bottomLinks a {
    background: #22baa0 none no-repeat scroll 0 0;
}

    /* DEPOSER ANNONCE */
.subtitle_triangle, .new_block .triangle.below {
    display: none;
}
#newad_form > h2, #comptepro_disable_fields > h2 {
    background: #34425a none repeat scroll 0 0;
    color: #fff;
    font-size: 14px;
    height: 35px;
    line-height: 35px;
    margin: 30px 0 15px 0;
    padding: 0;
    text-align: center;
    text-transform: uppercase;
}

.new_block {
    position: absolute;
    right: 0;
    top: 14px;
}
.photosup_ad_option_box_class {
    background-color: #eee;
}
.new_block .triangle.left {
    border-color: #eee #22baa0 #eee #eee;
}
input.button, a.button, .new_block .text {
    background: #22baa0 none repeat scroll 0 0;
}

}
