css:
@-moz-document domain("moodle.zis.ch") {
/*Latest version*/

/*ALL TITLES*/
	/*Remove top padding so title sticks to the top*/
div[id^="inst"][class^="block"]{
	padding-top: 0px!important;
}
	/*Remove triangle under title*/
div[id^="inst"][class^="block"]>div.header{
	background: none!important;
}
	/*Remove margin and border-radius*/
div[id^="inst"][class^="block"]>div.header>div.title{
 	margin: 0px 0px 0px 0px!important;	
	border-radius: 0px 0px 0px 0px!important;
}
	/*Remove top margin*/
div[id^="inst"][class^="block"]>div.header>div.title h2{
	margin-top: 3px!important;
}
	/*Remove useless span before */
span#maincontent{
	display: none!important;
}
/*END ALL TITLES*/

/*ANNOUNCEMENTS TITLE*/
#rssfeed-wrap{
	padding-top: 0px!important;	
}
#rssfeed-wrap>div.header{
	background:none!important;
}
#rssfeed-wrap>div.header>div.title{
	margin: -1px -10px 0px -10px!important;
	border-radius: 0px 0px 0px 0px!important;
}
/*END ANNOUNCNEMENTS TITLE*/

/*QUICK LINKS SIDEBAR*/
	/*Move Lion's journal closer to other links*/
a[href="http://www.lionsjournal.org/"] img#yui_3_7_3_2_1434289537944_121{
	margin-bottom: -10px !important;	
}
	/*Make wide enough so no scroll X*/
div#region-pre.block-region{
	width:226px !important;
}
	/*Reposition main block*/
div#region-main-wrap > div#region-main{
	margin-left: 436px !important;
}
	/*Make link the whole size of the table cell */
td:nth-child(2)>a, td:nth-child(2)>p>a{
	display: inline-block!important;
	padding-left: 80px!important;
	height: 65px!important;
	width: 140px!important;
	position: relative!important;
	right: 80px!important;
	margin-right: -80px!important;
	bottom: 6px!important;
	margin-bottom: -15px!important;
	line-height: 65px!important;
}

	/*Remove useless links*/
table[style] tbody tr:nth-of-type(3),
table[style] tbody tr:nth-of-type(4),
table[style] tbody tr:nth-of-type(5),
table[style] tbody tr:nth-of-type(6),
table[style] tbody tr:nth-of-type(9),
table[style] tbody tr:nth-of-type(11),
table[style] tbody tr:nth-of-type(12),
table[style] tbody tr:nth-of-type(13),
table[style] tbody tr:nth-of-type(17){	
	display: none !important;	
}
	/*Make table height as small as possible*/
p[style] + table{
	height: auto !important;
}
	/*Change icons1 Announcements-normal */
table[style] tbody tr:nth-of-type(1)::before{
	background-image: url("http://s5.postimg.org/eo84fda6v/announcement_icon.png")!important;
}
	/*Change icons2 Advisory-normal*/
table[style] tbody tr:nth-of-type(2)::before{
	background-image: url("http://s5.postimg.org/w08h0t3o3/lightbulb_icon.png")!important;	
}
	/*Change icons7 CAS-normal*/
table[style] tbody tr:nth-of-type(7)::before{
	background-image: url("http://s5.postimg.org/pdlt7mlzr/IB_LOGO.png")!important;
}
	/*Change icons8 Naviance-normal*/
table[style] tbody tr:nth-of-type(8)::before{
	background-image: url("http://s5.postimg.org/93vn4qbbr/Untitled_1_300_283.png")!important;
}
	/*Change icons10 Powerschool-different*/
table[style] tbody tr:nth-of-type(10)::before{
	overflow: hidden!important;
	background-image: url("http://s5.postimg.org/gxw8q4j4n/vector_circle_p_logo.png")!important;
	background-size: 127px auto!important;
	background-position: -30px!important;
}
	/*Change icons14 Library-normal*/
table[style] tbody tr:nth-of-type(14)::before{
	background-image: url("http://s5.postimg.org/bazvsnglz/best_online_marketing_books_for_entrepreneurs_in.png")!important;
}
	/*Change icons15 Audiobooks-normal*/
table[style] tbody tr:nth-of-type(15)::before{
background-image: url("http://s5.postimg.org/u4lopnetz/ebook_services.png")!important;
}
	/*Change icons16 Vocab-different*/
table[style] tbody tr:nth-of-type(16)::before{
	content: ""!important;
	height: 65px!important;
	width: 65px!important;
	background-image: url("http://s5.postimg.org/3jo7r7is7/image.jpg")!important;
	border-radius: 65px 65px!important;
	margin-bottom: 5px!important;
}
	/*Change icons18 Zis_link-normal*/
table[style] tbody tr:nth-of-type(18)::before{
	background-image: url("http://s5.postimg.org/p7d61nj6b/logo_footer.png")!important;
}
	/*Change icons19  GCD-normal*/
table[style] tbody tr:nth-of-type(19)::before{
	background-image: url("http://s5.postimg.org/tnilx4313/Global_Citizen_Diploma_Logo.png")!important;
}
	/*Remove all OG images*/
table[style] tbody tr td:first-child a img[src]{
	display: none!important;
}
	/*Common all links images*/
table[style] tbody tr::before{
	content: ""!important;
	height: 70px!important;
	width: 70px!important;
	float: left!important;
	left: 0px!important;
	top: 5px!important;
	margin-top: 10px!important;
	position: relative!important;
	background-size: 65px 65px!important;
	background-repeat: no-repeat!important;
}
	/*Remove cell & space before title*/
table[style] tbody tr td:nth-of-type(1){
	display: none!important;	
}
	/*Add space at the top of text to align with the logo*/
table[style] tbody tr td:nth-of-type(2){
	padding-top: 20px !important;	
}
/*END QUICK LINKS*/
}
