css:
@-moz-document domain("google.com") {
.tsf-p, a{
    -webkit-transition: all 0.5s;
}

#searchform{
	animation-name: fadeinanimraise;
    animation-duration: 0.5s;
}

#hplogo{
    -webkit-transition: transform 3s;
    -webkit-perspective : 100 px;
    
    animation-name: logoanim;
    animation-duration: 1s;
}

#gb{
    -webkit-perspective : 100 px;
    animation-name: topbaranim;
    animation-duration: 2s;
}

#fbar{
    -webkit-perspective : 100 px;
    animation-name: footeranim;
    animation-duration: 3s;
    -webkit-transform-origin: bottom;
}

.jsb, #resultStats{
    animation-name: fadeinDelay;
    animation-duration: 3s;
}

.g, .mnr-c{
    animation-name: fadeinanimraise;
    animation-duration: 0.5s;
}

.xpdopen{
    -webkit-perspective : 100 px;
    -webkit-transform-origin: left;
    animation-name: logoanim;
    animation-duration: 2s;
}

.hdtb-mitem, .hdtb-mitem#hdtb-more, .hdtb-tl{
    -webkit-transform-origin: top;
    animation-name: fadeinanim;
    animation-duration: 1s;
}

@keyframes logoanim {
    from{ -webkit-transform: rotatey(90deg) rotatex(20deg) rotatez(10deg);
          opacity : 0}
    

    to{-webkit-transform: rotateX(0deg) rotateY(0deg) rotateZ(0deg)}
}

@keyframes topbaranim {
    from{ -webkit-transform: rotatey(90deg) rotatex(30deg) rotatez(-20deg)}

    to{-webkit-transform: rotateX(0deg) rotateY(0deg) rotateZ(0deg)}
}

@keyframes footeranim {
    from{ -webkit-transform: rotatex(90deg)}

    to{-webkit-transform: rotateX(0deg) rotateY(0deg) rotateZ(0deg)}
}

@keyframes fadeinanim {
    from{ opacity : 0}

    to{opacity : 100}
}

@keyframes fadeinanimraise {
    from{ opacity : 0;
		  -webkit-transform: translate(0px, 20px) }

    to{   opacity : 100;
	      -webkit-transform: translate(0px, 0px)}
}

@keyframes fadefromleft {
    from{ opacity : 0;
		  -webkit-transform: translate(-3vw, 0px) rotatey(90deg)}

    to{   opacity : 100;
	      -webkit-transform: translate(0px, 0px)}
}

@keyframes fadeinDelay {
    0%{ opacity : 0}

    80%{opacity : 0}
    
    100%{opacity : 100}
}

#hplogo:hover{
 	-webkit-transform: rotateX(360deg) rotateY(360deg) rotateZ(360deg)
}
}
