css:
@-moz-document domain("tweetdeck.twitter.com") {

/*******************************************************
SIDEBAR
*******************************************************/

.is-open,
html.dark .is-open {
    transform: translateX(200px) !important;
    margin-right: 200px !important;
    transition-duration: 200ms !important;
}

.drawer,
html.dark .drawer {
    width: 200px;
    left: -200px;
}
.is-open .drawer:after,
html.dark .is-open .drawer:after {
    left: 200px;
}
.nbfc.compose-account-names.link-clean b,
html.dark .nbfc.compose-account-names.link-clean b {
    display: none!important;
}
.nbfc.compose-account-names.link-clean p,
html.dark .nbfc.compose-account-names.link-clean p {
    margin-top: 7px;
    line-height: 20px;
}
.is-list .compose-account,
html.dark .is-list .compose-account {
    float: none;
    margin-right: 0;
    margin-bottom: 5px;
    padding: 5px;
    background-color: #5E727E;
}
.txt-size--13,
.txt-base-small,
html.dark .txt-size--13,
html.dark .txt-base-small {
    font-size: 14px !important;
}
/*------------------ICONS------------------*/
.icon-large,
html.dark  .icon-large{
    font-size: 20px;
    height: 26px;
    width: 26px;
}

.nav-user-info .tweet-avatar {
    width: 36px;
    height: 36px;
    margin: auto!important;
    border-radius: 50%!important;
    border: none!important;
}

ul.js-int-scroller {
    padding: 0 4px!important;
}
.with-nav-border-t:before {
    position: relative;
    top: -15px!important;
    border-top: 1px solid #82929f!important;
}
/*******************************************************
ACCOUNT SELECTION
*******************************************************/

.compose-account,
html.dark .compose-account {
    position: relative;
    float: left;
    background-color: #5E727E;
    border-radius: 0;
    color: #fff;
    padding: 0px;
    margin: 8px;
}
.compose-account .avatar.size48,
html.dark .compose-account .avatar.size48 {
    width: 30px;
    height: 30px;
}
.compose-account .avatar.size32,
html.dark .compose-account .avatar.size32 {
    width: 16px;
    height: 16px;
}
.obj-left .avatar.size32,
html.dark .obj-left .avatar.size32 {
    width: 32px;
    height: 32px;
}
.is-grid .compose-account:nth-child(4n+4),
.is-minigrid .compose-account:nth-child(6n+6),
html.dark .is-grid .compose-account:nth-child(4n+4),
html.dark .is-minigrid .compose-account:nth-child(6n+6) {
    margin-right: 5px;
}
.is-minigrid .compose-account,
html.dark .is-minigrid .compose-account {
    margin-right: 5px;
    margin-bottom: 5px;
}
img.avatar.compose-account-img,
html.dark img.avatar.compose-account-img {
    width: 30px;
    height: 30px;
}
.is-list .compose-account,
html.dark .is-list .compose-account {
    float: none;
    margin-right: 0;
    margin-bottom: 5px;
    padding: 0;
    background-color: #5E727E;
}
.is-selected.compose-account .compose-account-img,
html.dark .is-selected.compose-account .compose-account-img {
    box-shadow: none;
}
a.js-account-item.compose-account.is-actionable.is-selected.cf.link-clean,
.compose-account.is-selected,
html.dark a.js-account-item.compose-account.is-actionable.is-selected.cf.link-clean ,
html.dark .compose-account.is-selected {
    background: #55acee;
}
.compose-account-selected,
html.dark .compose-account-selected {
    display: none !important;
    position: absolute;
    bottom: 0;
    right: 37px;
    width: 13px;
    height: 13px;
    padding: 14px 5px 15px 5px;
    text-align: center;
    border-radius: 10px 0 0 10px;
    background-color: #52c33f;
    box-shadow: none;
}
.nbfc.compose-account-names.link-clean p,
html.dark .nbfc.compose-account-names.link-clean p {
    margin-top: 10px;
    line-height: 20px;
}
.txt-size--13, 
.txt-base-small,
html.dark .txt-size--13 ,
html.dark .txt-base-small {
    font-size: 12px !important;
}
/*******************************************************
TWEETS
*******************************************************/
.tweet {
    padding: 0 5px 0 3px;
    border-top: 1px solid #E0E7ED;
}
html.dark .tweet {
    padding: 0 5px 0 3px;
    border-top: 1px solid #444444;
}
.txt-size-variable--18,
.txt-large,
html.dark .txt-size-variable--18,
