.tweet-header,
html.dark .tweet-header {
    height: 26px;
    margin: 0 0 10px -5px;
}
.tweet-avatar,
html.dark .tweet-avatar {
    width: 26px;
    height: 26px;
    margin: 0px 0 0 0px;
    border-radius: 0;
    border: 5px solid transparent;
}
.is-conversation .tweet-avatar,
html.dark .is-conversation .tweet-avatar {
