    background-color: #FFFFFF;
}
html.dark .stream-item {
    border-bottom: 0px solid #292F33;
    background-color: #222426;
}
/*******************************************************
TWEETS FOOTER ICONS
*******************************************************/
.tweet-footer,
html.dark .tweet-footer {
    margin: 3px 0 8px;
}
.margin-r--13,
html.dark .margin-r--13 {
