}
.tweet-footer .icon:before, 
.Icon:before,
html.dark .tweet-footer .icon:before ,
html.dark .Icon:before {
    font-family: "tweetdeckregular";
    font-weight: bold;
    text-shadow: -1px 0px #657786;
    font-style: normal;
    -webkit-font-smoothing: none;
}
/*******************************************************
MEDIA
*******************************************************/
.js-media,
html.dark .js-media {
    border: none!important;
    padding: 0;
    margin-bottom: 0;
}
.media-size-medium,
html.dark .media-size-medium {
    border-radius: 0px;
    height: 120px;
}
.media-size-large,
html.dark .media-size-large {
    height: 270px!important;
    margin: 0px 0 4px 4px!important;
    width: auto!important;
}
.media-item-gif,
html.dark .media-item-gif {
