css:
@-moz-document domain('4chan.org') {
body {
    color: #333;
}
body {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}
@media (min-width: 768px) {
    body {
        width: 750px;
    }
}
@media (min-width: 992px) {
    body {
        width: 970px;
    }
}
@media (min-width: 1200px) {
    body {
        width: 1170px;
    }
}
.navLinks {
    padding-left: 0;
    margin-bottom: 0;
    list-style: none;
    visibility: hidden;
    display: block;
    height: 43px;
    padding-top: 15px;
}
.navLinks > a,
.navLinks > .thread-stats,
.navLinks > label {
    visibility: visible;
}
.navLinks > a {
    width: 100px;
}
.navLinks > a {
    display: inline;
    position: relative;
    top: -0px;
    min-width: 80px;
    min-height: 30px;
    padding: 10px 25px 10px 25px;
    cursor: pointer;
    font-weight: bold;
    color: #FFF;
    border-radius: 5px;
    border-style: solid;
    border-width: 0 0 6px 0;
    background-color: #5eb2e5;
    border-color: #5399c3;
    text-decoration: none;
    color: #fff !important;
}
.navLinks > a:hover {
    top: 2px;
    border-width: 0 0 4px 0;
}
.navLinks > a:active {
    top: 8px;
    padding-bottom: 8px;
    border-width: 2px 0 0 0;
}
.navLinks > .thread-stats {
    padding: 8px 15px;
    list-style: none;
    background-color: #f5f5f5;
    border-radius: 4px;
}
#togglePostFormLink {
    visibility: hidden;
    height: 60px;
}
#togglePostFormLink > a {
    display: inline-block;
    visibility: visible;
    position: relative;
    min-width: 80px;
    min-height: 30px;
    padding: 10px 25px 10px 25px;
    cursor: pointer;
    font-weight: bold;
    color: #FFF;
    border-radius: 5px;
    border-style: solid;
    border-width: 0 0 6px 0;
    background-color: #b1d927;
    border-color: #91b711;
    text-decoration: none;
    color: #fff !important;
}
#togglePostFormLink > a:hover {
    top: 2px;
    border-width: 0 0 4px 0;
}
#togglePostFormLink > a:active {
    top: 8px;
    padding-bottom: 8px;
    border-width: 2px 0 0 0;
}
.thread {
    position: relative;
    margin: 0 -15px 15px;
    border-style: solid;
    margin-right: 0;
    margin-left: 0;
    background-color: #fff;
    border-color: #ddd;
    border-width: 1px;
    border-radius: 4px;
}
div.post div.file {
    margin-bottom: 10px;
}
div.post div.file .fileText {
    padding: 8px 15px;
    list-style: none;
    background-color: #f5f5f5;
    border-radius: 4px;
    display: inline-block;
}
div.post div.file .fileThumb {
    display: inline-block;
    margin: 0px 15px 15px 0px;
    padding: 4px;
    line-height: 1.42857143;
    background-color: #fff;
    border: 1px solid #ddd;
    border-radius: 4px;
    -webkit-transition: border .2s ease-in-out;
    -o-transition: border .2s ease-in-out;
    transition: border .2s ease-in-out;
}
div.post blockquote.postMessage {
    font-size: inherit;
    border: none;
    padding: 0;
}
div.post div.postInfo {
    display: inline-block;
    height: 28px;
}
span.summary {
    display: inline-block;
    margin: 12px;
}
div.opContainer {
    min-height: 30px;
    width: 98% !important;
    border-bottom: 3px solid #DDD !important;
    float: left;
    padding: 15px 1% 0 1%;
}
div.post-hidden {
    border: 0;
}
div.post-hidden div.opContainer {
    border: 0 !important;
}
div.replyContainer {
    padding-top: 5px;
    width: 100%;
    background-color: #fff;
    border-bottom: 1px solid #DDD;
    display: table;
}
div.replyContainer:nth-child(even) {
    background-color: #f5f5f5;
}
div.replyContainer div.reply {
    background-color: transparent;
    border: none;
    display: table-cell;
}
div.replyContainer blockquote.postMessage {
    margin: 15px 0px;
}
.dateTime {
    font-style: italic;
    color: #666;
}
div.sideArrows {
    color: #999;
    display: table-cell;
    float: none;
    text-align: center;
    width: 50px;
}
#quote-preview {
    border-style: solid;
    margin-left: 0;
    background-color: #EAEAEA;
    border-color: #ddd !important;
    border-width: 1px !important;
    border-radius: 4px;
    padding: 10px 10px 0 10px;
}
.bottomCtrl {
    padding: 8px 15px;
    list-style: none;
    background-color: #f5f5f5;
    border-radius: 4px;
    display: inline-block;
}
.bottomCtrl .deleteform {
    display: inline-block;
    padding-right: 12px;
    border-right: 1px solid #ddd;
    margin-right: 5px;
}
input[value="Delete"],
input[value="delete"] {
    padding: 5px 10px;
    cursor: pointer;
    font-weight: bold;
    border-radius: 2px;
    border-style: solid;
    border-width: 0;
    background-color: #da5440;
    color: #fff;
}
input[value="Delete"]:hover,
input[value="delete"]:hover {
    background-color: #bb3f2d;
}
.quote {
    padding: 8px 12px;
    border-left: 5px solid #eee;
    display: table;
    margin: 3px 0;
    background-color: #fff;
}
}
