css:
@-moz-document url-prefix("http://ugx-mods.com/forum") {
div#wrapper {
	width: 64%;
    min-width: 1024px;
}

.navigate_section {
	padding: .5em 1em .5em;
    background: #363636;
}

#toolbar {
	height: 32px;
    border-top: 0;
}

#nav {
	height: 32px;
}

#nav li a {
	height: 32px;
	line-height: 32px;
}

#mainarea {
	box-shadow: 0 0 0;
    border-bottom: 0;
    padding: 0;
}

#nav li a.current {
	background: #8E0808;
}

#nav li a:hover {
	background: #8E0808;
}

.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th {
	background: #8E0808;
    -webkit-box-shadow: 0 0 0;
    -moz-border-radius: 0;
    box-shadow: 0 0 0;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}

#footer {
	background: #282828;
}

h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
	margin-bottom: 6px;
}

div.title_barIC {
	border-bottom: 0;
}

table.table_list tbody.header td div.cat_bar {
	margin-bottom: 0;
}

body, td, th, tr {
	padding: 0;
}

table {
	border-spacing: 0;
}

h3 {
	-webkit-margin-before: 0px;
	-webkit-margin-after: 0px;
	-webkit-margin-start: 0px;
	-webkit-margin-end: 0px;
    -moz-margin-before: 0px;
    -moz-margin-after: 0px;
    -moz-margin-start: 0px;
    -moz-margin-end: 0px;
}

table#sp_main {
	border-spacing: 0;
}

.windowbg {
    background-color: #282828;
	box-shadow: 0 0 0 0 !important;
}

#userinfo {
    /*
	margin: 18px 10px;
    -webkit-border-radius: 0;
    border-radius: 0;
    border: 0;
    padding: 10px;
    */
    visibility: hidden;
}

.poster h4 {
	border-bottom: 0;
    background: 0;
}

.poster ul li {
	border-bottom: 0;
    background: 0;
}

#upshrinkHeaderIC p {
	padding: 1em 2em 1em;
}

#upshrinkHeaderIC p.inline {
	padding: 1em 2em 0;
    display: block;
}

#upshrinkHeaderIC p.last {
	padding: 1em 2em 0;
    border-top: 0;
}

#upshrinkHeaderIC p.stats {
	font-size: 1em;
}

h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg, .table_list tbody.header td {
	line-height: 36px;
}

h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
	padding: 0 12px 0;
}

.table_grid th.first_th {
	background: #8E0808;
}

.table_grid th.last_th {
	background: #8E0808;
}

.topic_table table thead {
	border-bottom: 0;
}

.pagelinks {
	padding: .6em 1em .4em;
}

table.table_grid td {
	border-bottom: 0;
}

.windowbg, #preview_body {
	-webkit-box-shadow: 0 0 0;
	-moz-box-shadow: 0 0 0;
	box-shadow: 0 0 0 !important;
	border: 0;
	margin: 0;
	border-bottom-left-radius: 0;
	border-bottom-right-radius: 0;
	-webkit-border-bottom-left-radius: 0;
	-webkit-border-bottom-right-radius: 0;
	-moz-border-bottom-left-radius: 0;
	-moz-border-bottom-right-radius: 0;
}

.poster {
	/*margin-left: 0;*/
    /*margin-top: 10px;*/
    margin-right: 0;
    border-radius: 16px 0 16px 0;
    width: 200px;
    background: #101010;
    padding: 1em 0 1em;
}

#forumposts h3.catbg {
	margin-bottom: 0;
}

.windowbg span.topslice span {
	height: 0;
}

p#whoisviewing {
	background: #363636;
    padding: 1em;
    margin-bottom: 12px;
}

.postarea div.flow_hidden {
	/*padding-top: .6em;*/
	padding: .6em 1em .6em;
}

.signature, .attachments {
	width: 96%;
}

.windowbg span.botslice span {
	/*height: 0;*/
    height: 12px;
}

.windowbg2 span.topslice span {
	height: 0;
}

.post {
	margin-left: 45px;
}

#forumposts span.botslice {
	/*border-bottom: 0;*/
    margin-bottom: 12px;
}

#forumposts .signature, .post .signature {
	margin-left: 45px;
}

.signature, .attachments {
	width: 94%;
}

.combobox {
    /*width: inherit;*/
	width: 1024px;
    /*padding: 1em 4.5em 1em;*/
    margin: 12px auto;
}

.postarea div.flow_hidden {
	border-bottom: 0;
}

.pagesection {
	padding: 0 1em .2em;
}

#admin_menu {
	border-left: 1em;
    padding: 1em .6em .2em;
}

img.avatar {
    display: block;
    width: 152px;
    height: 152px;
	border-radius: 76px;
	margin: 0 auto 0;
}

.poster li.avatar {
	margin: 0 auto 0;
}

#header ul {
	float: right !important;
	display: block;
    text-align: right;
}

.fb_iframe_widget {
	position: absolute !important;
    margin-left: 120px;
    margin-top: 50px;
}

#___ytsubscribe_0 {
	position: absolute !important;
    margin-left: 90px !important;
}

.twitter-follow-button {
	position: absolute !important;
    margin-left: -10px;
    margin-top: 26px;
}

.navPages {
	background: #111;
	display: inline-block;
	padding: 5px 7px;
	border-radius: 50%;
}

.navPages:hover {
	background: #8E0808;
}

.buttonlist ul li a {
	background: #111;
    margin-left: 10px;
}

.buttonlist ul li a:hover {
	color: #fff;
}

.buttonlist ul li a span {
	background: #111;
    height: inherit;
    line-height: 24px;
    right: 0;
    padding: 0 8px 0 8px;
}

.buttonlist ul li a span:hover {
	background: #8E0808;
}

.buttonlist ul li a.active {
	background: #111;
}

.buttonlist ul li a.active:hover {
	color: #fff;
}

.buttonlist ul li a.active span {
	background: #111;
    text-transform: none;
}

.buttonlist ul li a.active span:hover {
	background: #8E0808;
}

#display_jump_to {
	padding: .5em 2em .5em;
}

.nextlinks {
	padding: .8em 1.7em .5em;
}

.nextlinks_bottom {
	padding: .8em 1.7em .5em;
}

.description, .plainbox {
	border-top: 0;
}

#bbcBox_message div img {
	background: #cbcbcb !important;
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
	border: 0;
    margin: 0 0 .3em;
    background: #191919;
}

.codeheader, .quoteheader {
	padding: .6em 1.2em .6em;
    background: #363636;
    border-radius: 8px 8px 0 0;
}

.block_code pre code {
	box-shadow: 0 0 0 !important;
	-webkit-box-shadow: 0 0 0 !important;
	-moz-box-shadow: 0 0 0 !important;
    background: #191919 !important;
}

.block_code {
	top: -11px !important;
}

div.sp-wrap {
	border: 0 !important;
    border-radius: 8px !important;
}

div.sp-head {
	padding: .6em 1.2em .6em !important;
}

div.sp-body {
	border-top: 0 !important;
}

#forumposts .modified {
	margin-left: 45px;
    margin-top: 8px;
}

.titlebg, .titlebg2, thead tr.titlebg th, tr.titlebg td, tr.titlebg2 td {
	background: #121212;
    box-shadow: 0 0 0;
    -webkit-box-shadow: 0 0 0;
    -moz-box-shadow: 0 0 0;
    border-radius: 0;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
}

.dropmenu li a.active {
	background: #111 !important;
}

.dropmenu li a.active:hover {
	background: #8E0808 !important;
}

.dropmenu li a.active span.firstlevel {
	background: 0 !important;
}

.dropmenu li a.active span.firstlevel:hover {
	background: 0 !important;
}

.dropmenu li a.firstlevel span.firstlevel {
	left: 0;
}

.dropmenu a span {
	padding: 0 8px 0 8px !important;
}

.dropmenu li li a span {
	padding: 8px !important;
    color: #cbcbcb;
}

.dropmenu li li a span:hover {
    color: #cbcbcb;
}

.dropmenu li li a span.active {
    color: #cbcbcb;
}

.dropmenu li ul {
	background: #363636;
    padding: 0;
    width: inherit;
    border-bottom: 0;
}

.dropmenu li li a:hover,.dropmenu li li:hover>a {
	background: #111;
}

.dropmenu li li {
	border-left: 0;
    border-right: 0;
}

.dropmenu li ul ul {
	margin: -2.3em 0 0 19em;
}

.dropmenu li li.additional_items {
	background: #363636;
}

.signature, .attachments {
	margin-left: 45px;
    width: 93.2%;
}

tbody.header {
	margin-bottom: 6px;
}
}
