css:
@-moz-document domain("appleinsider.com") {
#top-promo, #article-footer-deals, iframe[name^="adblade"], div[class^="adkengage"], div[id^="div-gpt-ad"], div[id^="google_ads"], div[id^="tf_container"], #deals-social, #sidebar-right, #sidebar-left, .google-premium, .meta-row, #taboola-below-article-thumbnails, .rel-half-l-cnt, .rel-half-r-cnt-ad, #todays-headlines, #headline-module, #article-social-head, div[id^="rcjsload"] {
    display:none !important;
    width:0px !important;
    position: absolute !important;
    left:0px !important;
    top:0px !important;
    clear:none !important;
}

#wordmark {
    padding-left:20px;
    display: none !important;
}

/* Top Navigation */
#pn-bg {
    left:0px;
    top:0px;
    position:fixed;
    width: 100%;
    background: #2b2b2c;
}

/* Top Nav Button */
#pn li {
    background: #2b2b2c;
}

/* Header */

#header {
    padding:0px;
}

/* Content Page */
#content-left, .container, #content {
	width: 979px !important;
}

#content-home {
    padding:0px !important;
}


/* Article */
.article, .article-meta {
    width:979px !important;
    display: block;
    border: 0px solid orange;
    clear: none !important;
    padding-left:20px;
}

.article-nav {
    width:979px !important;
    display: block;
    border: 0px solid orange;
    clear: none !important;
}

.article-nav p {
    width:420px !important;
}

#next-article {
    width:450px !important;
    padding-left:10px;
}

#prev-article {
    width:450px !important;
    border-right: 1px solid #ebebeb;
    padding-right: 10px;
}

#prev-article:hover, #next-article:hover {
    background-color:#f7fafc;
}


/* Comment Section*/
#cmt-existing, #cmt-add {
    background-color: #2b2b2c !important;
    color: #d9d9d9 !important;
    padding-bottom: 30px;
    padding-left: 25px;
    padding-right: 25px;
    padding-top: 10px;
    width: 100%;
    display: block;
    border: 0px solid orange;
    position:relative;
    left:-11px;
}

#cmt-existing h1, #cmt-add h1 {
    color: #dadada;
}


#cmt-existing a {
    color: #58abff !important;
}

.quote-container {
    background: #3b3d40 !important;
    color: white !important;
    border: 1px solid #494b4f;
}

.comment {
    border-top: 1px solid #494b4f;
}
}
