css:
@-moz-document domain("clubzap.org"), domain("cz-podzone.com") {
td.embedded > a > b
{
 	font-weight: normal!important; 
    font-size: 15px;
}

.header img
{
 	display: none;   
}

img
{
    max-width:100%;
}

table
{
    border-collapse: collapse;
    border: none;
    background-color: #E6F5FF;
}

body, .outer, table.main, table.mainouter
{
    background-color:rgb(213,231,247)
}

.outer > table
{
    border-bottom:0px solid rgb(0,0,0) !important;
}

td.colhead
{
    font-weight: bold;
    color: #000;
    text-align: center;
    background-color: rgb(0,204,255);
}

.embedded > table, .embedded > table .main
{
    border: 1px solid gray !important;   
}

.sub table a, .comment a
{
 	text-decoration: underline;   
}

a:hover
{
    color: rgb(255,0,0);
 	text-decoration: underline;   
}
}
