css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://getpocket.com/a/read") {
  /* Common */
  .page-readerdark .reading_progress_bar,
  .page-readersepia .reading_progress_bar {
    background-color: #2aa198; /* cyan */
  }
  code, pre {
    font-size: 19px;
    font-weight: normal;
  }

  /* Solarized Light */
  .page-readersepia code,
  .page-readersepia pre {
    background-color: #eee8d5; /* base2 */
    color: #657b83; /* base00 */
  }
  .page-readersepia .noarticle_warning {
    background: none repeat scroll 0 0 #eddfd5;
  }
  .page-readersepia #page .pkt-nav {
    background-color: #fdf6e3; /* base3 */
  }
  .page-readersepia .toolbar_reader_title {
    color: #b58900; /* yellow */
  }
  .page-readersepia .toolbar_reader_divider {
    border-bottom-color: #586e75; /* base01 */
  }
  @media only screen and (min-width: 65em) {
    .page-readersepia .pkt-nav {
      border-bottom-color: #eee8d5; /* base2 */
    }
  }
  .page-readersepia {
    background: none repeat scroll 0 0 #fdf6e3; /* base3 */
  }
  .page-readersepia .reader_head {
    border-bottom-color: #eee8d5; /* base2 / brighter */
  }
  .page-readersepia .sub li,
  .page-readersepia .sub li a {
    color: #999491;
  }
  .page-readersepia .reader_head .tags .tag,
  .page-readersepia .reader_head .tags .tag-overflow-link {
    background-color: #b58900; /* yellow */
    color: #fdf6e3; /* base3 */
  }
  .page-readersepia .reader_head .tags .tag:hover {
    background-color: #586e75; /* base01 */
  }
  .page-readersepia .reader_content {
    color: #657b83; /* base00 */
  }
  .page-readersepia .reader_content h1,
  .page-readersepia .reader_content h2,
  .page-readersepia .reader_content h3,
  .page-readersepia .reader_content h4,
  .page-readersepia .reader_content h5,
  .page-readersepia .reader_content h6 {
    color: #b58900; /* yellow */
  }
  .page-readersepia .reader_content .sub a,
  .page-readersepia .reader_content .sub li {
    color: #b58900; /* yellow */
  }
  .page-readersepia .text_body a {
    color: #b58900; /* yellow */
  }
  .page-readersepia .text_body blockquote,
  .page-readersepia .text_body blockquote p {
    border-color: #657b83;/* base00 */
    color: #657b83; /* base00 */
  }
  .page-readersepia .text_body hr {
    border-color: #eee8d5; /* base2 / brighter */
  }
  .page-readersepia .RIL_IMG caption,
  .page-readersepia a .RIL_IMG caption {
    color: #b58900 !important; /* yellow */
  }
  *::-moz-selection {
    background: none repeat scroll 0 0 #d33682;
    color: #fff;
    text-shadow: none;
  }

  /* Solarized Dark */
  .page-readerdark code,
  .page-readerdark pre {
    background-color: #073642; /* base02 */
    color: #839496; /* base0 */
  }
  .page-readerdark .noarticle_warning {
    background: none repeat scroll 0 0 #eddfd5;
  }
  .page-readerdark #page .pkt-nav {
    background-color: #002b36; /* base03 */
  }
  .page-readerdark .toolbar_reader_title {
    color: #b58900; /* yellow */
  }
  .page-readerdark .toolbar_reader_divider {
    border-bottom-color: #586e75; /* base01 */
  }
  @media only screen and (min-width: 65em) {
    .page-readerdark .pkt-nav {
      border-bottom-color: #073642; /* base02 */
    }
  }
  .page-readerdark {
    background: none repeat scroll 0 0 #002b36; /* base03 */
  }
  .page-readerdark .reader_head {
    border-bottom-color: #073642; /* base02 / brighter */
  }
  .page-readerdark .sub li,
  .page-readerdark .sub li a {
    color: #999491;
  }
  .page-readerdark .reader_head .tags .tag,
  .page-readerdark .reader_head .tags .tag-overflow-link {
    background-color: #b58900; /* yellow */
    color: #002b36; /* base03 */
  }
  .page-readerdark .reader_head .tags .tag:hover {
    background-color: #586e75; /* base01 */
  }
  .page-readerdark .reader_content {
    color: #839496; /* base0 */
  }
  .page-readerdark .reader_content h1,
  .page-readerdark .reader_content h2,
  .page-readerdark .reader_content h3,
  .page-readerdark .reader_content h4,
  .page-readerdark .reader_content h5,
  .page-readerdark .reader_content h6 {
    color: #b58900; /* yellow */
  }
  .page-readerdark .reader_content .sub a,
  .page-readerdark .reader_content .sub li {
    color: #b58900; /* yellow */
  }
  .page-readerdark .text_body a {
    color: #b58900; /* yellow */
  }
  .page-readerdark .text_body blockquote,
  .page-readerdark .text_body blockquote p {
    border-color: #839496;/* base0 */
    color: #839496; /* base0 */
  }
  .page-readerdark .text_body hr {
    border-color: #073642; /* base02 / brighter */
  }
  .page-readerdark .RIL_IMG caption,
  .page-readerdark a .RIL_IMG caption {
    color: #b58900 !important; /* yellow */
  }
  *::-moz-selection {
    background: none repeat scroll 0 0 #d33682;
    color: #fff;
    text-shadow: none;
  }
}
