css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("say-move.org") {
  /* 文字設定 */
  html,body,div,h1,.h3,h4,h5,h6,p,blockquote,pre,table,th,tr,td,ul,ol,li,dl,dt,dd,form,fieldset,legend,hr,span,a,img,strong,em,
  ins,del,cite,abbr,acronym,sup,sub,dfn,code,var,samp,kbd,b,i{
    font-weight:800;
    font-family:'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', メイリオ, Meiryo, 'ＭＳ Ｐゴシック', sans-serif;
    text-decoration: none;
    color: #ccc !important; 
    border: inset 0px #222742 !important;
  }
  #ad04,#ad02,#ad03,#ad01,#my_header img, #sh_fc2footer_fix,.cmn_thumb_res,#my_navigation,#watch_actions,#dheader img,.origin_site{
    display:none !important;
  }
  #group_link,button,.linkpage{
    background:#56010e !important;	
    color: #ccc !important; 
  }
  .linkpage:hover,.linkpageactive{
    background:#9e0118 !important;	
     	
  }
  body, .current,.mov_info_right_adt{
    background:#171717 !important;	    
  }
  #my_contents,#disp table, #disp tbody, .mov_info_right_gen, .wrap_comesearch table{
    background:#232323 !important;
    border:0px !important;
  }
  input{
    background:#444 !important;
    border:0px !important;
    color: #ccc !important; 
  }
  #my_header a.switch_adt_ja img{
    background:#56010e !important;	
    display:block !important;
  }
  #my_header a.switch_adt_ja{
    content:"アダルト" !important;
    color: #ccc !important; 
  }
  select{
    background:#000 !important;
    border:0px !important;
    color: #ccc !important;     
  }
}
