css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("https://www.youtube.com/feed/subscriptions"), url("http://www.youtube.com/feed/subscriptions"),
url("https://www.youtube.com/feed/subscriptions/"), url("http://www.youtube.com/feed/subscriptions/"){
 
               /*Grid Subscriptions Start*/
             .feed-item-container .branded-page-module-title, .feed-item-container .yt-lockup-description {
                display: none !important;
                height: 0 !important;
            }
  
              #browse-items-primary {
                font-size: 0;
                margin-right: -15px;
                padding: 15px;
                padding-right: 0;
            }
  
              #browse-items-primary .section-list > li {
                display: inline-block;
                margin-bottom: 20px;
                margin-right: 10px;
                width: 196px;
                word-wrap: break-word;
            }
  
              #browse-items-primary .expanded-shelf-content-item{
                margin-bottom: initial;
                margin-right: initial;
            }
  
              #browse-items-primary .item-section .feed-item-container {
                border: initial;
                padding: initial;
            }
  
              #browse-items-primary .item-section .feed-item-container .menu-container{
                opacity: 0;
            }
  
              #browse-items-primary .item-section .feed-item-container:hover .menu-container{
                opacity: 1;
            }
  
              #browse-items-primary .item-section .feed-item-container .menu-container{
                top: 110px;
                right: -8px;
                z-index: 1;
            }
  
              #browse-items-primary .yt-lockup-thumbnail {
                float: initial !important;
            }
  
              #browse-items-primary .yt-lockup-meta, div#browse-items-primary .yt-lockup-byline {
                font-size: 11px;
                max-width: 196px;
            }
  
              #browse-items-primary .yt-lockup-title, div#browse-items-primary .feed-item-dismissal {
                font-size: 13px;
                margin-top: 4px;
                margin-bottom: 1px;
                max-width: 176px;
            }
           
              .yt-lockup-meta-info > li {
                display: inline;
            }
  
              .yt-lockup-meta-info, .feed-item-container .yt-ui-ellipsis, .yt-lockup-content div{
                overflow: hidden;
                text-overflow: ellipsis;
                white-space: nowrap;                     
            }
			
			  div.branded-page-v2-secondary-col {display: none!important;}
   
              .yt-ui-ellipsis:before {
                content: "";}

              .yt-ui-ellipsis:after {
                content: unset;}
    
              .yt-ui-ellipsis-2 {max-height: 2.5em!important; height: 60px!important;}
  
              .yt-ui-ellipsis {
                line-height: 1.2em; white-space: normal!important;}
  
              .expanded-shelf {margin-bottom: 8px!important}

.yt-lockup-tile .yt-lockup-byline .yt-uix-sessionlink {white-space: nowrap!important;}
			  
           /*Grid Subscriptions End*/
  
}
