css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.telegram.org") {

	body{
		background: black;
	}
	.im_page_wrap {
		background: #fff none repeat scroll 0% 0%;
		box-shadow: none;
		border-radius: 0 0 0 0;
		border-left: none;
		border-right: none;
		border-bottom: none;
	}

	.im_history_message_wrap{
		padding: 0px;
	}
	.im_dialog_message {
		display: none;
	}
	.im_dialog_photo {
		display: none;
	}
	.im_message_wrap {
		/**/border-bottom: #191919 1px solid;/**/
		padding: 0px;
		margin: 0px;
		margin-left: 20px;
	}
	.im_content_message_wrap {
		margin: 8px 10px 8px 16px;
		width: 100%;
	}

	.icon-message-status-tick {
		opacity: .7;
	}

	.footer_empty {
		display: none;
	}
	.footer_wrap {
		display: none;
	}

	.im_message_unread_split {
		background: #333;
	}

	.im_message_author::before {
		content: "<";
	}

	.im_message_author::after {
		content: ">";
	}
	.im_message_author{
		display: inline-block;
		font-weight: 100;
		/**/font-size: 14px !important;/**/
		vertical-align: top;
	}

	.im_message_reply_wrap {
		/**display: inline-block;/**/
	}

	.im_edit_selected_actions {
		text-align: center;
		text-transform: lowercase;
	}

	.im_page_wrap, .im_message_focus .im_message_outer_wrap{
		background: #101010;
		color: #bbb;
	}
	.im_dialogs_scrollable_wrap a.im_dialog:hover {
		background: #212121;
	}
	.im_dialog_peer {
		color: #666;
		font-weight: 700;
		margin-top: 0px
	}

	.im_history_selectable .im_message_outer_wrap:hover, .im_message_selected .im_message_outer_wrap, .im_message_focus .im_message_outer_wrap {
		background: #222;
	}

	.im_message_contact_photo, .im_message_from_photo, .im_message_fwd_photo {
		opacity: 0.5;
		display: none;
	}
	.im_message_from_photo:hover {
		opacity: 1;
	}

	.im_grouped_short .im_message_body, .im_grouped .im_message_body, .im_grouped_fwd .im_message_body, .im_grouped_fwd_short .im_message_body {
		margin-left: 0px;
	}
	.im_content_message_select_area {
		display: inline-block !important;
		margin-right: 16px !important;
		padding-right: 16px !important;
	}
	.icon-select-tick {
		margin: 16px 0px 0px 40px !important;
	}

	/**.im_message_contact_photo, .im_message_from_photo, .im_message_fwd_photo {
	width: 42px;
	height: 42px;
	border-radius: 50%;
	overflow: hidden;
}
	.pull-left {
	float: left !important;
}/**/


	/**.im_message_meta {
	float: left;
}
	.pull-right {
	float: left !important;
}
	.text-right {
	text-align: left;
}/**/
	.im_content_message_select_area {

	}
	/*.im_message_in .pull-right{
	float: left !important;
	text-align: right;
	margin-right: 10px;
	margin-left: -20px;
}
	.im_message_in .pull-right::after{
	content: "";
}*/

	.im_message_date, .im_message_fwd_date {
		/*padding: 0px 0px 0px 0px;*/
		opacity: 0.2;
		display: none !important;
	}
	.im_message_date:hover, .im_message_fwd_date:hover {
		opacity: 0.7;
	}

	.im_message_text {
		display: inline-block;
	}


	.im_dialog_meta{
		display: inline-block !important;
		float: left !important;
		margin-top: 0px;
	}
	.im_dialog_badge, .im_dialog_date {
		display: inline-block;
		margin-right: 10px;
		margin-top: 0px;
	}
	.im_dialogs_col_wrap {
    border-right: 2px solid #111;
}
	
	.im_history_col .nano > .nano-pane{
    background: rgba(255, 255, 255, 0.04) none repeat scroll 0% 0%;
    transition: none 0s ease 0s;
}
	.icon-message-status {
    margin-top: 6px;
}

}
