css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("courses.edx.org") {
/* Headers */
  nav.courseware,
  .global,
/* footers */
.edx-footer, .edx-footer-new,
.container-footer,.course-license,
#footer-edx-v3,
/* ui */
.help-tab, 
.sequence-bottom
  {display:none;}
  
  .container > div {
    border-width: 0px;
  }
  .container {
    max-width: 95%;
  }
  .course-index {
    border-right: 1px dashed #C8C8C8;
    border-radius: 0px 0px 0px 0px;
  }
  
  h2 {
    color: #646464;
    font-family: Raleway;
    letter-spacing: 1px;
    margin-bottom: 15px;
    text-transform: uppercase;
  }
  h1, h2, h3, h4, h5, h6 {
      color: #3C3C3C;
      font: 1.2em/1.2em "Georgia";
      margin: 0px;
  }
  
  
  
  /* Nav */
  .xmodule_display.xmodule_SequenceModule .sequence-nav-button.button-previous {
    border-radius: 0 0px 0px 82px;
    left: 0px;
   }
  .xmodule_display.xmodule_SequenceModule .sequence-nav-button.button-next {
    border-radius: 0px 0px 35px 0px;
    right: 0px;
  }
  
  .light-button, div.history-controls input[type="submit"], a.light-button, .gray-button, input[type="reset"], input[type="submit"], input[type="button"], button, .button, form input[type="reset"], form input[type="submit"], form input[type="button"], form button, form .button, .xmodule_display.xmodule_LTIModule div.lti .wrapper-lti-link .lti-link .link_lti_new_window {
    border: 0px solid #CFC6C6;
    border-radius: 0px;
    box-shadow: 0 0 0 0 #FFF inset;
    color: #333;
    display: inline-block;
    font-weight: bold;
    background-color:  #DDD;
    background-image: none;
    padding: 7px 18px;
    text-decoration: none;
    text-shadow: 0px 1px 0px #F9F8F8;
    background-clip: padding-box;
    font-size: 0.8125em;
  }
  .light-button:hover:not(:disabled), div.history-controls input[type="submit"]:hover:not(:disabled), a.light-button:hover:not(:disabled), div.info-wrapper section.handouts ol li div.hitarea:hover + h4.light-button:not(:disabled), div.info-wrapper section.handouts ol li div.hitarea:focus + h4.light-button:not(:disabled), .gray-button:hover:not(:disabled), input[type="reset"]:hover:not(:disabled), input[type="submit"]:hover:not(:disabled), input[type="button"]:hover:not(:disabled), button:hover:not(:disabled), .button:hover:not(:disabled), .xmodule_display.xmodule_LTIModule div.lti .wrapper-lti-link .lti-link .link_lti_new_window:hover:not(:disabled) {
    box-shadow: 0px 0 0px 0px #FBFBFB inset;
    cursor: pointer;
    background-color: #DDD;
    background-image: none;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav ol li a.active {
    background-color: #EEE;
    height: 44px;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav {
    margin: -40px 0px 30px;
    position: relative;
    border-bottom: medium none;
  }
  .xmodule_display.xmodule_SequenceModule .sequence-nav .sequence-list-wrapper {
    background-color: #DDD;
    background-image: none;
    position: relative;
    border: 0px solid #C8C8C8;
    height: 46px;
    margin: 0px 40px;
    box-shadow: 0px 0px 0px 0px rgba(0, 0, 0, 0) inset;
  }
  .xmodule_display.xmodule_SequenceModule .topbar a.block-link, .xmodule_display.xmodule_SequenceModule .sequence-nav a.block-link, .xmodule_display.xmodule_CourseModule .sequence-nav ol .xmodule_display.xmodule_SequenceModule .topbar a, .xmodule_display.xmodule_SequenceModule .sequence-nav ol a, .xmodule_display.xmodule_SequenceModule .topbar div.book-wrapper .book nav a, div.book-wrapper .book nav .xmodule_display.xmodule_SequenceModule .topbar a, .xmodule_display.xmodule_SequenceModule .sequence-nav div.book-wrapper .book nav a, div.book-wrapper .book nav .xmodule_display.xmodule_SequenceModule .sequence-nav a {
    border-left: 0px solid #E2E2E2;
    display: block;
}
.xmodule_display.xmodule_SequenceModule .sequence-nav ol li a:hover, .xmodule_display.xmodule_SequenceModule .sequence-nav div.info-wrapper section.handouts ol li div.hitarea:hover + h4, div.info-wrapper section.handouts .xmodule_display.xmodule_SequenceModule .sequence-nav ol li div.hitarea:hover + h4, .xmodule_display.xmodule_SequenceModule .sequence-nav div.info-wrapper section.handouts ol li div.hitarea:focus + h4, div.info-wrapper section.handouts .xmodule_display.xmodule_SequenceModule .sequence-nav ol li div.hitarea:focus + h4, .xmodule_display.xmodule_SequenceModule .sequence-nav ol li a:focus {
    background-color: #EEE;
    background-repeat: no-repeat;
    height: 44px;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav ol li a.active:hover, .xmodule_display.xmodule_SequenceModule .sequence-nav div.info-wrapper section.handouts ol li div.hitarea:hover + h4.active, div.info-wrapper section.handouts .xmodule_display.xmodule_SequenceModule .sequence-nav ol li div.hitarea:hover + h4.active, .xmodule_display.xmodule_SequenceModule .sequence-nav div.info-wrapper section.handouts ol li div.hitarea:focus + h4.active, div.info-wrapper section.handouts .xmodule_display.xmodule_SequenceModule .sequence-nav ol li div.hitarea:focus + h4.active, .xmodule_display.xmodule_SequenceModule .sequence-nav ol li a.active:focus {
    background-color: #EEE;
    background-repeat: no-repeat;
    background-position: center 14px;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav ol {
    position: absolute;
    top: 0px;
    left: 0px;
    box-sizing: border-box;
    display: table;
    height: 100%;
    margin: 0px;
    padding: 0px 0px;
    width: 100%;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav .right-shadow {
    background-image: none;
    position: absolute;
    top: 0px;
    right: 0px;
    width: 0px;
    height: 46px;
    background-color: transparent;
    pointer-events: none;
}
  .xmodule_display.xmodule_SequenceModule .sequence-nav .left-shadow {
    background-image: none;
    position: absolute;
    top: 0px;
    right: 0px;
    width: 0px;
    height: 46px;
    background-color: transparent;
    pointer-events: none;
}
.course-index div#accordion .chapter {
    width: 100% !important;
    box-sizing: border-box;
    padding: 11px 14px;
    background-image: none;
    box-shadow: 0px 0px 0px #AA9F86 inset;
    transition: background-color 0.1s linear 0s;
    border-bottom: 1px dotted #AA9F86;
    background-color: rgba(32, 161, 218, 0.13);
}
  .sidebar, .course-index, div#wiki_panel, div.info-wrapper section.handouts, div.book-wrapper section.book-sidebar, .profile-wrapper .user-info {
    box-sizing: border-box;
    display: table-cell;
    font-family: "Open Sans",Verdana,Geneva,sans-serif,sans-serif;
    font-size: 14px;
    position: relative;
    vertical-align: top;
    width: 23.7288%;
    background: #F2F2F2 none repeat scroll 0% 0%;
}
  .container > div {
    display: table;
    table-layout: fixed;
    width: 100%;
    border-radius: 0px;
    border: 0px solid #AAA;
    background: #FFF none repeat scroll 0% 0%;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.05);
}
}
