css:
@-moz-document url-prefix("https://www.touchdevelop.com/app") {
/*DarkDevelop - a dark theme for TouchDevelop
  by denexapp
  denis.mukhametov@gmail.com
*/

/*root*/
body {
	color: #d4d4d4;
    background-color: #1e1e1e;
}

/*icons in hub*/
.svg-icon-bell path, .svg-icon-Award path, .sdReportAbuse path {
    fill: #d4d4d4;
}

/*scrollbar in chrome/opera/yandex*/
::-webkit-scrollbar-thumb {
    background-color: #3e3e3e;
}


#wallOverlay ::-webkit-scrollbar-thumb {
    background-color: #ddd;
}

/*top left button round*/
.code-button-frame, .slBackContainer .topMenu-button-frame, #leftBtnRow .topMenu-button-frame, .sideTabScroll .topMenu-button-frame, .apiBackContainer .topMenu-button-frame {
    border: 2px solid #c5c5c5;
    background-color: inherit/*rgba(255, 255, 255, 0.1)*/;
}

/*top left button svg icon*/
.slBackContainer .topMenu-button-frame path, #leftBtnRow .topMenu-button-frame path, .sideTabScroll .topMenu-button-frame path, .sideTabScroll .navRound path, .apiBackContainer .topMenu-button-frame path {
    fill: #c5c5c5;
}

/*top left button text*/
.slBackContainer .topMenu-button, .code-button, #leftBtnRow .topMenu-button, .sideTabScroll .topMenu-button, .apiBackContainer .topMenu-button {
    color: #c5c5c5;
}

/*search box*/
.wall-textbox, textarea {
    border: 3px solid #3c3c3c;
    background: #3c3c3c;
    color: #cccccc;
}

/*hub/editor left pane*/
.slLeft, #editorContainer {
    background-color: #252525;
}


/*editor right pane*/
#leftPane {
  width: 70%;
}

/*editor right pane*/
#leftPaneContent {
    background-color: #1e1e1e !important;
}

/*editor container with right pane*/
.decl {
  padding: 1em 0em 1em 1em;
}

/*hub big bg logo*/
.hubBgLogo .svg-icon-touchDevelop path {
    fill: #000;
}

/*.hub big bg logo container*/
.hubBgLogo {
	opacity: 0.34;
}

/*active tab in hub*/
.sdTabLabel[data-selected^="yes"] {
    color: #ffffff;
}

/*tab in hub*/
.sdTabLabel {
    color: #adadad;
}

/*item in left pane*/
.navItem {
    color: #d4d4d4;
}

/*item in left pane small text*/
.navItem .navDescription {
    color: #999999;
}

/*editor left bar with buttons*/
#leftBtnRow {
    background-color: #333333;
    border-right: none !important;
}

/*cancel search/script hamburger button*/
.editorSearchContainer .svg-icon-cancel path, .editorSearchContainer .svg-icon-script path {
	fill: #e8e8e8;
}

/*editor left side buttons*/
.navItem-button {
    background: none;
}

/*links*/
a, .link-button, .sdExpandButton, .calcApiHelpMore, .float-help-link, .sdCmtScriptName {
    color: #007acc;
}

.sdExpandButton {
    text-decoration: none;
    
}

.sdCmtScriptName {
    border: inherit;
}

a:hover, .link-button:hover, .sdExpandButton:hover, .calcApiHelpMore:hover, .float-help-link:hover, .sdCmtScriptName:hover {
    color: #ffffff !important;
}

a:visited {
    color: #9900cc;
}

/*script/doc author bar*/
.sdScriptAuthor {
    background-color: #2d2d30;
}

/*doc heart*/
.sdDocsBtn path {
    fill: #adadad;
}

/*doc liked heart*/
.sdDocsBtn .svg-icon-wholeheart[aria-label^="wholeheart,#EAC117"] path {
    fill: #d95e40;
}

/*Microsoft Research logo*/
.hubLogo path:first-child {
    fill: #d4d4d4;
}

/*beta note*/
.beta-note {
    background: rgba(45, 45, 48, 0.75);
    color: #d4d4d4;
}

/*common controls*/
.wall-button, .menuItem, .theBox {
    border: 2px solid #d4d4d4 !important;
    color: #d4d4d4 !important;
    background: inherit !important;
}

/*radio button*/
.radio-button {
    color: #d4d4d4 !important;
    background: inherit !important;
}

/*radio button border*/
.radio-outer {
    border: 0.05em solid #d4d4d4;
}

/*radio button round*/
.radio-inner {
    background: #d4d4d4;
}

/*script description*/
.sdExpandableText {
	color: #a9a9a9;
}

/*dialogue*/
.modalDialogInner {
    background: #2d2d30 !important;
}

/*td settings*/
.modalDialog {
    color: #d4d4d4 !important;
}

/*tab tile count*/
.sdTabTile {
    color: #e8e8e8 !important;
}

/*tab tile svg*/
.sdTabTile path {
    fill: #e8e8e8;
}

/*user icon*/
.sdIcon circle[fill^="white"] {
    fill: none;
}

/*user insights key*/
.sdKey {
    background-color: inherit;
}

/*screenshot view background*/
.sdScreenShotLabel {
    background-color: #2d2d30;
}

/*screenshot view buttons*/
.sdScreenShotButtons path {
    fill: #d4d4d4;
}

/*current item in left pane*/
div.sdHeaderOuter[data-current^="yes"], button.navItem[data-current^="yes"] {
  background: #005f9e
}

/*selected items in left pane*/
.sdHeaderOuter[data-selected^="yes"], button.navItem[data-selected^="yes"]{
    border-left-color: #007acc;
}

/*clicked items in left pane*/
.sdHeaderOuter[data-active^="yes"], button.navItem[data-active^="yes"]{
    background-color: #0e639c;
}

/*items was selected in left pane*/
button.navItem[data-was-selected^="yes"] {
    border-color: #cccccc;
}

/*store buttons*/
.store-button {
    color: #d4d4d4;
}

/*code buttons*/
.calcButton {
    background-color: #252525;
}

/*code button background*/
.calcButtonsRight, .calcButtonsTop {
    background-color: #111111;
}

/*code button foreground*/
.calcOp {
    color: #d4d4d4;
}

/*code hint*/
.calcApiHelp {
    background: #333333;
    color: #d4d4d4;
}

/*code's current line*/
.stmt[data-current^="yes"] {
    background: #3f3f46;
}

/*editor side when line is selected*/
.stmtEditorSide {
    background: #252525 !important; 
}

/*editor bottom of side when line is selected*/
.apiDismiss {
    background: #252525;
}

/*between hint and left side*/
.showFromBelow {
    background-color: #333333;
}

/*code line*/
.line[data-active^="yes"] {
    background-color: #0e639c;
}

/*selection*/
.calcToken[data-selected^="yes"] {
    background: #3a628b;
}

/*selection markers*/
.calcCursorMarker path:first-child {
    fill: #50f121;
}

.calcCursorMarker path:nth-child(2) {
    fill: #1e1e1e;
}

/*block editing*/
.stmt[data-selected^="yes"] {
    background: #3a628b;
}

/*modal list*/
.modalList {
    background: inherit;
    color: inherit;
}

/*installed icon*/
.sdInstalled {
  	border-bottom-color: #333333;
  	border-left-color: #333333;
    opacity: 0.8;
}

/*text cursor*/
.calcCursor {
    color: #ffffff !important;
    margin-left: -0.1em !important;
}

/*literal string*/
.stringLiteral {
	color: #ce9178;
}

/*line with cursor*/
.line[data-carret^="yes"] {
    border-left-color: #007acc;
}

/*vertical left lines*/
.stmt[data-nest="0"] {
    border-left-color: #5f88b0;
}

.stmt[data-nest="1"] {
    border-left-color: #70bf53;
}

.stmt[data-nest="2"] {
    border-left-color: #d3963b;
}

.stmt[data-nest="3"] {
    border-left-color: #df80ff;
}

/*keywords*/
.kw {
    color: #569cd6;
}

/*code button bot text*/
.calcButtonHelp {
    color: #70bf53 !important;
}

/*copy code line buttons round*/
.code-button-frame {
    background: #1e1e1e;
}

/*copy code line buttons svg*/
.code-button-frame path {
    fill: #d4d4d4;
}

/*copy code line buttons description*/
.code-button-desc {
    background: #1e1e1e;;
}

/*code button svg icon*/
.calcButton path {
    fill: #d4d4d4;
}

/*code button blue triangle*/
.calcButtonTriangle[style^="border-top-color: blue; border-right-color: blue;"] {
    border-top-color: #317bf7 !important;
    border-right-color: #317bf7 !important;
}

/*script edit buttons*/
.sdBigButton {
    background: #0a7cd5;
}

/*forum script header*/
.sdBaseHeader {
    background: #3c3c3c;
}

/*forum script header*/
.sdBaseCorner {
    background: #3c3c3c;
}

/*forum like/abuse buttons text*/
.sdCmtBtns {
    color: #d4d4d4;
}

/*forum like/abuse buttons svg, data diamond icon*/
.sdCmtBtns path, .navDiamond path {
    fill: #d4d4d4;
}

/*forum liked heart*/
.sdCmtBtns svg[aria-label^="wholeheart,#a00"] path {
    fill: #aa0000;
}


/*translated text*/
.translated {
    background: #3c3c3c;
    border-top: 2px solid orange;
}

/*overlay while edit text*/
#calcOverlay {
    background-color: #252525;
    opacity: 0.3;
}

/* search highlight*/
.highlight {
    background-color: #515c6a;
}

/*grey things*/
.greyed {
    color: #888888;
}
}
