css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wsirc.com") {

/*@cell_bg: #fff-#C0C0C0;*/
body {
  background-color: #000000;
  color: #ffffff;
}
a {
  color: #0000e0;
}
td {
  color: #ffffff;
}
.infoMsg {
  color: #00e000;
}
.noticeMsg {
  color: #990000;
}
.serverMsg {
  color: #ffffff;
}
.errorMsg {
  color: #e00000;
}
.winTab {
  background-color: #000000;
  border-left: solid 1px #7f7f7f;
  border-top: solid 1px #7f7f7f;
  border-right: solid 1px #7f7f7f;
}
.winSelected {
  background-color: #1f1f1f;
  border-left: solid 1px #7f7f7f;
  border-top: solid 1px #7f7f7f;
  border-right: solid 1px #7f7f7f;
}
.messageRow {
  vertical-align: top;
}
.cellTime {
  border-bottom: solid 1px #2f2f2f;
  background-color: #0f0f0f;
}
.cellName {
  border-bottom: solid 1px #2f2f2f;
  background-color: #1f1f1f;
}
.cellHTML {
  border-bottom: solid 1px #2f2f2f;
  background-color: #29292e;
}
#sendLine {
  border-top: solid 1px #7f7f7f;
  border-left: solid 1px #7f7f7f;
  border-right: solid 1px #29292e;
  border-bottom: solid 1px #29292e;
  background-color: #0f0f0f;
}
#chatCommands {
  border-top: solid 1px #29292e;
  border-left: solid 1px #29292e;
  border-bottom: solid 1px #7f7f7f;
  border-right: solid 1px #7f7f7f;
  background-color: #1f1f1f;
}
.advert {
  background: url("http://wsirc.com/images/top_wave.gif") no-repeat left top;
}
.loginBox {
  border: solid 1px #ffffff;
  background-color: #1f1f1f;
}
.loginTitle {
  background-color: #ffffff;
  color: #29292e;
}
.button {
  border: solid 1px #ffffff;
  background-color: #1f1f1f;
  color: #0000e0;
}
#serverList {
  border-bottom: solid 1px #ffffff;
  background-color: #ffffff;
}
#serverList .tab {
  background: url("http://wsirc.com/images/left_stab.gif") no-repeat left top;
}
#serverList span {
  background: url("http://wsirc.com/images/right_stab.gif") no-repeat right top;
  color: #ffffff;
}
#serverList #currentTabB {
  background-image: url("http://wsirc.com/images/right_stab_on.gif");
  color: #ffffff;
}
#serverList #currentTabA {
  background-image: url("http://wsirc.com/images/left_stab_on.gif");
}
.contentContainer {
  background-Color: #1f1f1f;
  border-bottom: solid 1px #7f7f7f;
  border-left: solid 1px #29292e;
  border-right: solid 1px #7f7f7f;
}
#channelMessage {
  border-top: solid 1px #7f7f7f;
  border-left: solid 1px #7f7f7f;
  border-right: solid 1px #29292e;
  border-bottom: solid 1px #29292e;
}
#onlineList {
  border-top: solid 1px #7f7f7f;
  border-left: solid 1px #7f7f7f;
  border-right: solid 1px #29292e;
  border-bottom: solid 1px #29292e;
  background-color: #29292e;
}
.titleChannelList {
  background-color: #000000;
}
.oddChannelList {
  background-color: #1f1f1f;
}
.evenChannelList {
  background-color: #29292e;
}
#suggestions {
  border: solid 1px #ffe000;
  background-Color: #414c69;
}
.serverUser {
  color: #e00000;
}
.userNickColor {
  color: #00a0c0;
}
#colorPicker {
  border: solid 1px #ffffff;
  background-color: #000000;
}
#smilyPicker {
  border: solid 1px #ffffff;
  background-color: #000000;
}
#userContextMenu {
  border: solid 1px #ffffff;
  background-color: #000000;
}
#userContextMenu td:hover {
  background-color: #8080ff;
}
#userSubMenu {
  border: solid 1px #ffffff;
  background-color: #000000;
}
#userSubMenu td:hover {
  background-color: #8080ff;
}
.onlineUser {
  border-bottom: solid 1px #000000;
  background-color: #29292e;
}
.overOnlineUser {
  border-bottom: solid 1px #000000;
  background-color: #000000;
}
#optServerList {
  background-color: #29292e;
  border-top: solid 1px #7f7f7f;
  border-left: solid 1px #7f7f7f;
  border-right: solid 1px #29292e;
  border-bottom: solid 1px #29292e;
}
.optServerItemSelect {
  background-color: #a0a0ff;
}
.optServerItem {
  background-color: #29292e;
}
.optServerItemOver {
  background-color: #1f1f1f;
}
.optServerItemCell {
  border-bottom: solid 1px #000000;
}
.menuItem {
  background-color: #a0a0ff;
  color: #ffffff;
}
.menuItemOver {
  background-color: #1f1f1f;
  color: #ffffff;
}
#helpBalloon {
  border: solid 1px #c0b080;
  background-color: #414c69;
}
.buttonSelected {
  background-color: #80ff80;
}
.borderedTable {
  border: solid 1px #ffffff;
}
input[type="text"],
input[type="password"],
select {
  border: dotted 0px #7f7f7f;
  border-radius: 2px;
}
.titleLine {
  background-color: #ffffff;
  color: #29292e;
}
#resServerList {
  background-color: #29292e;
}
.resServerTable {
  background-color: #29292e;
}
table.resServerTable tr:nth-child(even) {
  background-color: #333333;
}
table.resServerTable tr:nth-child(odd) {
  background-color: #000000;
}
table.resServerTable tr:hover {
  background-color: #ff8080;
}
.tab {
  color: #ffffff;
  background-color: #7f7f7f;
  border-top: 1px solid #1f1f1f;
  border-left: 1px solid #1f1f1f;
  border-right: 1px solid #dfdfdf;
}
.highTab {
  color: red;
  background-color: #7f7f7f;
  border-top: 1px solid #1f1f1f;
  border-left: 1px solid #1f1f1f;
  border-right: 1px solid #dfdfdf;
}
.tab_on {
  color: #ffffff;
  background-color: #4f4f4f;
  border-top: 1px solid #1f1f1f;
  border-left: 1px solid #1f1f1f;
  border-right: 1px solid #dfdfdf;
}

}
