css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {
/*侧边*/
#tree_pane {
    background-color: white;
    color: #383838;
}
#subscriptions_nav {
    padding-top: 0;
}
/*顶栏*/
#subscriptions_buttons, #sb_rp_tools {
    background-color: #F3F2EE;
    background-image: none;
}
#feed_searcher {
    background-color: white;
    color: #383838;
}
#sb_tp_search_overlay {
    background-color: white;
    background-image: none;
}
#sb_tp_dock{
    background-color: white !important;
    background-image: none!important;
}
#footer{
    display: none !important;
} 
/*屏蔽广告*/
.ad_footer_remove,#consent_bbb,.block_article_ad,.ad_title,.avs_iframe,#sb_rp_upgrade_button,#sinner_container,
#google_image_div,
#all_gad_6125,#all_gad_6124,
[class="ar article ar_showed ar_ad ar_skip article_tile ar_fixed_width radius ad_stripe"],
[class="ad_size_wide_skyscraper sinner side_sinner border_sinner"]{
		display: none !important;
}
/*页面视图*/
#reader_pane,#article_dialog,
[class="article_full_contents"],
[class="article_footer"],
[class="article_tile_content_wraper "],
[class="article_tile_footer"]
{
    background-color: #F3F2EE !important;
}
div.article_tile,div.article_card.article_current,.article_card.ar,.bundle_wrap,.discover_feed {
    border: none;
}
div.article_magazine.article_current {
    box-shadow: 0px 0px 5px 0px rgba(0, 0, 0, 0.3);
}
div.article_magazine {
    background-color: #F3F2EE !important;
}
.article_expanded {
    background-color: #F3F2EE !important;
}
.article_tile_title {
    padding: 0px 2px;
}
.article_content img, .article_content iframe, .article_content div{
    max-width: 100% !important;
}
.article_stripe {
    display: none;
}
#article_dialog_wrapper{
    width: 750px !important;
    top: 0px !important;
    background-color: #F3F2EE !important;
} 
#article_dialog{
    height: auto !important;
}
.ar {
    border-bottom: none;
}
}
