css:
@-moz-document domain("web.skype.com") {
/* Fond partie droite */
:root .swx .themeLight.chat {
    background-color:#252B2D!important
}
/* Bulle moi; Hover gauche; Ajout contact selection hover; Search hover; Background textarea actif */
:root .swx .themeLight.chat .conversation .message.me .bubble, :root .swx .themeWhite.side .recent:hover,:root .swx .themeWhite.side .searchItem:hover, :root .swx .themeWhite.side .navigation .navItem:hover:not(.active), :root .swx .chat .input.active {
    background-color:#444
}
/* Textes bulles; Texte lorsque focus; Pseudo actif; Disponible */
:root .swx, :root .swx .chat .input.active form textarea, :root .swx .chat .conversationHeader .headerMain .roster .grid .tile.conversationTopic h3 > a,:root .swx .chat .conversationHeader .headerMain .roster .grid .tile.conversationTopic .displayMessage > a {
    color:#E3E3E3
}
/* Déroulant + en haut a gauche */
:root .swx.themeBlue,:root .swx .themeBlue {
    background-color:#181818
}
/* Fond partie gauche;Bulles expéditeur, Background droite haut conv; ??? ; Fond Clic Droit; Nouveau text area  */
:root .swx .themeWhite, :root .swx .themeLight.chat .conversation .message.their .bubble, :root .swx .chat .conversationHeader .headerMain, aside.sideContainer,:root ul.swxContextMenu,:root .swx .chat .conversationHeader .headerAdd {
    background-color:#1E1E1E!important;
}
/* Bordure Haut Gauche */
:root .swx .meApp .summary .innerSummary {
    border:1px solid #000
}
/* Gauche contact actif; ???; Nouveau actif; Clic droit fond li x2 */
:root .swx .themeWhite.side .recent.active,:root .swx .themeWhite.side .searchItem.active, :root .swx .themeWhite.side .navigation .navItem.active, :root ul.swxContextMenu li:focus span,:root ul.swxContextMenu li:hover span {
    background-color:#224352!important
}
/* Nom contacts liste gauche; noms contacts liste ajouts, Texte inactif, Texte rechercher actif; Clic droit texte */
:root .swx .themeWhite.side .recent .tileName,:root .swx .themeWhite.side .searchItem .tileName, :root .swx .chat .input form textarea, :root .swx .search .input.active input,:root ul.swxContextMenu li {
    color:#D2D2D2
}
/* déroulant cover */
:root .swx .showCover, :root .swx .cover {
    opacity:0.3
}
 /* Orange new message */   
:root .swx .themeWhite .timeline .recent.unread:not(.active) .message {
    color: #CE6600;
}
    
/* Bouton plus dark */
:root .swx .btn.primary:before {
    background-color:#0085B6!important
}
/* Bouton hover plus dark */
:root .swx .btn.primary:hover:before {
    background-color:#006388!important
}
}
@-moz-document url-prefix('https://login.skype.com/login') {
footer, #cloudWrap, nav {display: none}
html, body, header {background-color: #181818 !important;}
header {background: url(https://az663213.vo.msecnd.net/0-190-1/images/skype_logo_white.png) no-repeat center; margin-top: 20px; margin-bottom: 20px  }
.contentBlock {width: 500px; margin: auto}
    .loginContainer {text-align: center; background-color: #444; width: 500px}
.fieldRow {margin: auto; color:white !important;}
.inputField input {color: white}
#unifiedFacebook {position: relative !important;}
.fbIcon {width: 40%;margin: auto !important;}
}
