css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("aryion.com") {
  .blacklist-overlay {
  }
  a.thumb.scrollthumb {
    box-shadow: 0px 0px 0px 0px !important;
    border-radius: 4px;
  }
  #login-block {
    background-color: #7F50CF;
    border-radius: 1em;
    position: fixed;
    overflow: visible !important;
    min-height: 1px !important;
    z-index: 3;
    padding: 1em;
    left: 50%;
    width: 500px;
    margin-left: -250px;
    margin-top: -1.25em;
  }
  .headerbar {
  }
  .shout-bubble-arrow-border, .shout-bubble-arrow {
    display: none;
  }
  .new-shout, .shout-reply {
    background: none;
    border: none;
  }
  .odd .new-shout {
    margin: 0px 0px 0px 120px;
  }
  a[style="float: right; text-align: left; width: 100px;"] {
    float: left !important;
  }
  a[style="float: right; text-align: left; width: 100px;"] img {
    float: right !important;
  }
  #sized-note {
     border: none; 
  }
  /* BACKGROUNDS */
  #accentbar, #userdata, #menucontainer, .g-box-header {
    background: #7F50CF; /* Purple Accent */
  }
  #globalheader {
    background: #7F50CF !important; /* Needs to not be white -custom page css dosen't affect this so its okay*/
    overflow: visible !important;
  }
  #folder-listing {
    width: 100% !important;
  }
  .comment-header {
    background: white !important;
  }
  .ui-state-default, div[style="overflow: auto; position: relative; padding: 1em;"], #userpagetabs li,
  #globalheader, #message-form, #g-footer, .g-box, #sized-note  { /* The great window bleacher */
    background: white; /* White Backdrop */
  }

  /* GENERAL UI */
  .item-box .g-box-header[style] { /* hackjob to make uploaded writing look better*/
    padding: 0;
    border-bottom-right-radius: 0em;
border-bottom-left-radius: 0em; 
  }
 /* #item-itself[style*="width: 80%;"] {
    padding: .25em .5em;
  }*/ /* Would bulk up the item window and make it fit with the rest of the g-boxes, but makes the scrollbar hang so eeeh. */
  .avatar {
    /*border: 2px rgba(0,0,0,.3) solid;*/
    border-radius: 50%;
    height: 100px;
    width: 100px;

  }
  .iconuser img {
    border-radius: 50%;
  }
  html[lang] { /*by specifying the lang attribute we exclude site-hosted stories! yay no more bugs with this */
     /*this has a background?*/
     background: #C0C0FF;
  }
  body {
    margin: 0;
    width: calc(100% - 16px);
    min-width: 990px; /* i think its a reasonable enough minimum, who even owns 1024x768 displays anymore!?*/
    margin-left: 8px;
    box-shadow: none;
    font-family: verdana !important; /* Fix font variation */
    background: #C0C0FF;
    /*background: url("http://i.imgur.com/o0H4KPl.png");*/ /*Uncomment to fix background*/

  }
  hr {
    display: none; /* Not needed */
  }
  
  /* UI: G-box */
  .g-box-header { /* Make headers have white text and reset border */
    border-radius: 0;
    color: white;
  }
  .g-box {
    margin: 0px;
    padding: 0px;
    border: 0;
  }
  .g-box-contents > p {
    line-height: 150%;
    font-size: 100%;
    color: #222;
  }
  p, .ui-selectee {
    color: #333;
  }
  #globalheader p {
    color: white;
  }
  #front-page {
    max-width: 100% !important;
    padding: 0 !important;
    background: white;
  }
  .g-box[style*="width: 90%"] {
    width: 100% !important; /* hijack inline styles */
  }
  .item-box > .g-box-header {
    border-radius: .5em;
  }
  body > .g-box, body > div > .g-box { /* Only non-nested boxes get this style */
    border-radius: .5em;
    margin: 0 auto;
    margin-bottom: 1em;
    margin-top: .5em;
    background: white;
    /*width: 80% !important;*/
    overflow: hidden;
  }
  #globalheader, #message-form, #g-footer { /* This needs to be display dependent but whatevs */
    padding: 0;
    margin: 0 auto;
    /*width: 90%;*/
    border-radius: 0 0 .5em .5em !important;
    overflow: hidden;
  }
 /* #item-box {
    max-width: 100px !important;
    width: 100px !important;
  }
  #item-box > .g-box-header {
    border-radius: .5em 0 !important;
  } */
  .g-box-header .ui-droppable {
    color: #536482 !important;
    padding: 0 .25em !important;
  }
  .title, .user-link, .msg-loader {
    color: #7F50CF;
  }   
  
  #menucontainer {
    right: 0;
 /* line-height: 24 !important;*/
  }

  .expanded {
       margin-top: 10px;
  float: left;
        font-size: 12.5px !important;
    font-weight: normal !important;
  }
  .expanded > a {
    font-size: 12.5px !important;
    font-weight: normal !important;
    padding: 0 !important;
    padding-right: 1em !important;
  }
  
  /* LINKS */
  a {
    color: #7F50CF;
  }
  a:hover {
    color: #470047 !important;
  }
  .g-box-footer a, a[href*="/g4/user"], .topic-title > a, #userpagetabs a , .g-box-header a {
    color: #7F50CF;
  }
  .g-box-footer a:hover, a[href*="/g4/user"]:hover, .topic-title > a:hover, #userpagetabs a:hover, .g-box-header a:hover {
    color: #470047;
  }
  #welcome-msg-block a {
    color: white !important;
  }
  #welcome-msg-block a:hover {
    color: #C0C0FF !important;
  }
  #messageform a, .g-box-header a, #g-footer a  {
    color: white;
  }
  #messageform a:hover, .g-box-header a:hover, #g-footer a:hover  {
    color: #C0C0FF !important;
  }
  #globalheader a:hover  {
    color: #C0C0FF !important;
  } 
  
  
  /* UI: Usertabs */
  #userpagetabs > ul > .ui-state-default {
    box-shadow: 0;
    border-radius: .5em .5em 0 0 !important;
    border: 0 !important;
  }
  #userpagetabs ul {
    padding: 0;
    width: 90% !important;
  }
  div a.thumb img, .blacklist-overlay {
    border-radius: 4px;
  }
  #userpagetabs ul li a {

  }
  .g-box-footer {
    background: white;
  }


  a:hover {
    color: lightblue;
    text-decoration: none;
  }
  
  /* HEADER */
  #globalheader {
    height: 25px !important;
    margin-bottom: 1em;
  }

  #gallery-items {
    margin-top: 2em;
  }
  #ss-view {
    width: 0; /* Okay, yes this does limit 'functionality', but 3 pane is fine enough for me and makes everything fit */
  }
  #sitelogo {
    display: none;
  }
/*
  /* HEADER: Usercenter */
  #usercenter {
    margin-left: 0;
    float: left;
  }
  #userdata {
    background: none;
    font-size: 12.5px !important;
    font-weight: normal !important;
    box-shadow: none; /* blend it in with the ui bar */
    margin: 0;

    padding: 0px;
    padding-left: 8px;
    padding-top: 5px;
  }
  #userdata p {
    float: left; 

  }
  #userdata > p[style="font-weight: bold; color: #FAFAFA;"] {
   margin-right: 1em;
  }
  #usercenter img {
    display: none;
  }
  #menucontainercontainer {
    width: 240px;

  }
  #accentbar {
    float: left;
    height: 0px !important;
  }
  p > a[href="/g4/messagepage.php"]:before {
    content: "ã€€| ";
  }
  p > a[href="/g4/messagepage.php"]:after {
    content: "";
  }
  #globalsearch {
    height: 25px;
    max-height: 25px;
    padding: 0;
    margin: 0;
    float: right;
    border-radius: 0 0 .5em 0;
  }
  #globalsearch input {

    margin: 0;
    padding: 0; 
    padding-left: 1em;
    width: 200px !important;
    color: white !important;
  }
  #svcinfo {
    display: none;
  }
  #globalsearch {
    right: 0;
  }
  #search-submit {
    display: none;
  }
  #form-header, #form-header-spacer { /* Remove these because I've never used them and am too lazy to fix how I did globalheader */
    display: none;
  }
  
  /* FOOTERS */
  #footer {
    padding: 0;
    margin: 0 auto;
    background: white;
    box-shadow: 2px 0px 5px rgba(0, 0, 0, 0),
                -2px 0px 5px rgba(0, 0, 0, 0);
    border-radius: .5em .5em 0 0 !important;
    height: 5em;
    text-align: center;
  }
  #g-footer {
    margin-top: 2em; /* Give some breathing room */
  }

  /* WELCOME MSG */
  #ss-header {
    display: none;
  }
  #ss-scroll .g-box-header, #ss-scroll, #ss-scroll .g-box-title {
    color: #333 !important;
    background: white;
    font-weight: normal !important;
  }
  #ss-scroll .icon-24 {
    display: none;
  }
  #latest-writing .g-box-header {
    margin-bottom: 1em;
  }

  #globalheader, #welcome-msg-block {
    max-width: 100% !important;
  }
  #welcome-msg-block {
    background: #7F50CF;
    width: 100% !important;
    margin: 0 auto !important;
    border: none !important;
    border-radius: 0.5em 0.5em 0 0;
    height: 0em !important;
    box-shadow: 0px 1px 5px rgba(0, 0, 0, 0);
  }
  #welcome-msg-block td {
    margin: 0;
    padding: 0;
    width: 50% !important;
    text-align: center;
  }
  #welcome-msg-block td[style="width: 74%"] {
    display: none;
  }
  /*#front-page:before {
    vertical-align: center;
    background: white;
    display: block;
    color: #7F50CF;
    margin: -.5em 0 auto auto;
    text-align: center;
    width: 100% !important;
    border-bottom: 1px white solid;
    content: "Eka's Portal";
    font-size: 300%;
    font-weight: bold;
    padding-top: 1.25em;
    
  }*/
  #welcome-msg-block td[style="width: 15%"] li {
    float: right;
  }
  #welcome-msg-block td[style="width: 15%"] {
    border-right: 1px white dotted;
  }
  #welcome-msg-block li {
    float: left;
    margin-left: 1em;
    margin-right: 1em;
    display: inline !important;

  }
  #welcome-msg-block ul {
    list-style-type: none !important;
    display: inline-block !important;
    width: 100%;
    margin: 0 !important;
    padding: 0 !important;
    text-align: center !important;
  }
  #front-page, #message-form {
    border-radius: .5em !important;
  }
  .gallery-items img[src="//static.aryion.com/g4/thumb/sys/folder.png"] {

  }
  /* USERPAGE */
  #right-column {
    float: right !important;
  }
  #right-column > .g-box, #left-column > .g-box {
    margin: 1em 0 !important;
    width: 100% !important;
  }
  #left-column {
    margin: 0 !important;
    width: 49% !important;
    min-width: 49%;
  }
  /* MESSAGES */

  .gallery-item .g-small span {
    display: block;
  }
  .biicon11 {
    display: none !important;
  }
  body[dir*="LTR"] {
    background: rgb(255,255,255) !important;
  }
  p[style*="margin: 0.75em 0.5em 0 0.5em;"] {
    margin: 0.75em 0.5em 0 0.5em;
  }
  p[style*="clear: left;"] {
    padding-bottom: 2em !important;
  }
  .thumb > img, img[style="max-height: 300px; max-width: 300px;"], .scrollthumb {
    border-bottom: 2px rgba(0,0,0,.3) solid;
    /*border-radius: .5em !important; */

  }
  .scrollthumb {
    display: block;
    margin-top: 20px;
  }

  input[type="button"] {
    border: 0;
    border-radius: .5em;
    border-bottom: 1px rgba(0,0,0,.3) solid;
  }

  #ss-scroll > div, .region, #content > .section {
    margin: .5em;

    border-radius: .5em !important;
    overflow: hidden;
    background: white;
  }
  #g-footer, #footer {
    margin: 0 auto;
    border-radius: .5em !important;
    margin-bottom: 1em;
    width: 30%;
    margin-top: .5em;
  }
  #content > .section {
    margin-left: calc(150px + 1em);
    padding-left: 1em !important;
    padding-top: 1em !important;
  }
  .region > .section {
    padding-right: 0;
    padding-left: .5em;
  }
  .region {
    padding-top: 1em !important;
  }
  div[style*="width: 79%"] {
    margin-left: 1em;
    width: calc(79.9% - 1em) !important;
  }
  /*div[style*="width: 20%"] {
    width: 16% !important;
   margin-right: 0 calc(10% - 2em); 
  }*/
  .detail-item {
    border: 0px rgba(0,0,0,0);
    margin-bottom: 1em;
  }
  .comment {
    border-top: none !important;
    border: none;
    padding: .5em !important;
    margin-top: .5em !important;
    background: white;
  }
  .detail-item .comment {
    margin-left: 0em !important; /* lol we can do this because they use inline styles for the normal thing */
  }
  .comment-header span {
    color: #333;
  }
  #ss-viewport .g-box {
    width: 405px;
    max-height: 817px;
  }
  #ss-viewport {

  }
  .forum-topic {  
    border-bottom: 0;
     margin-bottom: 1em;
  }
  #userpagetabs > ul {
    width: 80%;
    margin: 0 auto;
  }
  #userpagetabs {
    margin: 0;
    margin-bottom: -.5em;
    padding-right: 0em;
  }
  
  .g-corner-all {
    border-radius: .5em;
  }
  .item-title { /* this fixes the text. it's ugly and kind of awkward. sorry */
    margin: 0 auto;
    max-width: 160px;
    width: 180px;
    height: 21px;
    max-height: 42px;
    text-overflow: ellipsis;
    overflow: hidden;
  }
  #uph {
    padding: 0;
    margin: 0 auto;
    background: white;
    margin-top: .5em;
  }
  #latest > .g-box-contents {
    max-height: calc(100px + .5em);
  }
  #latest .thumb {
    margin-bottom: 1em !important;
  }
  .preview > img {
      border-radius: .5em !important; 
  }
  #left-column {
    margin-top: 1em;
    width: 40% !important;
    margin-left: calc(10% - .5em);
    margin-right: .5em;
        border-radius: .5em !important;
    overflow: hidden;
  }
  iframe {
    border: 0;
    border-radius: 0 0 .5em .5em !important;
  }

}
