css:
@-moz-document domain("getpocket.com") {
.pkt-nav, body, #content .wrapper.active, .side-nav, .page_queue_grid .item_content, .sub, .page_queue_list .item_content, .edit-content, .tag-detail
{
	background-color: #3F3F3F !important; /*background, dark gray */
}

.page_queue_grid, .item_content, .edit-content, .item-detail, .item-domain, .tag-detail /* articles list */
{
  border: 1px solid #313633 !important;
  border-bottom: 1px solid #313633 !important;
}

h1
{
    color:#f0dfaf !important; /*conditional */
}

h1, h2, h3, h4, h5, h6, h7
{
    color:#f0dfaf !important; /*conditional */
}

.reader_content, .excerpt
{
    color:#dcdccc !important; /* normal, gray */
}

a 
{
	color: #7f9f7f !important; /* comment, green */
}

a.topic_detail, a.topic_detail:visited
{
	color:#dcdccc !important; /* normal, gray */
}

a:visited 
{
	color: #DCA3A3 !important; /* string, red */
}

.reading_progress_bar
{
    background-color: #8CD0D3 !important; /* light blue, number */
}

.text_body blockquote, .text_body blockquote p
{
    border-color: #8CD0D3 !important;
	color: #8CD0D3 !important;
}

hr /* line, blue */
{
    background-color: #8CD0D3 !important;
}

/* all borders, dark gray, 313633 */
.pkt-nav 
{
  border-bottom: 1px solid #313633 !important;
}

#styleMenu
{
	border: 1px solid #313633 !important;
}

#styleMenu .textoptions_font, .textoptions_theme, .textoptions_font>li
{
  border-right: 1px solid #313633 !important;
}

#styleMenu .textoptions_theme 
{
  border-top: 1px solid #313633 !important;
}

.textoptions_font, .textoptions_theme, .textoptions_font>li, .textoptions_theme
{
    border-color: #313633 !important;
}

#styleMenu .textoptions_font .selected 
{
  background-color: #909090 !important; /* darkgray selected option */
}

.page-readersepia .RIL_IMG caption, .page-readersepia a .RIL_IMG caption 
{
    color: #8CD0D3 !important; /* image caption, blue, number */
}
.page-readerdark .RIL_IMG caption, .page-readersepia a .RIL_IMG caption 
{
    color: #8CD0D3 !important; /* image caption, blue, number */
}
.page-readerlight .RIL_IMG caption, .page-readersepia a .RIL_IMG caption 
{
    color: #8CD0D3 !important; /* image caption, blue, number */
}

.RIL_IMG cite
{
    color: #8CD0D3 !important; /* image caption, blue, number */
}
}
