css:
@-moz-document domain("www.facebook.com") {
.rhc.photoUfiContainer {
    display: none;
}
.stageWrapper {
    width: 100% !important;
    height: 100% !important;
}
.fbPhotoSnowliftPopup {
    width: 100% !important;
    height: 100% !important;
}
.fbPhotoSnowliftContainer {
    width: 100%;
    height: 100%;
}
._n3 {
    width: calc(100% - 40px) !important;
    height: calc(100% - 40px) !important;
}
.fbPhotoSnowlift .stage img.spotlight {
    width: auto !important;
    height: auto !important;
}
._n9 {height: 100%;padding: 0;}

.stage:before, .stageWrapper.showVideo:before {
    content: "";
    display: inline-block;
    height: 100%;
    vertical-align: middle;
}
.fbPhotoSnowlift .videoStage {
    right: 0;
    left: 0;
    margin: auto;
}
}
