@-moz-document domain("sicp.iijlab.net") {
    body {
        max-width: 40em;
        line-height: 1.8em;
        letter-spacing: normal;
        font-size: 0.94em;

        margin-right: auto;
        margin-left: auto;

        padding-left: 1.5em;
        padding-right: 1.5em;
    }

    pre{
        font-family: Consolas, 'Courier New', "モトヤLシーダ3等幅",Courier, Monaco, monospace;
        font-size: 16px;
        background-color: #fafafa;
        border: solid 1px #bbbbbb;
        line-height: 120%;
        margin: 1em 0 1em 0;
        padding: 0.99em;
        overflow-x: auto;
        overflow-y: auto;
    }
}
