css:
@-moz-document url-prefix("http://www.hordes.fr/tid/forum#!view/"), url-prefix("http://twinoid.com/g/le-choc-les-habitants"), url-prefix("http://twinoid.com/tid/forum#!view/") {
/* Balise Double Gras */

.tid_editorContent strong strong {
  color: red !important;
  opacity: 1 !important;
  text-shadow: 0px 0px 10px red !important;
}

/* Balise Double Italiques */

.tid_editorContent em em {
  color: #2BFAFA !important;
  text-shadow: 0px 0px 10px #2BFAFA !important;
}

/* Balise Italiques */

.tid_editorContent em {
  opacity: 0.9 !important;
  color: #f0d79e !important;
}

/* Balise Gras : **Texte** */

.tid_editorContent strong {
  color: #afb3cf !important;
  opacity: 0.8 !important;
}

/* Forum twinoid (couleur des post et du fond) */
#tid_forum .tid_bg1 { background-color: #965c36 !important; }
#tid_forum .tid_border1 { border-color:#965c36 !important; }
#tid_forum .tid_bg1 { color:white !important; }
#tid_forum .tid_bg2 { background-color:#4a261e !important; }
#tid_forum .tid_border2 { border-color:#4a261e !important; }
#tid_forum .tid_bg2 { color:white !important; }
#tid_forum .tid_bg3 { background-color:#4a261e !important; }
#tid_forum .tid_border3 { border-color:#4a261e !important; }
#tid_forum .tid_bg3 { color:#eba475 !important; }
#tid_forum .tid_bg4 { background-color:#321914 !important; }
#tid_forum .tid_border4 { border-color:#321914 !important; }
#tid_forum .tid_bg4 { color:white !important; }
#content { background-color: #7e4d2a; background-image: url(http://image.noelshack.com/fichiers/2015/25/1434720685-azerthj.png); background-repeat: repeat-x;
}

/* Couleur du texte générale du forum twinoid (texte gris en blanc) */
.scontainer.forum .tid_editorContent {
  color: white;
}

/* Lien des forum twinoid style hordiens */

.scontainer.forum .tid_editorContent a {
  color: #ddab76;
}
.scontainer.forum .tid_editorContent a:hover {
  color: white;
}

/* "Disparition" de la premiére bordure et du fond des avatar */
.tid_twinoidAvatar table td {
	border : 0px !important;
	margin : 0px !important;
	padding : 0px !important;
	background-color : transparent !important;
	overflow : hidden;
	text-align : center;
	vertical-align : middle !important;
	outline : none !important;
}
/* "Disparition" de la seconde bordure des avatar */

.tid_userSheet td.tid_avatar .tid_twinoidAvatar {
	-moz-box-shadow : 0px 0px 2px transparent !important;
	-webkit-box-shadow : 0px 0px 2px transparent !important;
	box-shadow : 0px 0px 2px transparent !important;
}

/* Spoil */

.tid_editorContent .tid_spoil {
background-image: url("http://data.hordes.fr/gfx/design/spoiler.gif") !important;
background-repeat: no-repeat !important;
    background-position: 4px 1px !important;
    display: block !important;
    background-color: rgb(0,0,64) !important;
      border-radius: 3px!important;
  -moz-border-radius: 3px!important;
  -webkit-border-radius: 3px!important;
}

.tid_editorContent .tid_spoil .tid_wspoil:first-child {
		padding-left: 60px !important;
	}
	.tid_editorContent .tid_spoil:hover {
		visibility: visible !important;
		background-image: url("http://data.hordes.fr/gfx/design/spoiler.gif") !important;
		background-color:transparent !important;
		color : rgb(0,0,64) !important;
	}

/* Balise annonce */

.tid_editorContent .tid_announce {
  background-image: url("http://image.noelshack.com/fichiers/2015/25/1434895561-modhabitant.png")!important;
  background-position: bottom left!important;
  background-repeat: no-repeat!important;
  padding-left: 60px !important;
}

/* Balise modération */

.tid_editorContent .tid_mod {
  background-image: url("http://data.twinoid.com/img/lang/fr/modAnnounceBg.png")!important;
  background-color: #bd3d00!important;
}
/* Balisse RP */

.tid_editorContent .tid_roleplay .tid_wroleplay {
display: block;
margin-top: -10px;
padding-top: 10px;
background-repeat: no-repeat;
background-image: none;
background-position: 5px top;
}

.tid_editorContent .tid_roleplay {
display: block;
margin-top: 7px;
margin-bottom: 15px;
margin-left: 20px;
max-width: 350px;
padding: 3px;
color: white;
font-style: italic;
font-size: 11pt;
border-radius: 6px;
-moz-border-radius: 6px;
-webkit-border-radius: 6px;
background: #321914;
border: 1px solid #965C36;
border-top-color: #965C36;
-moz-box-shadow: 0px 0px 2px #965C36;
-webkit-box-shadow: 0px 0px 2px #965C36;
box-shadow: 0px 0px 2px #965C36;
}

.tid_editorContent .tid_preRoleplay {
display: block;
margin-top: 7px;
margin-left: 10px;
padding: 0px 5px;
padding-left: 20px;
max-width: 200px;
height: 18px;
overflow: hidden;
font-weight: bold;
font-size: 8pt;
font-style: italic;
line-height: 18px;
background-image: url("http://www.hordes.fr/img/icons/r_rp.gif");
background-repeat: no-repeat;
background-position: 1px 0px;
}

.tid_editorContent .tid_roleplay .tid_wroleplay strong {
color: red !important;
}

#tid_forum .tid_postForm textarea {
width: 95%;
resize: vertical;
min-height: 250px;
background: #4A261E;
color: white;
}

/* Balise Annonce */

.tid_editorContent .tid_announce {
  background-position: bottom left !important;
  background-color: #965C36 !important;
  text-shadow: 0px 0px 10px #4A261E !important;
  border: 1px solid #4A261E !important;
  -moz-box-shadow: 0px 0px 2px #965C36 !important;
  -webkit-box-shadow: 0px 0px 2px #965C36 !important;
  box-shadow: 0px 0px 2px #965C36 !important;
  text-align: left !important;
  min-height: 20px !important;
  vertical-align: bottom!important;
}

/* Balise Mod */

.tid_editorContent .tid_mod {
  background-position: 5px 5px !important;
  background-color: #290000 !important;
  color: red !important;
  text-shadow: 0px 0px 10px red !important;
}

.tid_editorContent .tid_mod {
  background-color: #290000 !important;
}

/* Clic sur un pseudo */

#tid_userMenu div.tid_content {
  background-color: #4A261E !important;
  border: 1px solid #965c36 !important;
  -moz-box-shadow: 0px 2px 4px #965C36 !important;
  -webkit-box-shadow: 0px 2px 4px #965C36 !important;
  box-shadow: 0px 2px 4px #965C36 !important;
  color: white !important;
  background-image: none;
}

/* infobulle des bouton forum */

#tid_simplePointer .tid_arrow, #tid_simpleTip .tid_arrow {
display: none;
visibility: hidden;
}

#tid_simplePointer .tid_inner, #tid_simpleTip .tid_inner {
  text-shadow: 0px 0px 10px red !important;
  background-color: #290000!important;
  border: 1px solid #965C36!important;
  outline: 1px solid #4A261E!important;
  color: red!important;
}

/* loading caché */

#tid_forum .tid_loading {
opacity: 0.0;
background: transparent;
}

/* balise questionnaire changement de couleur */

.tid_editorContent .tid_module {
max-width: none;
max-height: none;
margin: 0px;
padding: 0px;
text-decoration: none;
color: white;
font-family: Arial, sans-serif;
font-size: inherit;
font-style: normal;
font-weight: normal;
font-variant: normal;
text-transform: none;
background: #321914;
border: 0px;
outline: none;
text-shadow: none;
float: none;
}

.tid_questionModule {
margin: 20px 0px;
padding: 10px;
min-width: 180px;
border: 1px solid #965C36;
border-top: 0px;
border-radius: 3px;
-moz-border-radius: 3px;
-webkit-border-radius: 3px;
-moz-box-shadow: 1px 1px 3px black;
-webkit-box-shadow: 1px 1px 3px black;
box-shadow: 1px 1px 3px black;
-moz-box-shadow: 1px 1px 3px #965C36;
-webkit-box-shadow: 1px 1px 3px #965C36;
box-shadow: 1px 1px 3px #965C36;
background-color: #321914;
background-image: url("http://image.noelshack.com/fichiers/2015/13/1427217901-1.png");
background-position: center top;
background-repeat: repeat-x;
}

/* Agrandissement du div de pagination */
		#tid_forum .tid_forumThread .tid_actionBar .tid_pages {
		width: 248px !important;
	}
	/* Alignement divers */
	#tid_forum .tid_actionBar .tid_buttonBar {
		margin: 0px !important;
		margin-left: 1px !important;
		margin-top: -2px !important;
	}

	/* "bouton" de choix de page */
	.tid_cur.tid_tip.tid_parsed {
		background-color: transparent !important;
		background-image: url(http://data.hordes.fr/gfx/design/button.gif) !important;
		border: 1px solid #98341c !important;
  		border-bottom: 1px solid black !important;
		margin-top: auto !important;
		color: #f0d79e !important;
	}
	.tid_cur.tid_tip.tid_parsed:hover {
		border: 1px solid #98341c !important;
		border-top: 2px solid black !important;
		border-bottom: 0px solid #98341c !important; 
		outline: 1px solid #f0d79e !important;
		color: white !important;
	}

	/* Bouton de pagination et Répondre / suivre / Forum */
	.tid_buttonBar a {
		background-image: url(http://data.hordes.fr/gfx/design/button.gif) !important;
		border: 1px solid #98341c !important;
        border-bottom: 1px solid black !important;
		margin-right: 1px !important;
		color: #f0d79e !important;
	}
	
	/* Effet "bouton appuyé" */
	.tid_buttonBar a:hover {
		opacity: 0.99 !important;
		border: 1px solid #98341c !important;
		border-top: 2px solid black !important;
		border-bottom: 0px solid #98341c !important; 
		outline: 1px solid #f0d79e !important;
		color: white !important;
	}
	/* Ne pas faire l'effet "bouton appuyé" au OFF */
	.tid_buttonBar .tid_off:hover {
		border: 1px solid #7c5a50 !important;
		opacity: 1 !important;
		outline: 1px solid #421d15 !important;
		color: #dddbd8 !important;
	}
	/* Gestion de l'affichage des boutons en "Off" */
	.tid_buttonBar .tid_off {
		background-image: url(http://data.hordes.fr/gfx/design/button_off.gif) !important;
		border: 1px solid #7c5a50 !important;
		opacity: 1 !important;
		outline: 1px solid #421d15 !important;
		color: #dddbd8 !important;
	}
	.tid_buttonBar a.tid_off img {
		opacity: 0.3 !important;      
	}

	/* Gestion des boutons "Répondre" */
	.tid_postForm button {
		background-color: transparent !important;
		margin-top: 4px !important;
		background-image: url(http://data.hordes.fr/gfx/design/button.gif) !important;
		border: 1px solid #98341c !important;
		outline: 1px solid black !important;
		color: #f0d79e !important;
		-moz-box-shadow : none !important;
		-webkit-box-shadow : none !important;
		box-shadow : none !important;
	}
	.tid_postForm button:hover {
		opacity: 0.99 !important;
		border: 1px solid #98341c !important;
		border-top: 2px solid #98341c !important;
		border-bottom: 0px solid #98341c !important;  
		outline: 1px solid #f0d79e !important;
		color: white !important;
		padding: 2px 20px !important;
		-moz-box-shadow : none !important;
		-webkit-box-shadow : none !important;
		box-shadow : none !important;
	}
	#tid_forum .tid_mainBar .tid_actionBar {
		opacity: 1;
	}

/* Balise ? */

#tid_forum .tid_threadNotice.tid_announce {
  background-color: #290000;
  border: 1px solid #965C36;
}

/* User */

#tid_userMenu .tid_links .tid_siteLinks a, #tid_userMenu .tid_links .tid_minorLinks a {
  color: #ddab76 !important;
}

#tid_userMenu .tid_links .tid_siteLinks a, #tid_userMenu .tid_links .tid_minorLinks a:hover {
  color: white !important;
  background: transparent !important;;
}

#tid_userMenu .tid_baseInfos .tid_asv .tid_age, #tid_userMenu .tid_baseInfos .tid_asv .tid_country, #tid_userMenu .tid_baseInfos .tid_asv .tid_city {
  color: red;
  text-shadow: 0px 0px 10px red;
}
#tid_userMenu .tid_rel.tid_isUnknown {
  background-color: #290000!important;
}

.tid_userStatus .tid_arrow.tid_up {
  margin-top: -6px!important;
  margin-left: 30px!important;
  background-image: none!important;
}

.tid_userStatus .tid_bubble {
  -moz-box-shadow: 0px 2px 4px #4A261E!important;
  -webkit-box-shadow: 0px 2px 4px #4A261E!important; 
  box-shadow: 0px 2px 4px #4A261E!important; 
  background-color: #290000!important;
  border: 1px solid #965C36!important;
  color: white !important;
}

#tid_userMenu .tid_links .tid_tinyLinks a {
  color: #DDAB76!important;
}
#tid_userMenu .tid_links .tid_tinyLinks a:hover {
  color: white!important;
}
}
