css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jukepop.com") {
  body {
    background-color: #FDFDFD;
  }
  
  .panel-1, .panel-2, .panel-3, .panel-4 {
    display: none;
    background: #333;
  }
  
  .navbar-static-top, .panel-1, .panel-2, .panel-3, .panel-4, .panel-5 {
    background: #333;
  }
  
  .panel-5 .get-started-button, .navbar-inner-container .sign-up-button {
    color: #EEE;
    background: #888;
  }
  
  .panel-5 .get-started-button:hover, .navbar-inner-container .sign-up-button:hover {
    color: #EEE;
    background: #111;
  }
  
  .navbar-inverse {
    background-color: #454545;
    border-color: #DDDDDD;
    color: #DDDDDD;
    opacity: 0;
  }
  
  .navbar-inverse:hover {
    opacity: 100;
  }

  .col-md-6 {
    display: block;
    position: relative;
    left: 1%;
    width: 80%;
    margin: 0 auto;
  }
  
  .main-content h1, .text-center, #reading-col .container-text {
    color: #222;
  }
  
  #reading-col, .chapter-text h2, .activity-item, .post-child-activity-form textarea, .post-activity-form textarea {
    color: #111;
  }
  
  .chapter-text, .activity-item, .user-stats, .serial-infobox, .post-child-activity-form textarea, .input-group textarea, .post-activity-form textarea, #reading-col .container-text {
    background-color: #EEE;
  }

  .col-md-2, .col-md-4, img, .serial-cover, .alert {
    display: none;
    padding: 0px;
    margin: 0px;
  }
  
  .col-md-8 .container-text {
    color: #111;
    background: #DDD;
    width: 650px;
  }
  
  .col-md-8 #chapter-form {
    width: 600px;
  }
  
  .col-md-8 .form-group {
    width: 600px;
  }
  
  .col-md-8 .container-text A {
    color: #000088;
  }
  
   .col-md-8 .container-text td {
     color: #111;
     background: #DDD;
   }
  
  .hamburger-menu, .hamburger-footer {
    border-color: #111;
  }
  
  .child-activity-container .activity-post {
    border-bottom: solid 1px #111;
  }
  
  .activity-item .activity-content {
    margin-left: 0px;
  }
  
  .parent-activity, #hc-bottom-section {
    background-color: #DDD;
    padding: 5px;
  }

  .hamburger-menu-icon img, .nav-logo-link img {
    display: table;
  }

  .hamburger-container, .post-child-activity-form button, .input-group button, .post-activity-form button {
    background-color: #222;
    border-color: #111;
    color: #DDD;
  }

  .activity-avatar, .posting-avatar, .bookshelf-jp30-badge {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 0px !important;
    padding-top: 0px !important;
    background: none;
    border: 0 none;
  }
  
  .vote-btn img, .bookshelf-btn img, .btn-jp-thin img {
    display: block;
  }
  
  #synopsis-section, #font-increase, #font-decrease, #toggle-reading, .dropdown-toggle {
    display: none;
    width: 0;
    height: 0;
    background: 0 none;
    border: none;
  }
  
  .change-chapter {
    top: -10px;
    left: 7%;
    height: 30px;
    margin: 5px;
  }
  
  .previous-chapter img, .next-chapter img {
    display: block;
    height: 18px;
  }
  
  #comment-col {
    width: 80%;
  }
  
  .synopsis-jp30-badge, .bookshelf-jp30-badge, .bookitem-jp30-badge, .book-item-author, .book-item .subtext-black {
    display: none;
    background: none;
  }
  
  .bookshelf-item-container, .book-item {
    padding: 0px;
    width: 140px;
    height: 50px;
    overflow: hidden;
    margin: 0px;
    box-shadow: none;
    background-color: #EEE;
    border: solid 1px #222;
  }

  .bookshelf-item .removebookshelf {
    display: block;
    position: absolute;
    width: 10px;
    height: 10px;
    left: 128px;
    top: 10px;
    background: #000;
  }
  
  .tooltip .fade .bottom .in {
    display: none;
  }
  
  .bookshelf-item-container .text-left strong, .bookshelf-item-container .text-left, .book-item {
    font-size: 10px;
    font-weight: 800;
    padding: 0px;
    margin: 0px;
  }
  
  .bookshelf-item-container .subtext {
    display: none;
  }
  
   .bookshelf-item, .book-item {
    height: auto;
    top: -10px;
    margin: 0px;
    line-height: normal;
  }
  
  .bookshelf-item a, .book-item a {
    display: block;
    height: 60px;
    width: 140px;
    padding: 2px;
    padding-left: 5px;
  }
  
  .bookshelf-item a:hover, .book-item a:hover {
    background: #222;
  }
    
  .new-chapters-badge {
    display: block;
    min-width: 0px;
    width: 25px;
    height: 16px;
    top: 43px;
    left: 115px;
    padding: 1px;
    font-size: 10px;
    color: #222;
    background-color: #DDD;
    border: 1px solid #111;
    border-radius: 0px;
  }
  
  #content {
    width: 700px;
  }
  
  #sidebar {
    left: -40px;
    width: 170px;
  }
  
  .add-nav .active {
    display: block;
    width: 100%;
    margin: 0px;
    padding: 0px;
    overflow: hidden;
  }
  
  .add-nav .active a {
    padding: 10px;
  }
  
  .header .dark-bg {
    height: 50px;
  }

  .cke_editable {
    color: #222;                      
  }
}
