@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kickass.so"), domain("kickass.to"), domain("kickasstor.eu"), domain("kickasstor.bitproxy.eu"), domain("kickassto.come.in"), domain("kasssto.come.in") , domain("kickassunblock.info"), domain("kat.cr"){
body,html{border:medium none;height:100%;margin:0;overflow:visible;padding:0;width:auto;text-align:center!important;background-image: url("https://scontent-sjc2-1.xx.fbcdn.net/hphotos-xap1/t31.0-8/10355421_10204305389583425_7513189854322664998_o.jpg");background-attachment: fixed;}
body{max-width:1280px!important;margin:auto!important;position:relative!important;text-align:left!important;}

#wrapper{margin:auto!important;max-width:1280px!important;}
#wrapperInner{padding-bottom:0!important;}
header{position: fixed!important;left:0!important;}
a{color:#999!important;}
a:hover{color:#eee!important;}
a:visited{color:#777!important;}
a:active{color:#fff!important;}

.seedLeachIcon,.seedLeachIcon{background:none!important;border:0!important;background-image:url("http://oi62.tinypic.com/r2j8nd.jpg")!important;}
#navigation li a:link{background:inherit!important;}
#navigation .dropdown a:hover, .ui-menu .ui-menu-item a.ui-state-hover, .ui-menu .ui-menu-item a.ui-state-active{border-color:rgba(0,0,0,0.3)!important;background-color:rgba(40,40,40,0.2)!important;color:#fff!important;}
#navigation li .dropdown {background:#3C3C3C!important;}
#navigation li a:hover,#navigation li .dropdown li a:hover{border-color:rgba(0,0,0,0.3)!important;background-color:rgba(40,40,40,0.2)!important;color:#fff!important;}
#menu{max-width:1280px!important;margin:0 auto!important;background:none!important;position:relative!important;}
header{background:#3C3C3C!important;}
.firstr{background:#444444!important;}    
.width100perc{padding-left:5px!important;}

.cellMainLink{font-weight:normal!important;}

a.turnoverButton, a.turnoverButton.blank:hover{
background: rgb(44,44,44)!important; /* Old browsers */
background: -moz-linear-gradient(top, rgba(44,44,44,1) 0%, rgba(28,28,28,1) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(44,44,44,1)), color-stop(100%,rgba(28,28,28,1)))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(44,44,44,1) 0%,rgba(28,28,28,1) 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, rgba(44,44,44,1) 0%,rgba(28,28,28,1) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(44,44,44,1) 0%,rgba(28,28,28,1) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, rgba(44,44,44,1) 0%,rgba(28,28,28,1) 100%)!important; /* W3C */
box-shadow:0 0 0 1px rgba(0, 0, 0, 0.08) inset, 0 -1px 0 0 rgba(0, 0, 0, 0.03) inset, 0 1px 0 0 rgba(255, 255, 255, 0.2) inset!important;
color:#fff;padding:4px 10px;text-shadow:none!important;}    


.siteButton, .siteButton:hover, .nohover:hover .siteButton, .icon16,.icon16:hover,.magnetlinkButton,.giantIcon.magnetlinkButton,.giantIcon.rssButton,.giantIcon.bookmarkButton,.giantIcon.magnetlinkButton:hover,.giantIcon.rssButton:hover,.giantIcon.bookmarkButton:hover,.siteButton.verifTorrentButton:hover,#thnxCount:hover{
background: rgb(68,68,68); /* Old browsers */
background: -moz-linear-gradient(top, rgba(68,68,68,1) 0%, rgba(44,44,44,1) 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(68,68,68,1)), color-stop(100%,rgba(44,44,44,1))); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(68,68,68,1) 0%,rgba(44,44,44,1) 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, rgba(68,68,68,1) 0%,rgba(44,44,44,1) 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(68,68,68,1) 0%,rgba(44,44,44,1) 100%); /* IE10+ */
background: linear-gradient(to bottom, rgba(68,68,68,1) 0%,rgba(44,44,44,1) 100%); /* W3C */
box-shadow:none!important;}
.torrentfingers,.thnx{background:none!important;box-shadow:none!important;}
#contentSearch{background: #1c1c1c; /* Old browsers */
background: -moz-linear-gradient(top, #1c1c1c 0%, #2c2c2c 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#1c1c1c), color-stop(100%,#2c2c2c)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #1c1c1c 0%,#2c2c2c 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #1c1c1c 0%,#2c2c2c 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top, #1c1c1c 0%,#2c2c2c 100%); /* IE10+ */
background: linear-gradient(to bottom, #1c1c1c 0%,#2c2c2c 100%); /* W3C */
color:rgb(220, 220, 220)!important;}
  
.giantIcon.magnetlinkButton:hover,.giantIcon.rssButton:hover,.giantIcon.bookmarkButton:hover,.siteButton.verifTorrentButton:hover{background:rgba(60, 60, 60,0.8)!important;}

#thnxCount span:hover,#fakeCount span:hover{color:#fff!important;}
  
#contentSearch input{color:#fff!important;}
.partner1Button{display:none!important;}
  
.dataList{color:#fff!important;}
.data{border:none!important;}
.data tr td, .data tr th {border:none!important;}
.torrent_files.folder,.torrent_files .folderopen{background-color:transparent!important;}
.textcontent img{width:auto!important;height:auto!important;max-width:700px!important;}
.textcontent img:hover{width:auto!important;height:auto!important;max-width:1100px!important;}
.quote,.textcontent{background-color: transparent!important;color:#fff!important;}

.ka-arrow-down {background:url("http://oi62.tinypic.com/r2j8nd.jpg")no-repeat scroll -190px -20px transparent!important;}
.ka-magnet {background: url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -190px -107px transparent!important;}
.ka-verify{background: url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -190px -107px transparent!important;}
.thnx{background: url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -190px -20px transparent!important;}
.fake{background: url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -190px -107px transparent!important;}
    
.giantIcon.magnetlinkButton span{background:url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -150px -150px transparent!important;display:block;width:30px;height:27px;}
.giantIcon.bookmarkButton span{background:url("http://kastatic.com/images/spriteContent-75d1f2a.png") no-repeat scroll -150px -175px transparent!important;display:block;width:30px;height:27px;}
.siteButton.giantIcon.rssButton.ajaxLink span{background:url("http://kastatic.com/images/spriteContent-75d1f2a.png") no-repeat scroll -150px -200px transparent!important;display:block;width:30px;height:27px;}
    
.buttonPic{background: url("http://oi62.tinypic.com/r2j8nd.jpg") no-repeat scroll -142px 4px transparent!important;border-right: 1px solid rgb(166, 148, 90);margin-right: 12px;}

.darkButton{background: rgb(68,68,68)!important; /* Old browsers */
background: -moz-linear-gradient(top,  rgba(68,68,68,1) 0%, rgba(28,28,28,1) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(68,68,68,1)), color-stop(100%,rgba(28,28,28,1)))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(68,68,68,1) 0%,rgba(28,28,28,1) 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(68,68,68,1) 0%,rgba(28,28,28,1) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(68,68,68,1) 0%,rgba(28,28,28,1) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom,  rgba(68,68,68,1) 0%,rgba(28,28,28,1) 100%)!important; /* W3C */
border-color: rgba(56, 56, 56, 0)!important;}

.bbedit-toolbar{width:260px!important;background-color:#fff!important;}
.advertising .legend{background-color:transparent!important;}
.eventsButtons{display:none!important;}

textarea,.textinput{color: rgb(190, 190, 190)!important;font-size:13px;outline:medium none;width:180px;padding:2px 5px 3px;border-radius:3px 3px 3px 3px;background-color:rgba(20,20,20,0.7)!important;}

.commentThread .commentbody{background-image:none!important;background:#2C2C2C!important;color:#fff!important;}

.sharingWidgetBox, #tab-main div.floatleft{display:none!important;}

h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h1 a:hover span, h2 a:hover span, h3 a:hover span, h4 a:hover span {color:#fff!important;}

.sidebarCell{display:none!important;}
.tabNavigation .menuValue, #navstyle .menuValue, .smallTorrentBadge{color:#111!important;border-left: 1px solid rgb(100, 100, 100)!important;border-bottom: 1px solid rgb(88, 88, 88)!important;
background: rgb(170,170,170)!important; /* Old browsers */
background: -moz-linear-gradient(top, rgba(170,170,170,1) 0%, rgba(150,150,150,1) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(170,170,170,1)), color-stop(100%,rgba(150,150,150,1)))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, rgba(170,170,170,1) 0%,rgba(150,150,150,1) 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, rgba(170,170,170,1) 0%,rgba(150,150,150,1) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top, rgba(170,170,170,1) 0%,rgba(150,150,150,1) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom, rgba(170,170,170,1) 0%,rgba(150,150,150,1) 100%)!important; /* W3C */
margin:-1px 0px;}

select{background-color:rgba(0,0,0,0.6)!important;}

.contentTabContainer *{color:#fff!important;}

.torrentDownloaded {opacity: 0.2;}
.data th,.sliderbox h3{color:#eee!important;}
   
.sliderbox ul li:hover{background-color:rgba(30,30,30,0.6)!important;}

.siteButton span{background-image:none!important;color:#999!important;text-shadow:none!important;}
.redButton,.nohover:hover.redButton,.siteButton.dislikeButton,.minusbut,.darkButton,.menuValue,.circleButton,.siteButton{text-shadow:none!important;}
.siteButton.giantButton.verifTorrentButton span{text-shadow: 0 1px 0 #8c751e!important;}
.headmenu{background:none!important;}
.toppad10px{padding:0px!important;}

.headmainpart{height:56px!important;}
#navstyle{background:none!important;background-color:#0F0F0F!important;}
.darkbrownbg {background-color:rgb(15,15,15)!important;}
.mainpart,footer{background-color:#1C1C1C!important;}
    
#tagcloud,.tagcloud, .line50perc.showmore.botmarg0{display:none!important;}
.showmore{background:#3C3C3C!important;padding:5px;display:block;} 

h1 a, h2 a, h3 a, h4 a, h5 a{color:#ddd!important;}
h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover{color:#fff!important;}

tr.odd{background-color:#2C2C2C!important;}
tr.even{background-color:#212121!important;}
tr.odd,tr.even{color:#B4B4B4!important;}
tr.odd a,tr.even a {color:#DCDCDC!important;}
tr.even:hover{background:rgba(20,20,20,0.4)!important;}
tr.odd:hover{background:rgba(30,30,30,0.4)!important;}

table tbody *,#navstyle{border-color:rgba(255,255,255,0.1)!important;}

.tabs{margin-top:0px;}
    
#tab-main{background:#2C2C2C!important;padding:20px!important;}
hr.tabsSeparator{background-color:rgba(56, 56, 56, 0)!important;margin:0!important;height: 0px!important;}
.letterssort{background-color:transparent!important;border:0px!important;}
h1, h2, h3, h4, h5{color:#eee!important;}

.formtable{color:#fff!important;}
#captcha{color:#000!important;}
.hideSidebar{border:none!important;}
.firstPost strong{background-color:rgba(50,50,50,0.7)!important;}
.lightivorybg, .versionsUnFolded, .lightivorybg:hover{background-color:rgba(50,50,50,0.7)!important;}

#communityBlock.commentbody,.profileCloud,.quote,blockquote,.accentbox{background-color:rgba(0,0,0,0.2)!important;color:#fff!important;}
#ui-id-1{text-align:left!important;position:absolute!important;}
.tooltip, .ui-tooltip, .ui-widget, .ui-corner-all, .ui-widget-content{background:#fff!important;border-bottom: 0px solid #000 !important;}
.ui-menu .ui-menu-item a,.ui-widget-content a{color:#383838!important;text-shadow:none!important;}
.ui-menu .ui-menu-item a:hover{text-shadow:none!important;}
.ui-menu .ui-menu-item a:hover,.ui-menu .ui-menu-item a.ui-state-hover,.ui-menu .ui-menu-item a.ui-state-active{border-color:rgba(0,0,0,0.3)!important;background-color:rgba(60, 60, 60,0.1)!important;color:#191919!important;font-weight:normal!important;}

#fancybox-overlay{position:fixed!important;width:100%!important}
div[id="fancybox-wrap"][style^="width: 440px;"]{left:440px!important;}
div[id="fancybox-wrap"][style^="width: 840px;"]{left:240px!important;}
div[id="fancybox-wrap"][style^="width: 796px;"]{left:266px!important;} 

#previewPopupContainer,.previewPopupContainer{display:none!important;}
#fancybox-outer{background-image:none!important;background-color:#1C1C1C!important;}

.achTable a{color:black!important;} 
.achTable{color:#eee!important;}
.advertDownload,.block.botmarg5px,.vuze,span.font11px.lightgrey.block,.at4-share-outer-right{display:none!important;}
.font11px.lightgrey.block{color:#585858;}

/*.lazyjs.bbcodeImage{display:none!important;} */

.torrentFileList tr.last .torTree,.firstPost div {background-color:transparent!important;}
.torrent_files .folder, .torrent_files .folderopen{background:inherit!important;}
#ul_top > tbody:nth-child(1) > tr:nth-child(1n+0){color:#eee!important;}
#ul_top > tbody:nth-child(1) > tr:nth-child(1n+0):hover{background-color:transparent!important;}
footer{display:none!important;}
}
