/* How to customize:                        
 * "#name" is "id=name" clauses on any mark up "tag" (eg header, div, li)
 * "tag.name" is "class=name" clause on mark up "tag"
 * "tag" is aapplies to "tag" mark up everywhere
 * "name > tag" are "id=name" on a tag
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http[s]://itpro\\.tv/chat-popout-[1-9]/") {

html, body {
    color:cyan !important;
    background:black !important;
    }

span.chatboxtime     {
    font-size: 16px !important;
    color:yellow !important;
    } 
    
a {
    color:green !important;
    background:black !important;
    }
    
/* not working */
a.user_24 {
    /*color:cyan !important;*/
    color:orange !important;
    }   

/* a.zuser_\*, */
user_12064,
user_1,
user_14504,
user_1622,
user_6061
   {
    color:lightblue !important;
    }

drupalchat-embed-chatroom-content-user,
div.drupalchat-embed-chatroom-container, 
div.drupalchat-embed-chatroom-content,
p
 {
    font-size: 16px !important; /* take out !important need in code inspector to allow this override */
    color:white !important;
    background:black !important;
    }
    
element {
    color:black !important;
    background:black !important;
    }
    
tr.role-0 role-subscriber {
    color:red !important;
    background:black !important;
    }
 
}
